css:
@-moz-document domain("todoist.com") {
:root {
 	--bg: #222222;
    --border: #3d3d3d;
    --font: #a1a1a1;
    --pageBg: #1d1d1d;
}

/* i really want this to be global */
*:not(.counter_color),
.theme_noir a.action, a.action.action_add_item, .theme_noir .note_extra_info a, .theme_noir #item_selecter a, .theme_noir .blank_screen a, .theme_noir .premium_only a.sel_premium {
	color: var(--font) !important;
}

.drag_and_drop_handler img,
.icon.gear_menu{
	filter: brightness(500%);
}

.on-drag {
 	background-color: red !important;   
}

#page_background,
#left_menu,
.theme_noir #top_bar,
#top_filters li:hover,
.reactist.panel_summary--expanded:hover,
.reactist.panel_summary:hover,
.ist_menu,
.FilterListItem:hover table,
.FilterListItemSortable.is_selected table,
.FilterListItem.on_menu_active,
.minical_container,
.minical_calendar,
.ist_menu .no_hover td,
#GB_window.notifications,
.GB_window .g_header,
.GB_tooltip_win.shadow,
.completed_quick_viz,
.GB_window.shadow,
#content,
.generic_left_list .current table,
.reactist.panel_summary.panel_summary--expanded,
.quick_add_task .input_due_date,
.ist_menu .no_hover td,
#ist_complete_floater,
.reactist.panel_summary .panel_summary__icon,
#todoist_app .panel_summary 
{
 	background-color: var(--bg) !important;
    border-color: var(--border) !important;
}

.generic_left_list.items li:hover table,
.ist_menu .on,
.text_box_holder,
#editor .input_due_date,
.minical--cell_today,
.theme_noir .quick_find {
 	background-color: var(--border) !important;  
    border-color: var(--border) !important;
}

.reactist.expansion_panel,
.generic_left_list .empty,
.form_content table {
 	border-color: var(--bg);   
}

.minical_scheduler,
.minical_calendar .help_recurring,
#quick_day_show div {
 	border-color: var(--border);   
}

.cmp_share_project_plus,
.cmp_task_actions,
.form_icon_holder {
    filter: brightness(500%);
}

.form_icon_holder img {
 	filter: invert(1);   
}
}
