css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("readms.net") {
  
  .modal-content,.pager li > a, .pager li > span,.dropdown-menu
  {background-color: #000 !important;}
  
  body
  {background-color: #111 !important;}
  
  .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover,.btn-default,.pager li > a:focus, .pager li > a:hover
  {background-color: #222 !important;}
  
  .btn-default:focus, .btn-default:hover
  {background-color: #333 !important;}
  
  #primary-nav .navbar-default,form.comment-post .comment-actions, form.comment-update .comment-actions,form.comment-post .comment-actions button, form.comment-update .comment-actions button,
  #reader-nav .navbar-reader,.btn-reader-page .btn,.btn-reader-page.open .btn,.btn-reader-chapter .btn,.btn-reader-chapter.open .btn,#reader-nav .navbar-reader .navbar-collapse > ul.nav > li > ul > li > a,
  #notification-box
  {background: #000 !important;}
  
  form.comment-post .comment-blocked, form.comment-update .comment-blocked,.alert
  {background: #111 !important;}
  
  #primary-nav .navbar-default .navbar-nav > .active a,form.comment-post .comment-actions button:hover, form.comment-update .comment-actions button:hover
  {background: #222 !important;}
  
  .header,ul.new-list li a:hover,ul.new-list li.active a:hover,ul.new-list li.active a,.comments .comment .comment-status, .comments .notify-comment .comment-status,.subnav,
  .supporter-user,.settings-section,#bitcoin-tx-table tr td.empty
  {background: transparent !important;}
  
  .dropdown-menu .divider,.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .navbar-default .navbar-nav > .open > a:hover,.form-control,
  .table-striped > tbody > tr:nth-of-type(2n+1)
  {background-color: transparent !important;}
  
  .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover,.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .open > a,.btn-default
  {background-image: none !important;}
  
  .icon
  {background-image: url("http://1.bp.blogspot.com/-W4wJeoIusFE/ViUMilXvqgI/AAAAAAAAAoM/Ue6DN4qqmxc/s320/little_luffy_render_by_carolester-d5ssjc4.png") !important;}
  
  body,.blog-post h2 a,.blog-post p,ul.new-list li a,.dropdown-menu > li > a,form.comment-post .comment-actions button, form.comment-update .comment-actions button,.form-control,.btn-default,
  .btn-reader-page .btn,.btn-reader-chapter .btn,a,#reader-nav .navbar-reader .navbar-collapse > ul.nav > li > a,.navbar-default .navbar-nav > li > a,#comments #comment-sort,.header .container .extra-nav ul li a,
  #notification-box p.info,#notification-box #comment-wrapper .no-notifications,.close
  {color: #bcbcbc;}
  
  #reader-nav .navbar-reader .navbar-collapse > ul.nav > li > ul > li > a
  {color: #bcbcbc !important;}
  
  .navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > .active > a:hover,a:hover,
  .navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .navbar-default .navbar-nav > .open > a:hover,
  form.comment-post .comment-actions button:hover, form.comment-update .comment-actions button:hover,.btn-default:hover,a:focus,
  .comments .comment .comment-status, .comments .notify-comment .comment-status,.btn-reader-page.open .btn,.btn-reader-chapter.open .btn,#reader-nav .navbar-reader .navbar-collapse > ul.nav > li > ul > li > a:hover,
  .comments .comment .comment-head .author .info .ago, .comments .notify-comment .comment-head .author .info .ago,
  .comments .comment.standard .comment-head .author .info strong, .comments .notify-comment.standard .comment-head .author .info strong,.close:focus, .close:hover
  {color: #98ad85 !important;}
  
  .alert .icon
  {color: #111;}
  
  .form-control
  {border: 1px solid #313030;}
  
  form.comment-post .comment-blocked, form.comment-update .comment-blocked
  {border: solid 4px #000 !important;}
  
  .pager li > a, .pager li > span,.pager li > a, .pager li > span,.comments .comment .icon, .comments .notify-comment .icon,#manga-page,.settings-section
  {border: transparent !important;}
  
  #primary-nav .navbar-default,.btn-default,.navbar-default .navbar-collapse, .navbar-default .navbar-form,.navbar-default,.alert
  {border-color: transparent !important;}
  
  hr,#login-modal .external,.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th
  {border-top: transparent !important;}
  
  .blog-post,ul.new-list li a,ul.new-list li.active a,.modal-header,.subnav
  {border-bottom: transparent !important;}
  
  .blog-post h2,.content-section,.comments .comment .comment-foot .comment-rate-down, .comments .notify-comment .comment-foot .comment-rate-down
  {border-left: transparent !important;}
  
  .alert
  {box-shadow: none !important;}
  
  a:hover,a:focus
  {text-decoration: none !important;}
  
  .btn-default,.alert
  {text-shadow: none !important;}
  
  .msa.msa-728_90
  {display: none !important;}

}
