ul#nav {
    width: 95%;
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
    background-image: none;
    background-color: #424858;
    box-shadow: 0 1px 1px rgba(0,0,0,.1);
    height: 58px;
}

div#nav_div {
    background-color: #424858;
}

ul#nav > li {
    float: left;
    height: inherit;
    line-height: inherit;
    background-image:none;
    
}
ul#nav > ul {
    list-style:none;
}
ul#nav > li > a {
    display: block;
  color: rgba(255, 255, 255, 0.7);
  font-size: 14px;
  transition-duration: 0.5s;
  transition-timing-function: ease;
  transition-delay: initial;
  transition-property: color;
  line-height: 18px;
  padding-left: 25px;
  padding-right: 25px;
    margin-left: inherit;
    margin-right: inherit;
    
    font-weight: normal;
    letter-spacing: inherit;
    
      padding-top: 20px;
    margin-bottom: -2px;
}
ul#nav > li > a:before {
  font-size: 16px;
    font-family: Font Awesome\ 5 Free;
    font-weight: 900;
    padding-right: 5px;
}

ul#nav > li > ul[class^="menucols_"] {
    list-style:none;
    min-width: 200px;
    text-align: left;
    width: auto;
    white-space: nowrap;
        transition: color .2s ease;
    -webkit-transition: color .2s ease;
    background-color: #fff;
    box-shadow: 0 1px 12px rgba(0,0,0,.1);
    border: none !important;
	opacity: 0;
     visibility: hidden;
    display: inline-block;
    position: absolute;
        overflow: hidden;
height:0;
   }

ul#nav > li:hover > ul[class^="menucols_"] {
	visibility: visible;
    opacity: 1;
height:auto;
    
}

ul#nav > li > ul[class^="menucols_"] li ul {
    list-style:none;
    padding-left: 0;
    margin: 0;
}

ul#nav > li > ul[class^="menucols_"] li a {
    display: block;
    padding: 8px 25px;
    clear: both;
    white-space: nowrap;
    font-size: 15px;
    font-weight: normal;
    color: #0a1832;
    -webkit-transition: all .35s ease;
    transition: all .35s ease;

}

ul#nav > li > ul[class^="menucols_"] li a:hover {
color: #31a529;
}

ul#nav > li > ul[class^="menucols_"] li span {
    display: block;
    padding: 8px 5px;
    clear: both;
    white-space: nowrap;
    font-size: 15px;
    font-weight: bold;
    color: #0a1832;
    -webkit-transition: all .35s ease;
    transition: all .35s ease;
}

ul#nav > li:hover > ul > li > ul > li {
    
    margin-left:0px
}

ul#nav > li:hover > ul > li[class^="nav-"] {
    width: 200px;
    
     display: inline-block;
    vertical-align: top;
}

ul#nav > li:hover > ul > li[class^="nav-"] a {
    font-weight: normal
}


ul#nav > li:nth-child(1) > a:before {
    content: "\f007";
}
ul#nav > li:nth-child(2) > a:before {
    content: "\f0e8";
}
ul#nav > li:nth-child(3) > a:before {
    content: "\f4c4";
}
ul#nav > li:nth-child(4) > a:before {
    content: "\f522";
}
ul#nav > li:nth-child(5) > a:before {
    content: "\f015";
}
ul#nav > li:nth-child(6) > a:before {
    content: "\f0c1";
}

ul#nav > li > a.active:parent {
	color: #fff;
    background-color: rgba(255,255,255,.1)!important;
}

ul#nav>li>a:focus, ul#nav>li>a:hover {
    background-color: transparent;
}
ul#nav>li>a:active, ul#nav>li>a:focus, ul#nav>li>a:hover {
    color: #fff;
}
ul#nav > li:hover {
    background-image: none;
}



