@-moz-document domain("sol.scouting.nl") {
[role="button"], a, area, button, input:not([type="range"]), label, select, summary, textarea {
  touch-action: manipulation !important;
}
button {
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
  border-bottom-left-radius: 0px !important;
}
button:focus {
  outline-color: -webkit-focus-ring-color !important;
  outline-style: auto !important;
  outline-width: 5px !important;
}
button, input, optgroup, select, textarea {
  margin-top: 0px !important;
  margin-right: 0px !important;
  margin-bottom: 0px !important;
  margin-left: 0px !important;
  font-family: inherit !important;
  font-size: inherit !important;
  line-height: inherit !important;
}
button, input {
  overflow-x: visible !important;
  overflow-y: visible !important;
}
button, select {
  text-transform: none !important;
}
[type="reset"], [type="submit"], button, html [type="button"] {
  -webkit-appearance: button !important;
}
.primaryButton, .secondaryButton{
  display: inline-block !important;
  font-weight: 400 !important;
  text-align: center !important;
  white-space: nowrap !important;
  vertical-align: middle !important;
  user-select: none !important;
  border-top-width: 1px !important;
  border-right-width: 1px !important;
  border-bottom-width: 1px !important;
  border-left-width: 1px !important;
  border-top-style: solid !important;
  border-right-style: solid !important;
  border-bottom-style: solid !important;
  border-left-style: solid !important;
  border-top-color: transparent !important;
  border-right-color: transparent !important;
  border-bottom-color: transparent !important;
  border-left-color: transparent !important;
  border-image-source: initial !important;
  border-image-slice: initial !important;
  border-image-width: initial !important;
  border-image-outset: initial !important;
  border-image-repeat: initial !important;
  padding-top: 0.375rem !important;
  padding-right: 0.75rem !important;
  padding-bottom: 0.375rem !important;
  padding-left: 0.75rem !important;
  font-size: 15px !important;
  line-height: 1.5 !important;
  border-top-left-radius: 3px !important;
  border-top-right-radius: 3px !important;
  border-bottom-right-radius: 3px !important;
  border-bottom-left-radius: 3px !important;
  transition-duration: 0.15s, 0.15s, 0.15s, 0.15s !important;
  transition-timing-function: ease-in-out, ease-in-out, ease-in-out, ease-in-out !important;
  transition-delay: initial, initial, initial, initial !important;
  transition-property: color, background-color, border-color, box-shadow !important;
}
.btn.focus, .primaryButton:focus, .secondaryButton:focus {
  outline-color: initial !important;
  outline-style: initial !important;
  outline-width: 0px !important;
  box-shadow: rgba(0, 123, 255, 0.25) 0px 0px 0px 0.2rem !important;
}
.btn.disabled, .primaryButton:disabled, .secondaryButton:disabled {
  opacity: 0.65 !important;
}
.btn:not([disabled]):not(.disabled) {
  cursor: pointer !important;
}
.primaryButton {
  color: rgb(255, 255, 255) !important;
  background-color: rgb(15, 156, 243) !important;
  border-top-color: rgb(15, 156, 243) !important;
  border-right-color: rgb(15, 156, 243) !important;
  border-bottom-color: rgb(15, 156, 243) !important;
  border-left-color: rgb(15, 156, 243) !important;
  border-top-width: 1px !important;
  border-right-width: 1px !important;
  border-bottom-width: 1px !important;
  border-left-width: 1px !important;
  border-top-style: solid !important;
  border-right-style: solid !important;
  border-bottom-style: solid !important;
  border-left-style: solid !important;
  border-image-source: initial !important;
  border-image-slice: initial !important;
  border-image-width: initial !important;
  border-image-outset: initial !important;
  border-image-repeat: initial !important;
}
.primaryButton:hover {
  color: rgb(255, 255, 255) !important;
  background-color: rgb(0, 105, 217) !important;
  border-top-color: rgb(0, 98, 204) !important;
  border-right-color: rgb(0, 98, 204) !important;
  border-bottom-color: rgb(0, 98, 204) !important;
  border-left-color: rgb(0, 98, 204) !important;
}
.primaryButton.focus, .primaryButton:focus {
  box-shadow: rgba(0, 123, 255, 0.5) 0px 0px 0px 0.2rem !important;
}
.primaryButton.disabled, .primaryButton:disabled {
  background-color: rgb(0, 123, 255) !important;
  border-top-color: rgb(0, 123, 255) !important;
  border-right-color: rgb(0, 123, 255) !important;
  border-bottom-color: rgb(0, 123, 255) !important;
  border-left-color: rgb(0, 123, 255) !important;
}
* {
  outline-color: initial !important;
  outline-style: initial !important;
  outline-width: 0px !important;
}
.waves-effect {
  position: relative !important;
  cursor: pointer !important;
  display: inline-block !important;
  overflow-x: hidden !important;
  overflow-y: hidden !important;
  user-select: none !important;
  -webkit-tap-highlight-color: transparent !important;
  vertical-align: middle !important;
  z-index: 1 !important;
  will-change: opacity, transform !important;
  transition-duration: 0.3s !important;
  transition-timing-function: ease-out !important;
  transition-delay: initial !important;
  transition-property: all !important;
}
.button-items .primaryButton, .secondaryButton{
  margin-bottom: 8px !important;
  margin-right: 5px !important;
}
.btn-danger, .btn-dark, .secondaryButton, .primaryButton, .btn-success, .btn-warning {
  color: rgb(255, 255, 255) !important;
}
