css:
@-moz-document url-prefix("https://www.gog.com") {

/* ==UserStyle==
@name           GOG.com - Dark Theme
@namespace      https://github.com/pabli24/GOG.com-Dark-Theme
@version        0.1.9
@description    Dark Theme for GOG.com
@author         Pabli
@license        CC-BY-SA-4.0 (https://creativecommons.org/licenses/by-sa/4.0/)
@homepageURL    https://github.com/pabli24/GOG.com-Dark-Theme
@supportURL     https://github.com/pabli24/GOG.com-Dark-Theme/issues
==/UserStyle== */

:root {
	--bg: #1d1d1d;       /* #ccc */
	--bg2: #222;         /* #e1e1e1 */
	--bg3: #1e1e1e;      /* #d2d2d2 */
	--bg-hover: #2d2d2d; /* #e5e5e5 */
	--text1: #ccc;       /* #212121 */
	--text2: #eee;       /* #262626 #1a1a1a*/
	--text3: #bbb;       /* #595959 gray*/
	--text4: #aaa;       /* #4c4c4c #404040*/
	--textw: #fff;       /* #000000 */
	--textb: #000;       /* #ffffff */
	--border: rgba(0,0,0,.08);
	--border2: #242424;
	--border3: #333;
}

/* text headers titles */
.big-spots__header, .module-header, .module-header__btn.is-active,
.gogmix__title, .product-row__title, .header__title>strong,
.news-item {
	color: var(--text2);
}
/* top page background */
.big-spots__blurs:before {
	background: linear-gradient(to right,var(--bg),rgba(0, 0, 0, 0));
}
.big-spots__blurs:after {
	background: linear-gradient(to left,var(--bg),rgba(0, 0, 0, 0));
}
.big-spots__blurs > div:after {
	background: linear-gradient(to top,var(--bg) 15%,rgba(0, 0, 0, .2));
	content: '';
	position: absolute;
	width: 100%;
	height: 100%;
}

.frontpage-wrapper:before {
	background: linear-gradient(to top,var(--bg) 50%,rgba(0, 0, 0, .3));
	content: '';
	position: absolute;
	width: 100vw;
	height: 100vw;
}
@media (min-width: 801px){
.frontpage-wrapper:before {
	background: linear-gradient(to top,var(--bg) 50%,rgba(0, 0, 0, 0.3));
}
}
@media (min-width: 1367px) {
.frontpage-wrapper:before {
	background: linear-gradient(to top,var(--bg) 60%,rgba(0, 0, 0, 0.3));
}
}
@media (min-width: 1681px) {
.frontpage-wrapper:before {
	background: linear-gradient(to top,var(--bg) 70%,rgba(0, 0, 0, 0.3));
}
}
/*  */
.about-gog {
	background: #343e48;
}
.about-gog__header {
	background-color: #343e48;
}
.about-gog__header:hover {
	background-color: rgba(255, 255, 255, 0.05);
}
.about-gog__header~.about-gog__header {
	border-color: #29323a;
}
.about-gog__icon {
	text-shadow: 0 0 30px hsla(0, 0%, 0%, 0.75);
}
.footer__dropdown-item {
	color: var(--text2);
}
.carousel-pagination.big-arrows .arrow-icon {
	fill: #fff;
}
/* price */
.price-btn, .price-text--discount {
	border: 1px solid var(--border3);
}
.price-btn, .price-text, .price-text--discount {
	color: var(--text1);
}
.price-text--discount {
	background: #38505c;
}

.big-spot.is-hovered .price-btn--active,
.btn--green, .is-buyable.gogmix:hover .price-btn--active, .product-row.is-buyable:hover .price-btn--active {
	background: linear-gradient(180deg,#5d7100,#354600);
	color: var(--textw);
}
.big-spot.is-hovered .price-btn--active,
.is-buyable.gogmix:hover .price-btn--active, .product-row.is-buyable:hover .price-btn--active {
	border-color: var(--border3);
}
/*  */
.product-row--grid,
.list--grid .product-row,
.list--rows--no-border .product-row {
	background: var(--bg2);
}
.list--grid .is-owned.gogmix, .list--grid .product-row.is-owned,
.is-owned.gogmix, .product-row.is-owned {
	background: var(--bg3);
}
.product-tile__label--in-library {
	background: hsla(0, 0%, 10%, 0.7);
	color: var(--text3);
}
.product-tile__label--is-wishlisted {
	background: #845810;
}
.product-tile__label--is-upcoming {
	background: #0f3854;
}
.product-tile__label--in-cart {
	background: #364800;
}
.product-tile__content,
.product-tile--active .product-tile__content,
.catalog__body--slide-desktop .product-tile__content,
.catalog__body--slide-desktop .product-tile--active .product-tile__content,
.catalog__body--slide-desktop .product-tile__title {
	background: var(--bg2);
}
.product-tile--list.product-tile--active .product-tile__title {
	color: var(--text4);
}
.product-tile--list.product-tile--active .product-tile__content {
	background: var(--bg2);
}
.product-tile--list.product-tile--active {
	border-bottom: 1px solid var(--border3);
}
.section-title--with-border {
	border-bottom: 1px solid var(--border3);
}
/* discover games  */
.tabbed-section__head {
	border-bottom: 1px solid var(--border3);
}
.product-tile--list:not(.product-tile--active) {
	border-bottom: 1px solid var(--border3);
}
.tabbed-section__tab {
	color: var(--text3);
	fill: var(--text3);
}
.product-tile--list:not(.product-tile--active) .product-tile__title,
.product-tile__price-discounted,
.product-tile__no-buy-block-slug,
.catalog__body--slide-desktop .product-tile__price-discounted,
.catalog__body--slide-desktop .product-tile__no-buy-block-slug {
	color: var(--text4);
}
.icon-wrapper-icon {
	filter: invert(1);
}
.discover-games-more {
	border: 1px solid var(--border3);
}
.discover-games-more__text {
	color: var(--text3);
}
.discover-games-more__icon {
	fill: var(--text3);
}
/* left column */
.news-item__title {
	color: var(--text1);
}
.news-item__info {
	color: var(--text3);
}
.news-item:hover {
	background:  var(--bg-hover);
}
/* left right background */
.module {
	background: var(--bg2);
	color: var(--text1);
	box-shadow: 0 1px 5px rgba(0,0,0,.15);
}
/* right column */
.gogmix, .product-row {
	border-top: 1px solid var(--border);
	color: var(--text3);
}
.product-row--has-action.gogmix:hover, .product-row--has-card.gogmix:hover, .product-row.product-row--has-action:hover, .product-row.product-row--has-card:hover {
	background: var(--bg-hover);
}
.module__bottom {
	border-top: 1px solid #272727;
	color: var(--textw);
	background: rgba(255, 255, 255, 0.01);
}
.module__bottom:not(.no-ho):hover {
	background: var(--bg-hover);
}
/* games store */
.sort-by-title {
	color: var(--text4);
}
.catalog__body--slide-desktop .product-tile__cover {
	background: var(--bg3);
}
.catalog__filters-sorting {
	border-top: transparent;
	border-bottom: 1px solid var(--border3);
}
.filter__item:not(:first-child) {
	border-top: 1px solid var(--border3);
}
.catalog__paginator-wrapper {
	border-top: 1px solid var(--border3);
}
.paginator-container .arrow-wrapper {
	background-image: none;
	background-color: var(--bg3);
	border: 1px solid var(--border3);
}
.paginator-container .page-input {
	border-bottom: 3px solid var(--border3);
}
.paginator-container .arrow-left, .paginator-container .arrow-right {
	fill: var(--text3);
}
:not(.spinner-halloween) .catalog__search:after {
	background-color: hsla(0, 0%, 5%, 0.8);
}
/* /promo */
/* .promo-columns-wrapper {
	background: var(--bg);
	margin-top: 0;
} */
.header__title {
	margin-top: 15px;
	color: var(--textw);
}
.header__countdown {
	color: var(--textw);
}

.module__top {
	color: var(--text2);
}
.list__products .product-row-price--new {
	color: var(--text2);
}

.list-category-header {
	color: var(--text2);
	background: #262626;
}
.list-category-header, .list__products:not(:empty) {
	border-top: 1px solid var(--border);
}
.list-search {
	border: 1px solid var(--border3);
	color: var(--text3);
	background: #2c2c2c;
}
.details {
	color: var(--text2);
}
.details-rev__author {
	color: var(--text4);
}
.promo-counter {
	background: #262626;
}
.promo-counter-left {
	color: var(--text3);
}
._checkbox-container:hover .checkbox--green, .checkbox--green.is-selected, .hidden-input:checked+.checkbox--green,
.subscriptions-list__group-header .subscriptions-option--on .checkbox {
	background: #405200;
	border-color: #435500;
}
/* /order */
.order__title--success {
	color: #8ac500;
}
.order-article {
	color: var(--text2);
}
._dropdown__items {
	background: #262626;
}
._dropdown__item, ._dropdown__item-unobtrusive, ._fake_dropdown__item {
	background: #262626;
	border-color: var(--border);
}
._dropdown__pointer-up:after {
	background: #262626;
}
._dropdown__item:hover, ._fake_dropdown__item:hover {
	background: #2f2f2f;
}
.order__title {
	color: var(--textw);
}
._search__spinner, ._spinner:after, .btn--gray.is-spinning:after, .module-header2__element:before, .spinner--big, .spinner--big-light, .spinner--small, .spinner--small-light, .top-nav-search>.spinner.show-on-off-canvas {
	filter: invert(1);
}
/* /game */
.product-actions {
	background-color: rgba(39, 39, 39, 0.9);
}
.product-actions-price__final-amount, .product-actions-price__giveaway, .product-actions-price__base-amount {
	color: var(--text4);
}
/* .edition-picker__trigger {
	background-image: linear-gradient(-180deg,var(--bg),var(--bg2));
	border: 1px solid rgba(64,64,64,.5);
	box-shadow: 0 1px 3px 0 rgba(0,0,0,.13);
} */
.productcard-basics__title {
	color: var(--text2);
}
.productcard-wrapper:before {
	content: "";
	width: 100%;
	height: 672px;
	position: absolute;
	background: linear-gradient(to top,var(--bg) 15%,rgba(0, 0, 0, .2));
}
.productcard-slider__nav-icon {
	color: rgba(255, 255, 255, 0.2);
}
.header {
	color: var(--text2);
}
.rating, .productcard-rating, .productcard-os-support, .productcard-basics__languages {
	color: var(--text3)
}
.productcard-basics__separator {
	background: var(--text3)
}
.un--body-alt {
	background-image: linear-gradient(to right,var(--text2),var(--text2));
}
.header__os {
	border: 1px solid rgba(255, 255, 255, 0.4);
	border-width: 0 1px;
}
.title {
	border-bottom: 1px solid var(--border2);
}
.languages__row, .table__row, .table-header, .average-body, .goodies__row:last-child .goodies__cell {
	border-bottom: 1px solid var(--border3);
}
.goodies__label.is-hovering {
	border-top: 1px solid var(--border3);
}
.goodies__cell {
	border-bottom: 1px solid var(--border3);
}
.goodies__cell.is-selected {
	border-left: 1px solid #9f82a0;
	border-right: 1px solid #9f82a0;
	background: rgba(255, 255, 255, 0.02);
}
.goodies__cell.is-hovering {
	background: rgba(255, 255, 255, 0.02);
}
.goodies__label.is-selected {
	border-top: 1px solid #9f82a0;
	color: #78387b;
}
.languages {
	color: var(--text2);
}
.module__foot {
	color: var(--text4);
}
.social-btn, .wishlist-btn {
	color: var(--text4);
	background: var(--bg2);
	box-shadow: 0 1px 5px rgba(0,0,0,.15);
}
.social-btn.is-wishlisted, .wishlist-btn.is-wishlisted {
	background: var(--bg2);
}
.social-btn:hover, .wishlist-btn:hover {
	background: var(--bg-hover);
}
.module--buy .module__top, .module--buy-series .module__top,
.quote, .user-review,
.product-details,
.game-features__title,
.requirement, .sysreq,
.footnotes,
.features {
	color: var(--color2);
}
.game-features__icon, .goodies__icon--description, .features-item__feature-icon, .languages__icon--more-languages {
	filter: invert(1);
}
.languages__icon, .system-requirements-selector__icon {
	fill: #fff;
}
.bonus__icon {
	filter: invert(1);
}
img[src="https://items.gog.com/separator.jpg"] {
	filter: invert(1);
}
.game-features__item {
	border-top: 1px solid var(--border);
}
.game-features__item:hover {
	background: var(--bg-hover);
}
.list--grid .product-row.product-row--has-action:hover, .list--grid .product-row.product-row--has-card.is-owned:hover, .list--grid .product-row.product-row--has-card:hover {
	background: var(--bg-hover);
}
.user-review__author {
	color: var(--text4);
}
.reviews-pagination-container {
	color: var(--text4);
}
.btn--green:not(.is-spinning):not(.is-disabled):not([disabled]):hover {
	background: linear-gradient(180deg,#687c00,#5e7d00);
}
.filters__search>._search__input {
	color: var(--text3);
}
.filters__search {
	border: 1px solid var(--border3);
	background: #2c2c2c;
}
.filter {
	background: var(--bg2);
	border-color: #1f1f1f;
}
.filters__search-explained {
	background: var(--bg);
	color: var(--text2);
}
.filter.is-selected,
.filter__item.is-selected, .filter__item.is-selected--mixed {
	color: var(--text2);
}
.module--filters,
.filter__item {
	color: var(--text3);
}
.filter-checkbox, .filter-radio, .checkbox, .radio {
	border: 1px solid #252525;
	background: #464646;
}
.filters__dropdowns {
	background: var(--bg2);
}
.account ._dropdown__items {
	color: var(--text2);
}
.gogmix:first-child > .gogmix__count {
	background-color: hsla(350, 68%, 30%, 1);
}
.gogmix:nth-child(2)>.gogmix__count {
	background-color: hsla(18, 77%, 40%, 1);
}
.gogmix:nth-child(3)>.gogmix__count {
	background-color: hsla(25, 77%, 40%, 1);
}
.gogmix:nth-child(4)>.gogmix__count {
	background-color: hsla(33, 77%, 40%, 1);
}
.gogmix:nth-child(5)>.gogmix__count {
	background-color: hsla(38, 77%, 40%, 1);
}
.gogmix:nth-child(6)>.gogmix__count {
	background-color: hsla(44, 73%, 40%, 1);
}
.gogmix:nth-child(7)>.gogmix__count {
	background-color: hsla(45, 62%, 40%, 1);
}
.gogmix__count {
	background-color: hsla(53, 35%, 40%, 1);
}


.dlcs .product-tile--list:not(.product-tile--active) .product-tile__title {
	color: var(--text4);
}
.dlcs .product-tile--list:not(.product-tile--active) {
	border-bottom: 1px solid var(--border3);
}
.skin-selector__current, .skin-selector__dropdown-icon {
	color: var(--text2);
}
.product-title__flag {
	background: #555;
}
.description .module {
	background-color: rgba(255, 255, 255, 0.1)!important;
}
.average-item, average-item, .average-item__textline {
	color: var(--text4);
}
.sort-by__title {
	color: var(--text3);
}
.filter__title {
	color: var(--text2);
}
.filter__item, .option__hr {
	border-bottom: 1px solid var(--border3);
	color: var(--text3);
}
.review__title, .review__profile-name, .review-new__add-button {
	color: var(--text1);
}
.review__extra, .review__profile-stats {
	color: var(--text3);
}
.review__text, .reviews__info, .review__was-helpful-button {
	color: var(--text2);
}
.paginator-container .page-indicator {
	color: var(--text3);
}
.review__item--most-helpful {
	border: 1px solid hsla(0, 0%, 50%, 0.1);
	background-color: hsla(0, 0%, 50%, 0.1);
}
.review__top-bar {
	background-color: hsla(0, 0%, 50%, 0.2);
}
/* /redeem */
.redeem-header__text {
	color: var(--text1);
}
.code-input__input {
	border: 1px solid var(--border3);
	color: var(--text2);
	background: #2d2d2d;
}
/* /mix */
.bg_holder:before {
	content: "";
	position: absolute;
	width: 100%;
	height: 500px;
	background: linear-gradient(to top,var(--bg) 15%,rgba(0, 0, 0, .2));
}
.game {
	border-bottom: 1px solid var(--border3);
}
.game p {
	color: #b5b5b5;
}
.mix_cont.first {
	border-top: 1px solid var(--border3);
	border-bottom: 1px solid var(--border3);
}
.mix_cont {
	border-bottom: 1px solid var(--border3);
}
.mix_cont .dark_un {
	color: var(--text2);
}
.mix_cont .count {
	color: var(--text2);
	background: rgba(255, 255, 255, .03);
}
.first .count {
	background: rgba(255, 255, 255, .03);
}
.mix_cont .votes {
	color: var(--text2);
}
.mix_cont .count_left, .mix_cont .count_right {
	background: none;
}
.refresh {
	color: var(--text3);
}
/* /news */
.article-section__title, .article__secondary_title {
	color: var(--text2);
}
.article__body, .article__body p {
	color: var(--text1);
}
.article__lead {
	border-bottom: 1px solid var(--border2);
}
.archives-switch {
	border-color: var(--border2);
}
.archives-switch:after {
	border-left: 1px solid var(--border2);
}
.archives-list__article {
	border-top: 1px solid var(--border2);
}
.small-spots .product-row {
	background: var(--bg2);
}
.small-spots .product-row.product-row--has-action:hover, .small-spots .product-row.product-row--has-card.is-owned:hover, .small-spots .product-row.product-row--has-card:hover {
	background: var(--bg-hover);
}
.article__related_topics .product-row {
	background: var(--bg2);
}
.article__related_topics .product-row.product-row--has-action:hover, .article__related_topics .product-row.product-row--has-card.is-owned:hover, .article__related_topics .product-row.product-row--has-card:hover {
	background: var(--bg-hover);
}
.article__related_topics .product-row.is-owned {
	background: var(--bg3);
}
.btn--gray {
	background: linear-gradient(180deg,hsla(0, 0%, 24%, 0.9) 4%,#2c2c2c);
	color: #cacaca;
}
.btn--gray:not(.is-spinning):not(.is-disabled):not([disabled]):hover {
	background: linear-gradient(180deg,#494949,#3e3e3e);
}
/* /forum */
div.big_post_main {
	background: var(--bg2);
	margin-bottom: 0px;
}
div.big_post_left, div.big_post_right {
	width: 0px;
	height: 0px;
}

/* /account */
._cs-left, ._cs-right {
	background: var(--bg);
}
.page-header {
	color: var(--text3);
}
.header__dropdown {
	color: var(--text2);
}
.account__filters-option {
	color: var(--text2);
}
.filter.is-applied {
	color: var(--text2);
}
.list--rows {
	background: var(--bg2);
	color: var(--text1);
}
.list--rows .product-row__content {
	color: var(--text3);
}
.filter.filter--search {
	border: 1px solid #303030;
	background: #2c2c2c;
}
.filters__section~.filters__section>.filter {
	border-color: #1f1f1f;
}
.filter.is-contracted:hover {
	background: #232323;
}
.filter.filter--search>._search__input {
	color: var(--text3);
}
.product-btn {
	box-shadow: 0 1px 3px -1px rgba(0, 0, 0, 0.25);
	background-color: #252525;
}
.account__products .product-row__label, .product-btn, .user-rectangle__btn {
	border: 1px solid #373737;
}


.game-details:after {
	background: linear-gradient(to top,var(--bg) 20%,rgba(0, 0, 0, 0.50));
	content: '';
	position: absolute;
	width: 100%;
	height: 100%;
	left: 0;
	top: 0;
	z-index: -1;
}
.module-header2__element--selected, .module-header2__dropdown, .module-header2__element-label {
	color: var(--text2);
}
.game-link__right {
	color: var(--text3);
}
.game-details__footer {
	color: var(--text3);
	background: rgba(0, 0, 0, 0);
	border-top: 1px solid #1d1d1d;
}
.game-details__galaxy-message {
	color: var(--text1);
}
.game-details__header-icon {
	filter: invert(1);
}
.game-details__section-description {
	color: var(--text3);
}

.module-header__btn {
	color: var(--text3);
}
.wishlist-options__text {
	color: var(--text3);
}
.header-dropdown__items>._dropdown__item {
	color: var(--text3);
}
.module-header2__element {
	color: var(--text3);
}
.module-header2__element--action:hover {
	color: var(--text2);
}
.module-header2__element--selected {
	color: var(--text2);
}
.iScrollIndicator {
	background: rgba(255, 255, 255, 0.18);
}
.iScrollIndicator:active, .iScrollIndicator:hover {
	background: rgba(255, 255, 255, 0.25);
}
.skin-selector__item.is-selected {
	color: var(--text2);
}
/* /checkout */
.form__row-key--small-print {
	color: var(--text3);
}
.form__row, .free-wallet {
	border-top: 1px solid  var(--border);
}
.form__row--interactive:hover {
	background: var(--bg-hover);
}
.accordion__body {
	background: #2b2b2b;
}
.accordion__label.is-active {
