}
.order-message:first-child {
	border-top: 1px solid var(--border3);
}
.order-message:last-child {
	border-bottom: 1px solid var(--border3);
}
.order-message {
	border-top: 1px dashed var(--border3);
}

/* /work */
.sta_container h1 {
	color: var(--textw);
}
.left a, .right a, .single a {
	color: var(--textw);
}
.header h2 {
	color: var(--text2);
}
.vdark_un {
	color: var(--text2);
}
.vdark_un:hover {
	color: var(--textw);
}
.sub-menu__item {
	color: var(--text2);
}
