.settings__nav-link.is-active, .settings__nav-link.is-active:hover {
	background: var(--bg-hover);
}
.order-item__header {
	background: var(--bg2);
}
.order-product__price {
	color: var(--text3);
}
.settings-content, .order-header-info {
	color: var(--text1);
}
.settings-item__label {
	color: var(--text3);
}
.settings__nav-svg {
	filter: invert(1);
}
