	background: #2d2d2d;
	border: 1px solid var(--border3);
	color: var(--text1);
}
.input:disabled::-webkit-input-placeholder {
	-webkit-text-fill-color: var(--text1);
}
.input::-webkit-input-placeholder {
	color: var(--text1);
}
select {
	color: var(--text1);
	background-color: #2d2d2d;
}
.tag-item {
	background: rgba(0, 0, 0, 0);
}
.tag-input-wrapper {
	border: 1px solid var(--border3);
	background: #2d2d2d;
}
.tag-input-wrapper__input {
	color: var(--text1);
}
.orders-header__search {
	color: var(--text3);
}
.orders-header__search.is-search-active {
	color: var(--text3);
	background: #2d2d2d;
	box-shadow: inset 0 0 0 1px var(--border3), inset 0 1px 3px rgba(0,0,0,.1);
}
._search__input {
	color: var(--text1);
}
