.job-company__section--jobs {
	background-color: var(--bg);
}
.offer-wrapper {
	background: var(--bg2);
}
.offer-wrapper:hover {
	background: var(--bg-hover);
}
.job-listings-search-input {
	background-color: #2c2c2c;
	box-shadow: inset 0 1px 3px rgba(0,0,0,.1);
	color: var(--text3);
