}
.about-header--dark-bg .about-header__menu--big-screen>.about-header__btn {
	color: rgba(255, 255, 255, 0.5);
}
.about-header--dark-bg {
	color: rgba(255, 255, 255, 0.08);
}
.glx-section--footer .glx-section__inner {
	color: var(--text3);
}
/* /reclaim */
.star-header {
	color: var(--textw);
}
.star-field__input {
	border: 1px solid var(--border3);
	background: #2c2c2c;
	box-shadow: inset 0 1px 3px rgba(0,0,0,.1);
	color: var(--text3);
}
.star-field__input.is-error {
	border-color: var(--border3);
	background: #2c2c2c;
}
.star-field__input::-webkit-input-placeholder {
	color: var(--text3);
}
.product-row.product-placeholder {
	border: 1px dashed var(--border3);
	color: var(--text2);
}
.product-row.product-placeholder .product-placeholder__text:after {
	background: var(--border3);
}
/* top menu */

}
@-moz-document regexp("https://www.gog.com/(?!u|feed|profile).*") {
/* main background */
html, body {
	background: var(--bg);
	color: var(--text1);
}
}
@-moz-document regexp("https://www.gog.com/(?!mix).*") {
.content, .wrapper {
	background-color: var(--bg);
}
}
@-moz-document url-prefix("https://www.gog.com/partner"), url-prefix("https://www.gog.com/reclaim"), url-prefix("https://www.gog.com/promo") {
.content:before {
	background: linear-gradient(to top,var(--bg) 50%,rgba(0, 0, 0, .3));
	content: '';
	position: absolute;
	width: 100vw;
	height: 100vw;
}
.container {
	z-index: 10;
	position: relative;
}
@media (min-width: 801px){
.content:before {
	background: linear-gradient(to top,var(--bg) 50%,rgba(0, 0, 0, 0.3));
}
}
@media (min-width: 1367px) {
.content:before {
	background: linear-gradient(to top,var(--bg) 60%,rgba(0, 0, 0, 0.3));
}
}
@media (min-width: 1681px) {
.content:before {
	background: linear-gradient(to top,var(--bg) 70%,rgba(0, 0, 0, 0.3));
}
}
}
