css:
@-moz-document url-prefix("https://mail.google.com") {
/*make category tabs thinner*/
.aKh {
    height: 42px;
}

/*do also for category line*/
.aKj, .J-KU-Jg-K9 .J-KU .aAy, .J-KU-Jg-K9 .J-KU .J-KU-KO .aAy {
    height: 42px;
}

/*shift icon up to match category shift up*/
.aKx, .J-KU-KO.aE2 .aKx {
    top: 11px;
}

/*shift category names up to give way to sender info */
.aKx, .J-KU-KO.aE2 .aKx {
    top: 9px;
}

.aPb .J-KU-KO.aE2 .aKx, .aE2 .aKx {
    top: 9px;
}

/*shift icons up*/
.aKp {
    top: -3px;
}

/*sender info make smaller font*/
.aKw>.aKs {
    color: #111;
    font-size: 9px;
    line-height: 16px;
}
}
