css:
.CodeMirror {
    border-bottom-color: #5C5C5B;
    border-top-color: #5C5C5B
}
.CodeMirror-cursor {
    transition: 1s;
    -webkit-transition: 1s;
    -moz-transition: 1s
}
.CodeMirror-sizer {
    border-right: none;
}
.CodeMirror-scroll {
    overflow: hidden !important
}

.codeeditor_statusbar {
    background: #272822;
    color: #f8f8f2;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

#codeshell-wrapper .grey-header {
    background: #272822;
    color: #f8f8f2;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}

#s2id_select-lang .select2-choice {
    background: #272822 !important;
    color: #f8f8f2;
    border: 1px solid #5C5C5B !important;
}

#s2id_select-lang .select2-choice div {
    background: #272822 !important;
}
.select2-drop,
.select2-input {
    background: #272822 !important;
    color: #f8f8f2 !important;
}

.cm-s-ambiance.CodeMirror,
.code-editors .loading-mode {
    background: #282828;
    color: #f8f8f2;
}
.cm-s-ambiance div.CodeMirror-selected {
    background: #232323;
}
.cm-s-ambiance .CodeMirror-line::selection,
.cm-s-ambiance .CodeMirror-line > span::selection,
.cm-s-ambiance .CodeMirror-line > span > span::selection {
    background: #232323;
}
.cm-s-ambiance .CodeMirror-line::-moz-selection,
.cm-s-ambiance .CodeMirror-line > span::-moz-selection,
.cm-s-ambiance .CodeMirror-line > span > span::-moz-selection {
    background: rgba(73, 72, 62, .99);
}
.cm-s-ambiance .CodeMirror-gutters {
    background: #272822;
    border-right: 0px;
    padding-right: 5px;
    height: 100% !important;
}
.cm-s-ambiance .CodeMirror-guttermarker {
    color: white;
}
.cm-s-ambiance .CodeMirror-guttermarker-subtle {
    color: #d0d0d0;
}
.cm-s-ambiance .CodeMirror-linenumber {
    color: #d0d0d0;
}
.cm-s-ambiance .CodeMirror-cursor {
    border-left: 2px solid #f8f8f0 !important;
}
.cm-s-ambiance .CodeMirror-code pre {
    padding-left: 10px;
}
.cm-s-ambiance span.cm-comment {
    color: #777777;
}
.cm-s-ambiance span.cm-atom {
    color: #ae81ff;
}
.cm-s-ambiance span.cm-number {
    color: #EDE480;
}
.cm-s-ambiance span.cm-property,
.cm-s-ambiance span.cm-attribute {
    color: #a6e22e;
}
.cm-s-ambiance span.cm-keyword {
    color: #F49D62;
}
.cm-s-ambiance span.cm-builtin {
    color: #66d9ef;
}
.cm-s-ambiance span.cm-string {
    color: #BDDCDC;
}
.cm-s-ambiance span.cm-variable {
    color: #8EBFBF;
}
.cm-s-ambiance span.cm-variable-2 {
    color: #EDE480;
}
.cm-s-ambiance span.cm-variable-3 {
    color: #EDE480;
}
.cm-s-ambiance span.cm-type {
    color: #EDE480;
}
.cm-s-ambiance span.cm-def {
    color: #fd971f;
}
.cm-s-ambiance span.cm-bracket {
    color: #f8f8f2;
}
.cm-s-ambiance span.cm-tag {
    color: #f92672;
}
.cm-s-ambiance span.cm-header {
    color: #ae81ff;
}
.cm-s-ambiance span.cm-link {
    color: #ae81ff;
}
.cm-s-ambiance span.cm-operator {
    color: #ffffff;
}
.cm-s-ambiance span.cm-error {
    background: #f92672;
    color: #f8f8f0;
}
.cm-s-ambiance .CodeMirror-activeline-background {
    background: #232323;
}
.cm-s-ambiance .CodeMirror-matchingbracket {
    text-decoration: underline;
