css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(wartale.com) {
	body {
        background-color: #01294b;
        color: #fffefa;
    }

    .ipsType_pageTitle,
    .ipsType_light,
   	.ipsType_richText {
        color: #fffefa;
    }

    .ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ),
    .cPost.ipsComment:not( .ipsComment_selected ):not( .ipsModerated ) .cAuthorPane,
	.ipsComment_highlighted.ipsBox:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular ), .ipsComment_highlighted:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular ) .ipsAreaBackground_reset,
.cke_wysiwyg_frame, .cke_wysiwyg_div,
  	.ipsAreaBackground_reset {
        background-color: #d4b580;
    }
  
 	 .ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even), .cke_top, .ipsAreaBackground_light {
     	background-color: #dec395;
     }

    .cPost.ipsComment:not( .ipsComment_selected ):not( .ipsModerated ) .cAuthorPane,
  	.ipsButtonBar,
   	.ipsType_sectionTitle,
	.ipsWidget .ipsCalendarDate,
     .ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
        background: #c29e61;
       color: #fffefa;
    }

  .ipsRichEmbed_header.ipsAreaBackground_light,
.ipsQuote_citation {
    background-color: #af8e58;
  }
  
  .ipsRichEmbed,
  .ipsQuote {
  	background-color: #c5a978;
  }

  .ipsColumn.ipsColumn_fluid,
  html[dir="ltr"] .ipsColumns > .ipsColumn:first-child:not( .ipsPad ), html[dir="ltr"] .ipsColumns.ipsColumns_noSpacing > .ipsColumn:not( .ipsPad ) {
  	padding-left: 12px;
  }
}
