/* AGENT_SHEET */
/*regexp('http://www\\.kohlchan\\.net/(?!ernst).*'),
regexp('https://www\\.kohlchan\\.net/(?!ernst).*') */
@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 domain("kohlchan.net"),
 domain("www.kohlchan.net"),
 url-prefix(http://kohlchan.net),
 url-prefix(https://kohlchan.net)
{
  /*@media (max-width: 767px)
  {                  
    div.post.reply::before
    {
      display: none !important;
    }
    div.post
    {
    padding-left: 0px !important;
    margin-left: 0px !important;
    }
    div.boardlist
    {
      font-size: 20px;
    }
    iframe#sidebar
    {
      display: none;
    }
    iframe#main
    {
      border-left: 0px solid black;
      width: 100%;
    }
    .desktop-style div.boardlist:nth-child(1)
    {
      position: static;
    }
  }*/
  div.site_name, div.site_slogan, h1, .postsubject
  {
    font-family: Courier, monospace !important;
    color: #00c000 !important;
  }

  h2, .postername, .tripcode
  {
    font-family: Courier, monospace !important;
  }

  span.hidden_thread, span.board_newposts, li.board_newposts, span.nav_group_button, .postreply, .postreply_highlighted, .file_reply
  {
    background: #222 !important;
  }

  span.hidden_thread
  {
    border: 1px solid #333 !important;
  }

  div.postform_info_notice
  {
    background: #333 !important;
    border: 1px solid #00c000 !important;
    color: #aaa !important;
  }

  div.postform_info_error
  {
    background: #333 !important;
    border: 1px solid #f00 !important;
    color: #aaa !important;
  }

  body
  {
    background: #111 !important;
  }

  p
  {
    color: #aaaaaa;
  }

  a
  {
    text-decoration: none !important;
  }

  hr
  {
    background-color: #222;
    height: 1px;
    border: 0;
  }

  ul
  {
    color: #aaa;
  }

  div.radio_onair, div.radio_idle, div.radio_offline
  {
 /* Radio-Tooltip*/
    color: #00c000 !important;
    background-color: #222 !important;
    border: 1px solid #00c000 !important;
  }

  img#button_radio_details + span
  {
 /* Radio-Leiste*/
    color: #555 !important;
    font-weight: normal !important;
  }

  body > div > b > a
  {
 /* Radio-Stream-Links */
    font-weight: normal !important;
  }

  body > div > b
  {
  /*Klammern zwischen Radio-Streams etc., so ein Krebs */
    color: #555 !important;
    font-weight: normal !important;
  }

  div
  {
 /* Löschen-Leiste, Datum, Dateiinfos etc. */
    color: #888 !important;
  }

  .boardlist a:hover
  {
    background: #00c000 !important;
    color: #333 !important;
  }

  form > div > table, td
  {
    border-style: none !important;
    color: #555;
  }

  span.nav_group_button
  {
    color: #00c000 !important;
    border: 1px solid #00c000 !important;
  }

  h2
  {
    color: #ddd !important;
    background: #333 !important;
    font-size: 120% !important;
  }

  center h2
  {
    font-family: sans-serif !important;
    font-size: 100% !important;
    color: #00c000 !important;
    background-color: #222 !important;
    border: 1px solid #00c000 !important;
  }

  .invalidquotelink
  {
    color: #555 !important;
  }

  a.quotelink:active, a.quotelink:link, a.quotelink:visited
  {
    color: #cccccc !important;
  }

  a.quotelink:hover
  {
    font-weight: normal !important;
    color: #00c000 !important;
  }

  table.postform td.label
  {
    color: #aaaaaa !important;
    background: #222 !important;
  }

  table.postform td.label_highlighted
  {
    background: #222;
  }

  form td
  {
/* "zum Löschen von Posts" und Zeile darüber*/
    color: #aaaaaa;
  }

  td.message_notice.firstrow_left
  {
 /* Meldungen beim Datei hochladen */
    background-color: #222 !important;
    color: #555 !important;
  }

  td.message_text.firstrow_right, td.message_text.lastrow_right
  {
    background-color: #222 !important;
    color: #aaa !important;
  }

  td.message_info.lastrow_left
  {
    background-color: #00c000 !important;
    color: #222 !important;
  }

  span.linkbox
  {
    background-color: #222 !important;
  }

  span.linkbox a, a:link, .authority_mod
  {
    color: #00c000 !important;
  }

  a:visited, .quote
  {
    color: #070 !important;
  }

  input[type=file] *
  {
/* Datei-Hochlade-Feld*/
    color: #555 !important;
  }

  center form
  {
 /* Shi-Painter */
    color: #333 !important;
  }

  form textarea *
  {
    background-color: #222 !important;
    color: #00c000 !important;
    border: none !important;
  }

  form input *
  {
    background-color: #222 !important;
    color: #ccc !important;
  }

  .postername
  {
    color: #ccc !important;
    font-size: 120%;
  }

  .tripcode
  {
    color: #aaa !important;
    font-size: 120%;
  }

  .postsubject
  {
    font-weight: bold !important;
    font-size: 150% !important;
  }

  .omittedinfo
  {
    color: #555 !important;
  }

  div.post.reply.highlighted /*fixed*/
  {
    border: 1px solid #00c000 !important;
  }

  span.spoiler:hover
  {
    color: #aaa !important;
  }

  .file_thread
  {
    background: #222 !important;
    border: #555 1px solid !important;
  }

  .file_reply
  {
    border: #555 1px solid !important;
  }

  .file_thread a, .file_reply a
  {
    color: #aaa !important;
  }  /* Links über Vorschaubildern*/

  div.box_title
  {
    background-color: #333 !important;
    color: #00c000 !important;
  }

  div.menu_title
  {
    background: #333 !important;
  }

  div.box
  {
    background-color: #222 !important;
    border-left: solid 1px #333 !important;
    border-right: solid 1px #333 !important;
    border-bottom: solid 1px #333 !important;
  }

  div.newsbody
  {
    background-color: #222 !important;
    border-left: solid 1px #333 !important;
    border-right: solid 1px #333 !important;
    border-bottom: solid 1px #333 !important;
  }

  img[src*="/images/button-expand.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/icon-report.png"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-paint.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-open.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-close.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-checkmark.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-refresh.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-animation.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }

  img[src*="/images/button-question.gif"]
  {
    padding: 0px 0px 15px 15px !important;
    width: 0px !important;
    height: 0px !important;
    background: url(data:image/png;base64,) no-repeat !important;
  }
  div.post.reply
  {
      background: #222 !important;
      border-color: #555 !important;
  }
  .post.op .file
  {
  background: #222;
  border: #555 1px solid;
  }
  p.fileinfo > span
  {
  color: #555 !important;
  }
  .file, .file.multifile 
  {
  background: #222;
  border: #555 1px solid; 
  }
  div.ban
  {
    background-color: #111 !important;
    border: 1px solid #555 !important;
  }
  legend
  {
    background-color: #222;
  }
  form table tr th
  {
    background-color: #222;
  }
  div.pages
  {
    background-color: #111;
    border: #111;
  }
  div.pages a.selected
  {
    color: #00c000;
  }
  a.post_anchor
  {
    display: block; position: relative; top: -30px; visibility: hidden;
  }
  div.post.reply::before
  {
    content: ">>";
    position: absolute;
    margin-left: -34px;
  }
  div.boardlist
  {
    background-color: #333 !important;
  }
}
