css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.ru")
{

/*Ashkore Familiar*/  
 
  /*Images - pictures*/
  #pet-headerPortrait-base
  {
    background: url("https://b.radikal.ru/b22/1805/51/b1d074ef56ca.png") -15px top no-repeat !important;
    background-size: auto 125px !important;
  }

  #pet-portrait
  {
    background: url("https://d.radikal.ru/d35/1805/9b/9b0928cf7123.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"]
  {
    display: none !important;
  }

    /*Infos*/

  #pet-info-name:before
  {
    content: "Ashkore" !important;
    font-size: 20px !important;
  }

  #pet-info-name
  {
    font-size: 0 !important;
  }

  #pet-info-evolve > .on
  {
    font-size: 0 !important;
  }

  .pet-info-evolve-icon
  {
    font-size: 13px !important;
  }

  .pet-info-evolve-icon:after
  {
    content: "Укрощение строптивого";
  }

  #pet-info-racegender:before
  {
    content: "Dragon" !important;
    font-size: 16px !important;
    line-height: 20px !important;
  }

  #pet-info-racegender
  {
    font-size: 0 !important;
    margin-top: 5px;
  }

  img[src*="/static/img/f.png"]
  {
    background: url("http://www.eldarya.ru/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
  }
 
 /*Nourriture - food*/
  
  #pet-info-food > img
  {
    background: url("https://b.radikal.ru/b05/1805/aa/2f83aed8d37a.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
  }

  #pet-info-food
  {
    font-size: 0 !important;
  }

  #pet-info-food:before
  {
    content: "Pans" !important;
    font-size: 16px !important;
  }

  #pet-info-food > span
  {
    font-size: 16px !important;
  }

  /*Ezarel sur la page du profil*/

  #player-display-pet
  {
    background: url("https://c.radikal.ru/c15/1805/e7/be5a95a6be85.png") no-repeat;
    background-size: auto 100%;
    height: 560px !important;
    width: 250px !important;
  }

  #player-display-pet > img
  {
    display: none !important;
  }
}
