css:
@-moz-document domain("warframe.market")
{
/*******background***********/
.floating-menu,
.floating-menu__section .root-menu-button > .combobox,
.floating-menu__section .root-menu-button > a,
.content .content__header--second,
.content .content__body,
.ad__line,
#cdm-zone-0b,
.cdm-zone-0b,
.user__about--edit textarea,
.widget-order__content,
.widget-order__content .widget-order__header {
    background: #23232d;
}
.seeker .seeker__input,
.content__header--second select,
.content__header flex--left,
.content__header select,
.content__body select option,
.content__header--second select option,
.content__header select optio,
.content .content__header,
.chat__empty,
.selector .selector__body,
.user-orders__sort,
.chat__controls,
.statistics__header,
.statistics__numbers,
.review__zero,
.btn__inside-input,
.chat__form textarea,
.item_orders .item_orders__sort,
.floating-menu__section .root-menu-button > ul,
.item-statistics__header,
.form-control,
.form-control:focus {
    background: #1d1d24;
}
.order-unit,
.messenger__block.opened .chat__row,
.messenger__block .messenger__chat_list,
.messenger__block .messenger__chat_block .chat__block,
.order-row,
.floating-menu__section .root-menu-button > ul li.online-switch > span.active,
.widget-order__top ul li {
    background: #2b2b38;
}
.order-unit:nth-child(odd),
.message.own,
.order-row:nth-child(2n),
.floating-menu__section .root-menu-button > ul li.online-switch > span:hover,
.widget-order__top ul li:nth-child(odd) {
    background: #22222b;
}


/*********color***************/
.content h1,
.chat__form textarea,
.user__about--edit textarea,
.widget-order__content .widget-order__header {
    color: #759098;
}
.form-control:focus,
.seeker .seeker__input .seeker__real-input input {
    color: #fff;
}


/*********border**************/
.chat__controls,
.statistics__header,
.ad__line .flex--left .selector .selector__body,
.btn__inside-input,
.user-orders__sort,
.sort-button,
.widget-order__content .widget-order__header {
    border-bottom: 1px solid #265663;
}
.selector .selector__body {
    border-left: 1px solid #265663;
}
.btn__inside-input {
    border-right: 1px solid #265663;
}
.selector .selector__body,
.btn__inside-input {
    border-top: 1px solid #265663;
}
.chat__form textarea,
.seeker .seeker__input,
.widget-order__top ul {
    border: 1px solid #265663;
}


/*********misc****************/
.group__controls > rect {
    fill: #1d1d24;
}
.ad__line {
    border: none;
}
section.content + section.flex--root {
    background-color: #22222b;
}
.settings__page .settings__content {
    background: #23232d !important;
}
.settings__content input,
.settings__content select {
    background-color: #1d1d24 !important;
    border-color: #1d1d24 !important;
    color: #77939b;
}
.settings__content .input-group-addon {
    background-color: #2b2b38;
    border-color: #1d1d24 !important;
    color: #77939b;
}
.verification__tutorial li:nth-child(2n) > div,
.verification__tutorial .compose__box {
    border: none;
    background-color: #2b2b38;
}
.item_orders--empty {
    background-color: #22222b;
}
.verification__tutorial li:nth-child(2n)>div,
.verification__tutorial .compose__box,
.transition__group {
    border: none;
    background-color: #2b2b38;
}
.settings__page .settings__content,
.content__body .container {
    background: #23232d !important;
}
.settings__content input,
.settings__content select,
input.form-control,
a.just_link,
.social_links,
.review__form select,
.review__form textarea,
.review__form .subtext,
.review__block,
.review__block p,
.review__listing + nav li a.active,
a.btn__light:hover,
.achievement__block,
.floating-menu__section .user__avatar {
    background-color: #1d1d24 !important;
    border-color: #1d1d24 !important;
    color: #77939b;
}
.achievement__block * {
    border-color: #2b2b38 !important;
}
.review__listing + nav li a.btn.active {
    color: #39a57e;
}
.settings__content .input-group-addon {
    background-color: #2b2b38;
    border-color: #1d1d24 !important;
    color: #77939b;
}
.verification__tutorial li:nth-child(2n)>div {
    background-color: #2b2b38;
}
.ad__line {
    border: none;
}
section.content + section.flex--root {
    background-color: #23232d;
}
a,
.content a {
    color: #5490a0;
}
.description__flex,
.floating-menu__section li.separator {
    border-left-color: #2b2b38 !important;
    border-left-style: solid !important;
}
.floating-menu__section .root-menu-button > ul,
.floating-menu__section .root-menu-button > ul li.online-switch {
    border-top-color: #2b2b38;
    border-top-style: solid;
}
.floating-menu__section .root-menu-button > ul li.online-switch {
    border-bottom-color: #2b2b38;
    border-bottom-style: solid;
}
.order-unit .order-unit__icon img {
    border-color: #265663;
}
.order-unit .order-unit__icon .order__type-marker {
    background-color: #265663 !important;
}
.marker_buy {
    color: #c6e1d7 !important;
}
.btn__light.red,
.s-offline,
.marker_sell {
    color: #ea6100 !important;
}
.floating-menu__section .root-menu-button > ul li.button > a:hover,
.floating-menu__section .root-menu-button > ul li.button > div:hover {
    background-color: #2b2b38 !important;
}
.logotype a {
    filter: invert(1) hue-rotate(180deg);
}
.order__inactive:nth-child(even) {
    border-left-color: #b95005 !important;
}
.order__inactive:nth-child(odd) {
    border-left-color: #ea6100 !important;
}
.load__box .btn:first-child {
    margin-right: 37px;
}
.order__own_line.row.order-row {
    background-color: #171e21 !important;
}
i.mi.market-discord {
    color: #677bc4;
}
}
