css:
@-moz-document domain("theregister.co.uk") {
* {
    text-shadow: none !important;

}
html, body {
    background: #141414 !important;
    color: #00727a !important;

}

#article #body {
    background: #141414 !important;
    color: #959D94 !important;
    line-height: 1.4em !important;
    font-size: 1.1em !important;

}

#forum_posts {
    background: #878787 !important;
    color: #3fe20d !important;
    line-height: 1.4em !important;
    font-size: 1.1em !important;
}

#content {
    background: #141414 !important;
    color: #3fe20d !important;
    line-height: 1.4em !important;
    font-size: 1.1em !important;
}

#body .crosshead {
    background: #c6c6c6 !important;
    color: #00727a !important;
    line-height: 1.4em !important;
    font-size: 1.1em !important;
}

div {
    background-color: inherit !important;
}

a:link, a.blacklink:link {
    color: #D2DDD0 !important;
    text-decoration: none;
}

a:visited, a.blacklink:visited {
    color: #959D94 !important;
    text-decoration: none;
}

a:hover {
    color: #8A5FAF !important;
    text-decoration: none;
}

.pages {
    background-color: #000 !important;
    border: none !important;
    font-size: 1em !important;
}

input {
    background-color: #000 !important;
    color: #FFF!important;
    border: #000 1px solid !important;
    margin: 5px !important;
}

.fullpost {
    background-color: #1F201F !important;
}

img.starfish {
    border: #000 1px solid !important;
}

.comments, #bestcomments {
    background-color: #1F201F !important;
    line-height: 200% !important;
    padding-top: 2px !important;
}
.commentside {
    background-color: #0B0B0B !important;
    line-height: 160% !important;
}

.surveybox, .statusbox {
    background-color: #0B0B0B !important;
}
.accountfloat {
    background-color: #000 !important;
    font-size: 0.5em !important;
    color: #959D94 !important;
}
.righthandbar img {
    opacity: 0.75;
    filter: alpha(opacity=75); /* For IE8 and earlier */
}
#mc_embed_signup {
    background-color: #1F201F !important;
    font-size: 1em !important;
}

}
