css:
@-moz-document domain(dwarffortresswiki.org) {
body,
div#content,
#mw-page-base,
#mw-head-base,
#mw-head,
.portal,
div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul li,
div.vectorTabs ul li span,
div.vectorTabs ul li.selected {
    background-image: none;
}
body,
#mw-panel,
#mw-head,
div.vectorTabs ul li,
#pagehistory li.selected {
    background: #191a1f;
}
#mw-panel.collapsible-nav .portal {
    margin: 0;
}
div.vectorTabs ul li a {
    color: #aaa;
}
div.vectorTabs ul li.selected {
    box-shadow: inset 0px -3px 0px 0px #5b57f9
}
div.vectorTabs ul li.selected a {
    color: white;
}
div.vectorTabs ul li:hover {
    box-shadow: inset 0px -3px 0px 0px #5b57f9;
}
div.vectorTabs ul li a:hover {
    color: #ddd;
    text-decoration: none;
}
div#content {
    color: #ddd;
    background: #22232b;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #ddd;
}
div#mw-panel div.portal div.body ul li,
div#mw-panel.collapsible-nav div.portal div.body ul li {
    padding: 10px;
}
div#mw-panel .portal .body ul li:hover,
div#mw-panel.collapsible-nav div.portal div.body ul li:hover {
    background: #22232b;
}
div#mw-panel div.portal div.body ul li a,
div#mw-panel div.portal div.body ul li a:visited,
div#mw-panel.collapsible-nav div.portal div.body ul li a,
div#mw-panel.collapsible-nav div.portal div.body ul li a:visited,
div.vectorTabs ul li.selected a,
div.vectorTabs ul li.selected a:visited,
div.vectorTabs li.new a:visited {
    color: #aaa;
    height: 100%;
    width: 100%;
    display: block;
}
div#mw-panel div.portal div.body ul li a:hover,
div#mw-panel.collapsible-nav div.portal div.body ul li a:hover,
div#mw-panel.collapsible-nav div.portal.collapsed h5:hover,
div#mw-panel.collapsible-nav div.portal.collapsed h5 a:hover,
div.vectorTabs ul li.selected a:hover {
    text-decoration: none;
}
div#mw-panel.collapsible-nav div.portal.collapsed h5 a {
    color: #4d4d4d;
}
div#p-navigation first {
    border-bottom: 1px solid #aaa;
}
#content i {
    color: #6a6b83;
}
#content div.thumbinner {
    background-color: #191a1f;
}
#bodyContent a:visited {
    color: #CAC802
}
#toc,
.toc {
    background-color: #191a1f;
    border: none;
}
.keyboard-key {
    color: #191a1f;
    box-shadow: none !important;
}
table.wikitable {
    background: #191a1f;
    width: 100%;
    color: #aaa;
}
table.wikitable > * > tr > th {
    background-color: #3f414f;
}
.special .mw-line-even,
.special .mw-line-odd {
    padding: 5px;
}
code {
    background-color: #191a1f;
}
.adsbygoogle,
#p-googleadsense,
div#content center,
div#content #bottom {
    display: none;
}
}
