css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.google.com")
{
	body
	{
		height: 100%!important;
	}

	*
	{
		font-family: "/*[[font]]*/"!important;
		font-size: 15px!important;
	}

	h3, h3 *
	{
		font-size: 25px!important;
	}

	.FVeGwb.CVnAc
	{
		margin-left: 180px!important;
		padding-top: 0px!important;
	}

	main.HKt8rc.CGNRMc
	{
		position: absolute!important;
		top: -20px!important;
		max-width: calc(100% - 430px)!important;
	}

	HEADER#gb.gb_Xa.gb_tb.gb_Id.gb_Jd.gb_sc > .gb_ke + .gb_Hd.gb_de.gb_Nd + .gb_Od.gb_de + .gb_qc.gb_oc.gb_g.gb_rc.gb_sc:last-child
	{
		max-width: 150px!important;
	}

	HEADER#gb.gb_Xa.gb_tb.gb_Id.gb_Jd.gb_sc > .gb_qc.gb_oc.gb_g.gb_rc.gb_sc > .gb_tc > :first-child:last-child > .daOE8d.eejsDc > .zWHNCf.EA71Tc > .SFllF
	{
		height: 30px!important;
	}

	HEADER#gb.gb_Xa.gb_tb.gb_Id.gb_Jd.gb_sc > .gb_qc.gb_oc.gb_g.gb_rc.gb_sc > .gb_tc > :first-child:last-child > .daOE8d.eejsDc > .zWHNCf.EA71Tc > .SFllF.smCdcb
	{
		font-size: 12px!important;
		padding-left: 5px!important;
	}

	HEADER#gb.gb_Xa.gb_tb.gb_Id.gb_Jd.gb_sc > .gb_qc.gb_oc.gb_g.gb_rc.gb_sc > .gb_tc > :first-child:last-child > .daOE8d.eejsDc > .zWHNCf.EA71Tc > .SFllF.smCdcb *
	{
		font-size: 12px!important;
	}

	a > .PysvYb
	{
		margin: 8px 5px 0px 5px!important;
	}

	aside.Xw9d0.SbZred
	{
		position: fixed!important;
		left: calc(100% - 230px)!important;
		top: 70px!important;
		width: 230px!important;
		overflow-y: scroll!important;
		height: calc(100% - 70px)!important;
		padding: 5px!important;
	}

	article.MQsxIb.xTewfe.R7GTQ.keNKEd.keNKEd.YKEnGe.EyNMab.lNIQOe.t6ttFe.Fm1jeb.EjqUne
	{
		padding: 5px!important;
	}

	.To2ZZb.u9jkpc.hpDt6e.DbQnIe.R7GTQ.keNKEd,
	.To2ZZb.u9jkpc.hpDt6e.DbQnIe.rrijPb.R7GTQ.keNKEd
	{
		border-radius: 5px!important;
		height: auto!important;
		display: inline-block!important;
		float: left!important;
		padding: 3px!important;
		margin: 2px!important;
	}

	.To2ZZb.u9jkpc.hpDt6e.DbQnIe.R7GTQ.keNKEd *,
	.To2ZZb.u9jkpc.hpDt6e.DbQnIe.rrijPb.R7GTQ.keNKEd > *
	{
		margin: 0px!important;
		width: auto!important;
		height: auto!important;
		line-height: 1!important;
	}
}
