css:
@-moz-document regexp("^(?!.*:2096.*)(.*writscrib.com.*).*$") {
body > :not(.es-photo-popup):not(#eb), .eb-content #eb {
	-webkit-filter: invert(100%);
	-moz-filter: invert(100%); 
    -o-filter: invert(100%);
    -ms-filter: invert(100%);   
}

img, .es-stream-embed__player, .es-quickposting, .ckit-circle, .es-story-panel-button.active, .es-story-actions, .es-icon-reaction, .es-profile-header__cover, .popbox-dropdown-nav__image, .es-sharing-list i, .es-stream-embed__cover-img, .eb-nmm-media__embed-item, .eb-nmm-image-preview.is-image, .es-album-browser-content a.embed-responsive, i.es-album-cover b, .es-photo-content div.es-photo em.es-photo-image, .es-popup-viewport, #es .video-container, #es .es-card .embed-responsive-item, #es .es-card__cover, #es .es-album-cover, #es .es-stream-embed__player .video-container iframe{
    -webkit-filter: invert(100%);
	-moz-filter: invert(100%); 
    -o-filter: invert(100%);
    -ms-filter: invert(100%);   
}

.o-nav__link.eb-navbar__icon-link i {
	  color: hsl(0, 50%, 66%); 
}


#sp-header, #sp-footer, #eb .btn-eb-primary {
    background-color: hsl(0, 18%, 85%) !important;
    color: hsl(180, 0%, 5%) !important;
    /*box-shadow: 0 3px 3px rgba(92, 133, 133, 0.2);*/
}

#eb .btn-eb-default {
 	background-color: hsl(21, 18%, 38%) !important;
    color: hsl(180, 0%, 5%) !important;
}

#eb .o-onoffswitch__checkbox:checked+.o-onoffswitch__label, #eb .o-onoffswitch__checkbox:checked+.o-onoffswitch__label:before {
 	border-color: hsl(308, 52%, 53%);
}

#eb .o-onoffswitch__checkbox:checked+.o-onoffswitch__label {
	background-color: hsl(308, 52%, 53%);
}

#sp-topesbar-wrap {
    background-color: hsl(0, 18%, 85%);
    color: hsl(180, 0%, 5%);
    border-bottom: 1px solid hsl(0, 50%, 66%);
}

#sp-main-body, .sp-dropdown-inner {
    background-color: hsl(0, 15%, 80%) !important;
    color: hsl(180, 0%, 13%) !important;
}

#sp-header a{
    color: hsl(180, 0%, 9%);
}

#sp-header a:hover {
    color: hsl(28, 31%, 52%);
}

#es .es-side-widget__title {
    color: hsl(180, 0%, 47%);
}

#es .es-side-widget__ft > a, #es .es-side-widget__ft > a:hover, #es .es-side-widget__ft > a:active, #es .es-side-widget__ft > a:focus, .es-side-widget__bd a, #sp-main-body a:hover {
    color: hsl(0, 50%, 66%) !important;
}

#es .es-profile-header-nav .is-active .es-profile-header-nav__link{
    color: hsl(0, 50%, 66%) !important;
    border-bottom-color: hsl(0, 50%, 66%) !important;
}


#sp-main-body a, a, body #es .btn-es-default-o, body #es .btn-es-default-o:hover, body #es .btn-es-default-o:active, body #es .btn-es-default-o:focus, body #es .btn-es-primary-o, body #es .btn-es-primary-o:hover, body #es .btn-es-primary-o:active, body #es .btn-es-primary-o:focus, #es .es-side-menu-panel__heading a:hover, #es .es-convo__sidebar-item, #es .words>span.without-data #es .words>span:hover, #es .es-convo__attached-link, #es .es-navbar__footer-link:hover, #es .es-navbar__footer-submenu > li.is-active > .es-navbar__footer-link, .es-profile-header-nav__item.is-active a, .es-profile-header-nav__item a:hover {
    color: hsl(0, 50%, 66%);
}

body #es .btn-es-default-o, .t-lg-pull-right.btn.btn-es-primary-o.btn-xs i, .es-verified, .eb-navbar-rss-icon, .o-nav__link.mod-es-menu-bar__icon-link i, #sp-footer1 a, #sidebar-toggler i, a.eb-nmm-breadcrumb__link, .eb-nmm-actionbar__cell.eb-nmm-actionbar__cell--upload a.btn, .eb-nmm-actionbar__cell.eb-nmm-actionbar__cell--upload a i, #eb .eb-nmm-actionbar .btn-nmm-upload, #eb .eb-nmm-actionbar .btn-nmm-new-folder, #eb .eb-nmm-sub-action a.is-active, #eb .eb-nmm-places-list__item.is-active .eb-nmm-places-list__link, #eb .eb-nmm-places-list__item.is-active .eb-nmm-places-list__icon, #eb a, #eb .t-text--primary {
    color: hsl(0, 50%, 66%) !important;
}

#eb .is-selected .eb-nmm-media__body, #eb .btn-eb-primary:hover {
	background-color: hsl(0, 50%, 66%) !important;
}

#eb .o-checkbox input[type="checkbox"]:checked+label:before {
	background-color: hsl(0, 50%, 66%);
    border-color: hsl(0, 50%, 66%);
}

#eb .eb-nmm-media-selection .eb-nmm-media, #eb .eb-composer-panel-tab.active {
    border-color: hsl(0, 50%, 66%);
}


#es .es-side-widget .o-tabs--stacked .o-tabs__item.active > .o-tabs__link{
	border-left-color: hsl(0, 50%, 66%);
}


/*.scrollup {
    right: 58px;
    border-radius: 50%;
}*/

#tinymce p, #es textarea.o-form-control, #tinymce p {
	color: #FFF !important;   
}

#es textarea.o-form-control {
	color: #000 !important;   
}

body #es a.profile-10 {
    color: hsl(271, 95%, 50%) !important;
}

body #es [data-name="Mr. Alpha"] .es-profile-header__title a, body #es [data-actor="6502"] .es-stream-title a, body #es [data-id="6502"] .o-flag div.o-flag__body > a {
    color: hsl(90, 56%, 48%) !important;
}

div.es-popup-handle {
	background-color: white;   
}
}
