css:
@-moz-document domain("www.roblox.com"), domain("web.roblox.com") {
body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  /*background*/
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://i.imgur.com/eBsXESC.png") !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(5px) !important;
    background-color: rgb(240,240,240) !important;
    z-index: -1 !important;
  }

  .container-list h3, .home-header-content h1 a {
    text-shadow: 2px 2px 2px #000;
    color: #FFF !important;
  }
  
  .home-header-content a {
    color: #888888 !important;
  }
  .blog-news .news .news-link {
    background: none !important;
  }
  .blog-news .news .news-link::after {
    background: none !important;
  }
.avatar-headshot, .avatar-card-image, .avatar, .avatar-card-container, .avatar-card-fullbody, .avatar .avatar-card-link, .avatar .avatar-card-image, .avatar-card-fullbody .avatar-card-link, .avatar-card-fullbody .avatar-card-image{
	border-radius: 0% !important;
}
#BodyWrapper, ul#dropdownUl, #MyCreationsTab, .gear-button-wrapper, .container-footer, .avatar, .avatar-card-btns, .StandardBox, #SearchControls, body, .text, pre,.MyRank span{
	background: rgba(0,0,0,0) !important;
    color: #000 !important;
    border-left: 0px !important;
    border-right: 0px !important;
    border-top: 0px !important;
    border-bottom: 0px !important;
}
  /*navigation bar*/
  .rbx-header {
    background-color: rgba(0, 0, 0, 1) !important;
  }
  /*.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0px 0px 0px red inset !important;
  }*/
  
  .game .game-item {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
/*Trim content backgrounds, fill in new ones*/
  #Body, #BodyWrapper {
    background: transparent !important;
  }
  /*groups page*/
  #mid-column {
    background-color: #FFF !important;
  }
  /*develop page*/
  .BuildPageContent {
    background-color: #FFF !important;
    float: none !important;
  }
  #MyCreationsTab {
    width:792px !important;
  }
  .content-area {
    border-right: none !important;
  }
  /*devex page*/
  #DevExLeftColumn {
    height: 684px !important;
  }
  /*account settings*/
  #AccountPageRight {
    background-color: #FFF !important;
  }
  .settings-section {
    padding: 10px !important;
  }
  .divider-right {
    border: none !important;
  }
  .StandardPanelContainer .StandardPanelWhite {
    padding: 0 !important;
    padding-left: 6px !important;
  }
  
  /*games page*/
  .games-page-left {
    margin-right: 10px !important;
  }
  .games-page-lists-container {
    background: none !important;
  }
  .games-list-container {
    border-left: 10px solid rgba(255,255,255,0) !important;
    border-right: 10px solid rgba(255,255,255,0) !important;
    background-color: rgba(255,255,255,0.9) !important; 
    margin-bottom: 60px !important;
  }
  .games-list {
    min-height: 275px !important;
  }
  
  /*Games page ad support*/
  .content .games-lists-multiple .games-list-container {
    width: unset !important;
  }
  #GamesListContainer1, #GamesListContainer8 {
    margin-right: 315px !important;
  }
  
  /*game page*/
  .rbx-para-overflow, .rbx-para-overflow::after {
    background: transparent !important;
  }
  /*catalog*/
  
  div#catalog {
    display: inline;
    background-color: #FFF !important;
    padding-left: 10px !important;
    padding-bottom: 5px !important;
  }
  #Body {
    background: #FFF !important;
  }
  .right-content {
    padding-left: 10px !important;
  }

  /*profile page*/
  p, p::after,
  .rbx-text-overflow, .rbx-text-overflow::after {
    background: transparent !important;
  }
  /*friends page*/
   .content .friends-subtitle, .content .friends-title {
     text-shadow: 2px 2px 2px #000;
     color: #FFF !important;
   }
  /*inventory page*/
  div.row > h1:nth-child(1), 
  .container-header .inventory-title h3,
  .recommendations-container h3 {
     text-shadow: 2px 2px 2px #000;
     color: #FFF !important;
  } 
  .pager-holder .rbx-pager {
    background-color: #FFF !important;
    border: 3px solid #FFF !important;
    border-radius: 4px !important;
  }
  /*item page*/
  #Item {
    background-color: #FFF !important;
    padding-top: 5px !important;
    padding-right: 5px !important;
  }
  #ItemContainer h1 {
    text-shadow: 2px 2px 2px #000;
    color: #FFF !important;
    padding-right: 5px;
  }
  /*forums*/
  .table-header .first {
    border-left: none !important;
  }
  .table td, .table th {
    padding-left: 10px !important;
  }
  /*place configuration*/
  .boxed-body {
    background-color: #FFF !important;
    padding-bottom: 10px !important;
  }
  #navbar {
    height: auto !important;
  }
}
