css:
@-moz-document domain("www.repubblica.it"), domain("repubblica.it") {
body , #rep-page-header {
    background-color: #000 !important;
}
#container , .rep-container , #rep-slim-header , #rep-page-header .top-header-wrapper , .column-6.pillar-right , .main-search-input , #adv-Piede {
    background-color: #3a0d0d !important;
}
.block-12, .block-2, .block-3, .block-4, .block-6, .block-8 {
    border: 1px solid #2a0f0f !important; 
    background: #470e0e  !important; 
    border-top: 4px #2a0f0f solid !important; 
}
/*testi*/
a {
    color: #b1b609 !important; 
}
/*homepage*/
.widget-partner {
    background: #641515 !important; 
    border: 1px solid #000 !important; 
}

#pwa-rep_button {
    border-top: 4px solid #986D1A !important; 
    /* background-image: url(//www.repstatic.it/cless/main/nazionale/2016-v1/img/home/Rep-logo.svg); */
    background-color: #470e0e !important; 
}
.entry-subtitle em, .entry-title em {
    background-color: #6c1616 !important; 
}
.widget-network {
    background: #561919 !important;
    border-top: 4px #3a0d0d solid !important;
}
.widget-network .swiper-pagination {
    background-color: #561919 !important;
}
/*LOGO*/
.rep-main-logo a , .rep-small-logo a {
    background-image: url(https://i.imgur.com/UOhMEmc.png);
}
.block-12-7.from-rep .block-overtitle, .block-12-8.from-rep .block-overtitle, .block-8-5.from-rep .block-overtitle {
    background-image: none !important ;
    background-color: #2a0f0f !important ;
    }
/*header che trasla*/
#rep-slim-header {
    border-top: 5px #000 solid !important;
    border-bottom: 1px #000 solid !important;
}
a.rep-small {
    background-image: linear-gradient(180deg,transparent 75%,#864922 0) !important;
}
/*bordi*/
.shared-detail header.middle-img .summary {
    border-top: 1px solid #9b5b17 !important;
}
.soft-news .share-and-comment , #rep-page-header .bottom-header-wrapper , .widget-network .swiper-slide , .share-and-comment , .top-footer section , aside.related , .toolbar , #network-extra-container , .topic-related , .middle-header-wrapper , .section-tecnologia .type-list li{
    border-bottom: 1px #000 solid !important;
}
[class*="from-"]::before , [class*="from-"] .block-overtitle::after , #pwa-rep_button::before {
    background-color: #000 !important;
}
[class*="from-"]::after {
    background-color: #575925 !important;
}
[class*="block-"] .media-figure , aside.related h1.label , .extra-container .account-data-container, .box-miolibro .iml-bottom-link li.first , #detail h1.category-name , #detail .topic-slider article , aside.related article , .toolbar time {
    border-right: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
}
.block-12::before, .block-2::before, .block-3::before, .block-4::before, .block-6::before, .block-8::before {
    background-color: #986d1a !important;
}
[class^="block-"].soft-news::after , .block-12::after, .block-2::after, .block-3::after, .block-4::after, .block-6::after, .block-8::after {
    background: #2A0F0F !important;
}
.bottom-header-wrapper , #trust-home , .toolbar , #network-extra-container , .box-miolibro .iml-bottom-link, .shared-detail .body-text aside.tags , #detail .category-slider , .widget-box_item {
    border-top: 1px solid #000 !important;
}
.hot-topics , .widget-meteo , .widget-giochi , .widget-oroscopo , .widget-iniziative-editoriali , .widget-guide-espresso , .widget-guide-rep , .trovaristoranti-box , .widget-dizionario {
    border: 1px solid #000 !important;
}
#rep-ed-locali-menu-btn , #rep-slim-header a.r-plus , #rep-slim-header [class*="rep-icon-"]{
    border-left: 1px solid #000 !important;
}
.hot-topics li {
    border-top: 4px solid #5f1414 !important;
}
.widget-blog {
    border: 1px solid #651313  !important;
    border-top: 4px #541010 solid  !important;
   } 


/* footer */
.widget-meteo , .widget-giochi , .widget-oroscopo , .widget-iniziative-editoriali , .swiper-pagination , .widget-guide-espresso , .widget-guide-rep , .trovaristoranti-box , .widget-dizionario , .top-footer {
    background: #241a2b !important;
}
#rep-page-footer .top-footer {
    border-top: 8px solid #3a0d0d !important;
}
#rep-page-footer .top-footer {
    border-top: 8px solid #351313 !important;
}
div#box-tvzap {
  display: none !important;
  background-color: khaki !important;
}
#box-tvzap {
  display: none !important;
  background-color: khaki !important;
}
#page-footer {
    background-color: #000 !important;
}
iframe {
    display: none !important;
}

/* Articolo */

body , .shared-detail header h1 , .shared-detail header .summary{
    color: #ceb031 !important;
}
aside.related article a h1, aside.related article a h1 a, aside.related article a h1 a:visited {
    color: #c9d725 !important;
}
detail .swiper-container .next {
    background-color: #744a16 !important;
}
.box-miolibro {
    border: 1px solid #863535 !important;
}
aside.related h1.label , #detail h1.category-name {
    color: #88bf1a !important;
}
.shared-detail .swiper-container .prev, .shared-detail .swiper-container .next {
    background-color: #87451a !important;
}
.section-base-template .main-nav ul li.selected a {
    background-color: #000 !important;
    border-top: 2px solid #000 !important;
    border-right: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    border-bottom: 1px solid #000 !important;
}
.main-nav ul li a {
    background: #541f1f !important;
    border-right: 1px solid #541f1f !important;
    border-left: 1px solid #541f1f !important;
    border-top: 2px solid #541f1f !important;
}
.section-base-template .main-nav ul li {
    border-right: 2px solid #000 !important;
}
/*sezioni*/
.bottom-header-wrapper {
    background: #000 !important;
}
.rep-section-menu > li.active > a, .rep-section-menu > li > a:hover {
    background: #38320b !important;
}
.widget-box {
    background: #322e07 !important;
}
.middle-header-wrapper .main-search-input {
    border: 1px solid #5e5c1e !important;
}
/*update*/
.shared-detail .body-text .inline-video h1.inline-video-title {
    font: 24px/26px 'lato-black',Arial,'Helvetica Neue',Helvetica,sans-serif;
    color: #eee22c !important;
}
}
