css:
@-moz-document url-prefix("https://news.ycombinator.com/") {
/* reset */

body,
td,
table,
input,
textarea,
.pagetop,
* {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif;
    font-weight: 400;
    -webkit-font-smoothing: antialiased;
}
body {
    background: #333;
    margin: 0;
}
.pagetop,
.pagetop a,
.pagetop a:visited {
    color: rgba(233, 233, 233, 0.5);
}
.brandname {
    display: none;
}
table {
    font-size: 14px;
}
#hnmain {
    width: 800px;
    background: transparent;
}
#hnmain > tbody > tr:last-child img {
    display: none;
}
#hnmain > tbody > tr:last-child table td {
    background: transparent;
}
#hnmain > tbody > tr > td {
    background: transparent;
}
#hnmain > tbody > tr > td[bgcolor] > table {
    padding: 0 0 20px 0 !important;
}
#hnmain > tbody > tr > td[bgcolor] > table > tbody > tr > td {
    font-weight: 400;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td:first-child a {
    background: #F06D3C;
    color: #fff;
    height: 50px;
    line-height: 50px;
    width: 50px;
    display: block;
    text-align: center;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td:first-child a:after {
    content: "HN";
    font-weight: 700;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td:first-child a img {
    display: none;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td:nth-child(2) .pagetop {
    color: #fafafa;
    padding-left: 10px;
    font-size: 14px;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td .pagetop {
    font-size: 14px;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td .pagetop a:hover {
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    padding-bottom: 2px;
}
.spacer {
    display: none;
}
.title,
.sitebit,
.subtext {
    font-family: inherit;
    font-size: inherit;
    text-align: left;
    position: relative;
}
.itemlist {
    width: 100%;
}
.votelinks a {
    display: block;
    position: relative;
    left: -4px;
    top: -2px;
}
.votearrow {
    background-image: none;
    margin: 0;
    width: 100%;
}
.votearrow:after {
    content: "â–²";
    display: block;
    background: #F06D3C;
    color: #fff;
    text-align: center;
    width: 14px;
    height: 14px;
    line-height: 13px;
    border-radius: 100%;
    font-size: 8px;
    -webkit-transition: all 0.15s;
}
.votearrow:hover:after {
    -webkit-transform: scale(1.3);
}
.athing + tr .subtext {
    position: relative;
}
.athing + tr .subtext {
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    padding-bottom: 16px;
}
.athing td {
    padding: 16px 0 4px;
}
.athing .title:first-child {
    padding-left: 20px;
}
.athing .title a:link,
.athing .title a:visited {
    color: rgba(233, 233, 233, 0.95);
    font-size: 18px;
}
.athing .title .rank {
    text-align: center;
    width: 30px;
    height: 30px;
    line-height: 30px;
    border-radius: 100%;
    display: inline-block;
    background: #fff;
    border: 1px solid rgba(0, 0, 0, 0.05);
    font-weight: 700;
    font-size: 12px;
    color: #555;
    position: absolute;
    left: 0;
}
.athing .title .comhead {
    display: none;
}
.subtext {
    color: rgba(233, 233, 233, 0.5);
}
.subtext a:link,
.subtext a:visited {
    color: rgba(233, 233, 233, 0.5) !important;
}
.subtext a:hover {
    text-decoration: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    padding-bottom: 2px;
}
.comment-tree .default .comhead {
    font-size: 14px !important;
    color: rgba(0, 0, 0, 0.5);
}
.comment-tree .comment {
    font-size: 14px !important;
    color: rgba(245, 245, 245, 0.5);
    line-height: 1.42em;
}
.c00,
.c00 a:link {
    color: rgba(245, 245, 245, 1);
}
.c5a,
.c5a a:link,
.c5a a:visited {
    color: rgba(211, 211, 211, 0.9);
}
.c73,
.c73 a:link,
.c73 a:visited {
    color: rgba(189, 189, 189, 0.8);
}
.c82,
.c82 a:link,
.c82 a:visited {
    color: rgba(167, 167, 167, 0.7);
}
.c88,
.c88 a:link,
.c88 a:visited {
    color: rgba(135, 135, 135, 0.6);
}
.c9c,
.c9c a:link,
.c9c a:visited {
    color: rgba(113, 113, 113, 0.4);
}
.cae,
.cae a:link,
.cae a:visited {
    color: rgba(91, 91, 91, 0.3);
}
.cbe,
.cbe a:link,
.cbe a:visited {
    color: rgba(69, 69, 69, 0.2);
}
.cce,
.cce a:link,
.cce a:visited {
    color: rgba(47, 47, 47, 0.1);
}
.cdd,
.cdd a:link,
.cdd a:visited {
    color: rgba(25, 25, 25, 0.1);
}
#hnmain table {
    width: 100%;
}
#hnmain form textarea {
    display: block;
    width: 100%;
    box-sizing: border-box;
    background: #fff;
    border: 1px solid rgba(0, 0, 0, 0.05);
    font-weight: 400;
    font-size: 14px;
    padding: 10px;
    outline: none;
}
#hnmain form input[type=submit] {
    border: solid 1px #F06D3C;
    height: 32px;
    background: transparent;
    border-radius: 32px;
    padding: 0 12px;
    color: #F06D3C;
    cursor: pointer;
}
.itemlist tr:last-child .title {
    padding: 10px 0;
}
.itemlist tr:last-child .title a {
    border: solid 1px #F06D3C;
    height: 32px;
    line-height: 32px;
    background: transparent;
    border-radius: 32px;
    padding: 0 12px;
    color: #F06D3C;
    display: inline-block;
}
td.ind {
    width: 0px;
}
td.votelinks {
    width: 0px;
}
}
