css:
@-moz-document url-prefix("https://kitsu.io/") {
    
    .library-sidebar .is-sticky, .media--sidebar .is-sticky {
        position: sticky;
        top: 65px;
    }

    .media-sidebar--sticky {
        position: sticky;
        top: 209px;
    }

    .cover-nav.navbar-light {
        margin-bottom: 20px;
    }

    .library-ad {
        margin-bottom: 0;
    }

    .feed-actions {
        margin-top: 0;
    }

    .user-cover, .user-cover .cover-photo {
        height: 300px;
    }

    .library-issues {
     display:none;   
    }

    .cover-page .transparent {
        background: #402f3f;
        transition: none;
        border-bottom: 1px solid #271c26
    }

    #kitsu-navbar {
        background: #402f3fe6;
    }

    .cover-page .transparent .navbar-buttons input {
        background: #332532;
    }

    .site-search__icon .icon svg {
        fill: #FFF;
    }

    .site-search__input.active {
        background: #332532;
    }

    .site-search__input, .notifications {
        transform: none;
    }

    .navbar-search-results {
        margin-left: 121px;
    }

    .notifications svg path {
        fill: #FFF;
    }

    .entry-icons .reaction-status svg, .entry-icons .reaction-status--add svg {
        width: 16px;
    }

    .media--feed {
        max-width: unset;
    }

    .container, .quick-update .flickity-viewport {
        max-width: 1200px;
    }

    .media-container {
        max-width: unset;
    }

    .col-sm-8 {
        flex: 0 0 70%;
        max-width: 70%;
    }


    .library-list td {
        padding-top: 0.3rem;
        padding-bottom: 0.3rem;
    }

    .library-list .cell-poster {
        width:55px;
        height: 55px;
    }

    .library-list td.rating-cell, .library-list td.progress-cell, .library-list td.edit-entry {
        padding-top: 1rem;
    }

    .title-wrapper {
        padding-top: 3px;
    }

    .media-favorite {
        background: rgba(0, 0, 0, 0.2);
    }

    .media-favorite .heart-wrapper {
        fill: #FFF;
    }

    /* advanced search*/

    .browse-page .global-container {
        background: linear-gradient(180deg, #332532 42px, #f7f7f7 42px);
        background-attachment: fixed;
    }

    .media-browse .filter-options {
        left: 0;
    }

    .media-browse .media-poster-header {
        left: 240px;
        margin-left: 0px;
    }

    .media-browse .media-grid .media-posters {
        padding: 75px 0;
    }

    .media-browse .media-grid .media-posters .row .media-posters-list {
        padding-left: 15px;
    }

    .global-row > .media-browse .media-grid .poster-wrapper {
        width: 12.5%
    }

    /* more color */
    .library-list thead {
        background: #332532;
        color: #FFF;
    }
    
    .library-search input, .explore-search input {
        border: 1px solid #E7E7E7;
    }

    .cover-nav.navbar-light, .navbar > .container > .row {
        background-color: #3C2E3B;
    }

    .cover-nav.navbar-light .nav-item {
        border-right: none;
        color: #FFF;
    }

    .cover-nav.navbar-light .nav-item:hover {
        color: #FFF;
        background: #534852;
    }

    .cover-nav.navbar-light .nav-item.active:hover {
        background: #f7f7f7;
    }

    .cover-nav.navbar-light .nav-item:first-child {
        border-left: none;
    }

    .navbar-nav .tag {
        background-color: #332532;
        color: #FFF
    }

    .navbar-nav > .nav-item {
        height: 100%;
    }

    .search--header {
        background-color: #3c2e3b; 
        color: #FFF;
    }
}
