css:
@-moz-document url-prefix("https://www.hidive.com/") {
    /* Insert code here... */
    
    .body-bg-color {
        background-color: #1b1e23;
    }
    
    .container-fluid {
        max-width: 1400px;
    }
    
    .top-page-offset {
        padding-top: 56px !important;
    }
    
    .navbar {
        background: #0b2a368a;
        border-bottom: 1px solid #00aeef;
    }
    
    .alertRibbon, .carousel-bottom, .top-page-offset > .carousel-wrapper > .tour, #marketApp {
        display:none !important;
    }
    
    #nav-search {
        display: block;
        width: 250px;
        background: #061b24;
    }
    
    #nav-search > .btn-default:hover {
        background-color: unset !important;
        border: unset !important;
        color: rgba(0,174,240,1) !important;
    }
    
    .carousel-inner > .item > a > img, .carousel-inner > .item > img, .img-responsive, .thumbnail a > img, .thumbnail > img {
        width:100%;
    }
    
    .title-slider .slick-prev:hover::before, .title-slider .slick-next:hover::before, 
    .user-carousel .slick-prev:hover::before, .user-carousel .slick-next:hover::before,
    .window-slider .slick-prev:not(.slick-disabled):hover::before, .window-slider .slick-next:not(.slick-disabled):hover::before
    {
        color: rgba(0,174,240,1);
    }
    
    /*
    .window .closeBtn {
        text-shadow: 2px 2px 10px rgba(0, 0, 0, 0.9);
    }
    */
    
    .title-slider .hitbox .player {
        margin-top: 15%;
    }
    
    .window .closeBtn:hover {
        color: rgba(0,174,240,1);
        transition: none;
    }
    
    .title-slider .hitbox .expander span {
        color: rgba(0,174,240,1);
    }
    
    .title-slider .slick-next {
        right: -35px;
    }
    
    .title-slider .slick-prev {
        left: -35px;
    }
    
    .window-slider .slick-next {
        right: -25px;
    }
    
    .window-slider .slick-prev {
        left: -30px;
    }
    
    .slick-dots li button {
        width: 25px;
    }
    
    .title-slider .slick-dots button::before, .user-carousel .slick-dots button::before {
        font-size: 20px;
    }
    
    .window .episodes {
        padding: 40px 15px;
    }
    
    .top-padding-15 {
        padding-top: 10px;
    }
    
    .bottom-gutter-5 {
        margin-bottom: 0px;
    }
    
    .top-page-offset {
        padding-top: 60px;
    }
    
    .tooltip {
        display: none !important;
    }
    
    
    /* -------------- details window -------------- */
    .section > .window {
        padding-bottom:0px;
        margin-bottom: 15px;
        margin-top: -13px;
    }
    
    .section > .window .details {
        /*padding-top: 47px;*/
        height: auto;
    }
    
    .section > .window .navigation {
        padding-top:0;
        top: 0px;
        bottom: unset;
        border-radius: 5px 5px 0px 0px;    
        background: #0b2a36ba;
        border-bottom: 1px solid #00aeef;
    }
    
    .section > .window .navigation div {
        margin: 0;
    }
    
    .section > .window .details .title-text .text-container {
        padding-top: 45px
    }
    
    .section > .window .details .title-img, .window .details .img-rotator img {
        border-radius: 0 6px 5px 0;
    }
    
    .section > .window .episodes  {
        padding: 65px 15px 26px 15px;
        z-index: 2;
    }
    
    .section > .window .recommended  {
        padding: 65px 15px 39px 15px;
        z-index: 2;
    }
    
    .title-slider .green.arrow_box::before {
        border: none;
    }
    
    .title-slider .arrow_box::after {
        border-width: 15px;
    }
    
    .window .navigation div div span {
        font-size: 15px;
        margin-bottom: 0px !important;
    }
    
    .window .navigation span.nav-selected {
        border-bottom: none !important;
        color: white;       
            
    }
    
    .window .navigation div div span:not(.nav-selected):hover, .window .navigation div div span:not(.nav-selected):active {
        border-bottom: none !important;
        color: rgba(0,174,240,1);
    }
    
    .window-slider .synopsis h3 {
        height: 3.2rem;
        white-space: normal;
        color: #00aef0 !important;
    }
    
    .window-slider .synopsis p {
        margin: 5px 0 0 0;
    }
    
    
    .window .details .title-img .player .btn {
        margin-top: 15px;
    }  
    
    
    /* -------------- anime page -------------- */
    
    .top-page-offset > .window .details {
        height: auto;
    }
    
    .nav-tabs {
        border-bottom: 1px solid #00aeef;
        margin-top: 0 !important;
    }
    
    .nav-tabs li a {
        border: none;
        background: #1b1e23 !important;
        padding: 5px 40px;        
        font-size: 15px;
        color: rgba(167,172,178,1);
    }
    
    .nav > li > a:focus, .nav > li > a:hover {
        border: none;
        background: #1b1e23 !important;
        color: rgba(0,174,240,1);
    }
    
    
    .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
        color: white;  
        border: 1px solid #00aeef;
        border-bottom-color: transparent;
    }
    
    .top-page-offset > .nav-tabs > li.active > a, .top-page-offset > .nav-tabs > li.active > a:hover, .top-page-offset > .nav-tabs > li.active > a:focus { 
        background: #020202 !important;
    }
    
}
