css:
@-moz-document domain("127.0.0.1"), domain("mygamify.fr"), domain("adminlte.io") {
.skin-yellow .main-header .navbar,
.skin-yellow .main-header .logo,
.skin-yellow .main-header .navbar .nav > li > a,
.navbar .fa,
.main-footer,
form label,
.box-body,
.box,
.nav-tabs-custom > .tab-content,
.login-box-body,
.register-box-body,
.skin-yellow .main-header .navbar .sidebar-toggle
{
	background-color: rgb(24, 24, 24);
	color: white;
}
.skin-yellow .main-header .navbar .nav > li > a,
.navbar .fa,
.content-header h1,
.skin-yellow .main-header .navbar .sidebar-toggle{
	color: white;
	border-color: white;
}
.content-wrapper,
.nav-tabs-custom,
.disconected,
.right-side{
	background-color: rgb(45, 45, 45);
}
.box-footer,
.nav-stacked > li > a,
.box-header{
	background-color: rgb(60, 60, 60);
	color: white;
}

.main-footer,
.box-footer,
.nav-tabs-custom > .nav-tabs{
	border: 0 none;
}

/*Dashboar*/
.widget-user .img-circle{
	background-color: black;
	border: 0 none;
}
.widget-user .box-footer{
	color: white;
}

/*Header hover*/
.skin-yellow .main-header .navbar .nav > li > a:hover,
.skin-yellow .main-header .navbar .nav > li > a:focus,
.skin-yellow .main-header .navbar .nav > li > a:active,
.skin-yellow .main-header .navbar .sidebar-toggle:hover,
.skin-yellow .main-header .navbar .sidebar-toggle:focus,
.skin-yellow .main-header .navbar .sidebar-toggle:active,
.skin-yellow .main-header .logo:hover,
.skin-yellow .main-header .logo:focus,
.skin-yellow .main-header .logo:active
{
		background-color: black;
}
/*SIDEBAR*/
.skin-yellow .sidebar-menu > li.active > a,
.skin-yellow .sidebar-menu > li.active > a:hover,
.skin-yellow .sidebar-menu > li.active > a:active,
.skin-yellow .sidebar-menu > li.active > a:focus,
.skin-yellow .sidebar-menu > li.active > a:active:focus,
.skin-yellow .sidebar-menu > li:hover > a,
.skin-yellow .sidebar-menu > li:hover > a:hover,
.skin-yellow .sidebar-menu > li:hover > a:active,
.skin-yellow .sidebar-menu > li:hover > a:focus,
.skin-yellow .sidebar-menu > li:hover > a:active:focus,
.skin-yellow .sidebar-menu > li:active > a,
.skin-yellow .sidebar-menu > li:active > a:hover,
.skin-yellow .sidebar-menu > li:active > a:active,
.skin-yellow .sidebar-menu > li:active > a:focus,
.skin-yellow .sidebar-menu > li:active > a:active:focus,
.skin-yellow .sidebar-menu > li:active:focus > a,
.skin-yellow .sidebar-menu > li:active:focus > a:hover,
.skin-yellow .sidebar-menu > li:active:focus > a:active,
.skin-yellow .sidebar-menu > li:active:focus > a:focus,
.skin-yellow .sidebar-menu > li:active:focus > a:active:focus,
.skin-yellow .sidebar-menu > li:focus > a,
.skin-yellow .sidebar-menu > li:focus > a:hover,
.skin-yellow .sidebar-menu > li:focus > a:active,
.skin-yellow .sidebar-menu > li:focus > a:focus,
.skin-yellow .sidebar-menu > li:focus > a:active:focus{
	background-color: black;
}

/*DAT TABLE*/
table.dataTable,
.table > caption + thead > tr:first-child > td,
.table > caption + thead > tr:first-child > th,
.table > colgroup + thead > tr:first-child > td,
.table > colgroup + thead > tr:first-child > th,
.table > thead:first-child > tr:first-child > td,
.table > thead:first-child > tr:first-child > th{
	border: 0 none;
}

table.dataTable thead,
table.dataTable tfoot{
	background-color: rgb(24, 24, 24);
	color: white;
	border: 0 none;
}
table.table-bordered.dataTable tbody th,
.table-striped > tbody > tr:nth-of-type(2n+1){
	background-color: rgb(60, 60, 60);
	color: white;
	border: 0 none;
}
table.dataTable tfoot th,
table.dataTable tfoot td,
table.table-bordered.dataTable tbody th,
table.table-bordered.dataTable tbody td
{
	border: 0 none;
}
table.dataTable tbody tr.even{
	background-color: rgb(100, 100, 100);
	color: white;
	border: 0 none;
}

/*Inputs*/
.form-control{
	color: white;
	background-color: black;
}
}
