@-moz-document url-prefix("https://drive.google.com/drive") {
@font-face {
  font-family: 'Product Sans';
  font-style: normal;
  font-weight: 400;
  src: url(https://fonts.gstatic.com/s/productsans/v5/HYvgU2fE2nRJvZ5JFAumwegdm0LZdjqr5-oayXSOefg.woff2) format('woff2'), local('Open Sans'), local('OpenSans');
}
:root {
    --background: #111;
    --backgroundSolid: #111;
    --backgroundSolid-2: #212121;
    --backgroundSolid-3: #242424;
    --backgroundSolid-4: #383838;
    --transparent: rgba(255, 255, 255, 0);
    --textLight: #fff;
    --textLight-2: #c3c3c3;
    --firstTransparencyLevel: rgba(255, 255, 255, 0.1);
    --secondTransparencyLevel: rgba(255, 255, 255, 0.2);
    --borderColor: #212121;
    --borderRadius: 6px;
    --fabBorderRadius: 24px;
    --sidenavBorderRadius: 0 24px 24px 0;
}
.a-nEbBXb body {
    background: var(--background);
}
.a-nEbBXb .a-D-B.a-D-B-gc-WErN3d, .a-s-tb-Kg-Q, .a-s-tb-Kg-Q > .a-s-tb-sc-Ja-Q-x, .a-nEbBXb .a-da-Mf-B-ji-j, .a-nEbBXb .a-qc-La {
    background: var(--transparent);
    color: var(--textLight);
}
/*text color*/
.a-nEbBXb .a-U-J-x, .a-s-T, .a-t, .a-dkl3Ye .o-Yc-o-T, .a-nEbBXb .a-ok-Ji-ga {
    color: var(--textLight);
}
/*text color with shadow*/
.a-dkl3Ye .h-R-w-d.a-l-S-Pc-w-d > .h-R-w-d-ff, .w7Ppu {
    color: var(--textLight);
    text-shadow: 1px 1px #000;
}
/*icon color*/
.a-nEbBXb .a-U-ye-ha > .a-U-J .a-U-Ze svg, .a-nEbBXb .a-U-xc-J .a-Ng-c-wk svg, .a-nEbBXb .a-U-J .a-U-Ze svg, .h-sb-Ic.h-R-d.a-c-d {
    fill: var(--textLight);
}
/*icons color*/
.a-s-fa-Ha-pa {
    fill: var(--textLight);
}
/*borders*/
.a-nEbBXb .a-U-xc > .a-da-fc-Ug::after, .a-nEbBXb .a-U-xc > .a-da-Ul::before, .ub-ml-Pr.USrqEe-ml-Pr-qc-j::before, .ca-k-oa, .ca-k-wb-j, .a-fd-ba-lp, .a-fd-ba-Q {
    border-color: var(--borderColor);
}
/*large divider borders*/
.a-nEbBXb .a-D-B-gc-WErN3d .a-D-B-Ak, .a-Ra-Qc.a-nEbBXb .a-t-D {
    border-color: var(--borderColor);
    border-top-left-radius: var(--borderRadius);
    border-top-right-radius: var(--borderRadius);
}
/*fab new button*/
.a-D-gc-wKOTTc .a-ec-Gd button.RTMQvb {
    background-color: #1967d2;
    color: white;
    border-radius: var(--fabBorderRadius);
}
.a-D-gc-wKOTTc .a-ec-Gd button.RTMQvb:hover {
    background-color: #3275d1;
}
.RTMQvb.Kzazxf.fCmhtc.hc0pBf.x6jRSb.a-qb-d.h-sb-Ic.sXaDqb::before {
    content: url('http://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/btw_ic_speeddial_white_24dp.png');
    padding: 11px 4px 0 2px;
}
/*sidebnav selected*/
.a-nEbBXb .a-U-ye-ha > .a-U-J {
    background: var(--secondTransparencyLevel);
}
/*sidenav hover*/
.a-nEbBXb :not(.a-U-ye-ha) > .a-U-J:hover, .a-nEbBXb :not(.a-U-ye-ha) > .a-U-J-W {
    background-color: var(--firstTransparencyLevel);
}
/*sidenav border*/
.a-dkl3Ye .a-U-J {
    border-radius: var(--sidenavBorderRadius);
}
/*----------titlebar----------*/
/*background and text*/
.gb_ce {
    background: var(--transparent);
    color: var(--textLight);
}
/*icons*/
.gb_Ta svg, .gb_lc svg {
    color: var(--textLight);
}
/*border after*/
.gb_Xa:not(.gb_Za) .gb_Hd {
    border-bottom: 1px solid var(--borderColor);
}
/*drive logo opacity*/
.gb_he.gb_me {
    opacity: 1;
}
/*//GOOGLE DRAWER//*/
/*background*/
.gb_ia, .gb_fa, .gb_aa, .gb_da {
    background: #111 !important;
}
/*google app hover*/
.gb_Z:hover .gb_3, .gb_T:hover .gb_X {
    background: #212121;
}
.gb_Z:hover a, .gb_T:hover a {
    border: 1px solid #212121;
}
/*text color*/
.gb_3, .gb_fa, .gb_X {
    color: #ffffff;
}
#gb a.gb_ja, #gb a.gb_ja:visited, #gb a.gb_ja:active, #gb a.gb_ka, #gb a.gb_ka:visited, #gb a.gb_ea, #gb a.gb_fa {
    color: #ffffff;
}
/*more*/
.gb_ea {
    background: #212121;
}
/*separator*/
.gb_ka, .gb_fa {
    border-bottom: 1px solid #212121;
}
/*marketplace apps notice*/
.gb_na div.gb_pa, .gb_ja {
    background: #212121;
}
/*account select subaccounts*/
.gb_Qb, .gb_Kb, .gb_Sb, .gb_Nb {
    background: #111;
    border-top-color: #212121;
}
.gb_Sb:hover, .gb_Pb:hover {
    background: #333;
}
.gb_3b, .gb_0b, .gb_aa {
    color: #fff;
}
/*account select button*/
.gb_Ea, .gb_za {
    border: 0;
    font-family: 'Google Sans',Roboto,RobotoDraft,Helvetica,Arial,sans-serif;
    font-weight: 500;
}
.gb_Ea, #gb a.gb_Ea.gb_Ea, .gb_Fa  {
    color: #fff;
    cursor: pointer;
    background-color: #4d90fe;
}
.gb_Kb .gb_Jb:hover, .gb_Hb .gb_Fb:hover {

    background-color: #649efe !important;
    background-image: none;
    color: #fff !important;

}
/*bottom bar*/
.gb_Hb {
    background-color: #212121;
}
/*notifications border*/
#gbsfw.gb_Tc {
    border-color: #212121;
}
/*-------------------*/
/*searchbar*/
.gb_Qe {
    background: var(--firstTransparencyLevel);
}
.gb_9e::placeholder {
    opacity: 1;
    color: var(--textLight-2);
}
.gb_Qe:hover {
    background: var(--secondTransparencyLevel);
}
.gb_Qe {
    border-radius: var(--borderRadius);
}
/*----------top navbar----------*/
/*nav dropdown*/
.Hb-ja-Na .a-l-S-Pc-w-d.h-R-w-d-Pb, .a-dk13Ye .a-l-S-Pc-w-d.h-R-w-d-W {
    background-color: var(--backgroundSolidLight);
}
/*sharring icon*/
.H-qa-H-d {
    background-color: var(--backgroundSolidLight);
}
.H-qa-H-d-c svg {
    filter: invert(100%);
}
/*--------------------*/
/*----------file list----------*/
/*list background*/
.a-nEbBXb .a-t-j .a-gd-j, .a-nEbBXb .a-t-D {
    background-color: rgba(0, 0, 0, 0.8);
}
/*selected item background*/
.a-nEbBXb .a-t-J-ha {
    background-color: var(--secondTransparencyLevel);
}
/*hovered item background*/
.a-nEbBXb .a-t-J:hover {
    background-color: var(--firstTransparencyLevel);
}
/*item details color*/
.a-nEbBXb .a-t-J {
    color: var(--textLight-2);
}
/*remove list border*/
.a-nEbBXb .a-t-cb {
    border: 1px transparent;
}
/*item name color*/
.a-nEbBXb .l-t-T-V > .l-Ab-T-r, .a-nEbBXb .a-t-J-ha .l-t-T-V > .l-Ab-T-r {
    color: var(--textLight);
}
/*drag color*/
.a-t-J.a-l-Sa-Wa-ka .a-t-cb-oa {
    background: none;
    border-bottom: none;
    border-top: none;
}
/*--------------------*/
/*----------context menus----------*/
/*background*/
.h-w.a-w.a-w-Xi.a-mb-w, .a-nEbBXb .a-w.a-w-Mr, .a-nEbBXb .a-mb-w.a-w-Mr, .a-nEbBXb .a-w-Mr::after {
    background: none;
    background-color: var(--backgroundSolid-3);
}
/*background_selected*/
.a-nEbBXb .h-v.h-v-pc {
    background-color: var(--backgroundSolid-4);
}
/*text color*/
.a-nEbBXb .h-v-x {
    color: var(--textLight);
}
/*--------------------*/
/*----------file cards----------*/
/*card background*/
.a-dkl3Ye .l-u-xb.l-u-o, .a-dkl3Ye .l-u-xb.l-u-Ab {
    background-color: transparent;
    border: 1px solid rgba(0, 0, 0, 0.8);
    border-radius: var(--borderRadius);
}
.a-u-xb-j {
    background-color: rgba(0, 0, 0, 0.8);
    border-radius: var(--borderRadius);
}
/*card background hover*/
.a-dkl3Ye .l-u-xb.l-u-o:hover, .a-dkl3Ye .l-u-xb.l-u-Ab:hover {
    background-color: var(--firstTransparencyLevel);
    border-color: transparent;
}
/*card background selected*/
.a-nEbBXb .l-u-ha {
    background-color: var(--secondTransparencyLevel) !important;
    border-color: transparent !important;
}
/*card text color*/
.a-nEbBXb .l-u-V {
    color: var(--textLight);
}
/*card text color selected*/
.a-nEbBXb .l-u-ha .l-u-V {
    color: var(--textLight);
}
/*drag hover*/
.a-nEbBXb .a-u-xb-j.a-l-Sa-Wa-ka .l-u-xb.l-u-o {
    background: none;
}
.a-u-xb-j.a-l-Sa-Wa-ka>.l-u-xb.l-u-o, .a-u-xb-j.a-l-Sa-Wa-ka>.l-u-xb.l-u-Wa-ka {
    outline: none;
}
/*--------------------*/
/*add file background*/
.a-l-Sa-Wa-ka {
    background-color: #40404080;
}
/*----------Quick Access----------*/
/*card background*/
.L202Xe {
    background-color: rgba(0, 0, 0, 0.8);
    border-color: rgba(0, 0, 0, 0.8) !important;
}
/*card text*/
.a-HZnjzd-xb-V {
    color: var(--textLight);
}
.a-HZnjzd-xb-Np {
    color: var(--textLight-2);
}
.a-dkl3Ye .L202Xe {
    border-radius: var(--borderRadius);
}
/*--------------------*/
/*----------settings----------*/
.lb-k-x, .ca-k .lb-k-V {
    background-color: var(--backgroundSolid);
}
.lb-k-V-r {
    color: var(--textLight);
    font-family: Product Sans;
}
/*checkbox*/
.a-nEbBXb .g-Qa:not(.H-qa-ng-Vi) {
    border-color: rgba(255, 255, 255, 0.8);
}
}

@-moz-document url-prefix("https://drive.google.com/drive") {
:root {
    --border-radius: 2px;
    --border-radius-none: 0px;
    --border-radius-label: 1px;
    --primary-background-color: #111;
    --window-background-color: #212121;
    --secondary-background-color: #212121;
    --secondary-background-color-b: #242424;
    --tertiary-background-color: #363636;
    --quaternary-background-color: #424242;
    --hover-background-color-dark-0-2: rgba(255, 255, 255, 0.2);
    --button-outline: #484848;
    --window-button-outline: #484848;
    --text-color-1: #ffffff;
    --text-color-2: #e0e0e0;
    --text-color-3: #dcdcdc;
    --text-color-4: #bdbdbd;
    --text-color-5: #9e9e9e;
    --text-color-blue: #78b4f8;
}

/* Info pane header hover */
.a-Mg-V:hover > .a-Mg-V-T {
    color: var(--text-color-blue);
}

/* Info Pane Header */
.a-nEbBXb .a-ub-Gd {
    background-color: var(--primary-background-color);
}

/* unselected tab  */
.a-nEbBXb .a-ub-ra-B > .h-ra-B > .h-ra {
    color: var(--text-color-4);    
}


.a-nEbBXb .a-Mg-V-T {
    color: #fff;
}

.a-nEbBXb .a-ub-oa {
    background-color: var(--primary-background-color);
}

.y-C, .a-nEbBXb .y-E-gh-D .y-C-Ed-V {
    background-color: var(--primary-background-color);
}

/* document and file text colors*/
.a-nEbBXb .y-C-q-j, .a-nEbBXb .y-C-ka-T.y-C-xj-ka:hover {
    color: var(--text-color-4)    
}

.y-C-ka:hover {
    background-color: var(--primary-background-color);
}

/* discription heading ie You created 6 items  ... also used for dates next to icon*/
.a-nEbBXb .y-C-q-D, .a-nEbBXb .y-C-Ya, .a-nEbBXb .y-C-Ed-V {
    color: var(--text-color-4)
}

/* document editor name */
.a-nEbBXb .y-C-H-Sb {
    color: var(--text-color-2);
}

/* borderz */
.a-Ra-Qc .y-C-Ed-V, .a-Ra-Qc .a-ub-Gd, .a-nEbBXb .a-ub-ra-B > .h-ra-B {
    border-color: var(--secondary-background-color)
}






}
