	background: #444!important;
	border-radius: 0px!important;
	box-shadow: none!important;
	color: rgba(255, 255, 255, 1)!important;
}
#elSearchExpanded {
	border-radius: 0!important;
	box-shadow: none!important;
	color: #747b7f!important;
