    outline: none!important;
}
.embedly-card-hug {
    background-color: white!important;
}
.ipsApp .ipsComment .ipsComment_header {
    box-shadow: none!important;
    border-radius: 0px!important;
}
body.ipsApp #elUserNav .ipsNotificationCount {
    border-radius: 0px!important;
}
