@-moz-document regexp(".*anilist.co.*") {
/*
	Style applying to all of anilist. Be careful
	If your code only applies to a specific part, you can add it there instead.
	Sections available:
	".*anilist.co.*"
		all of Anilist
	".*anilist.co/user/.*"
		all user pages
	".*anilist.co/home"
		the feeds
	".*anilist.co/(studio|search/(anime|manga)).*"
		browse pages
	".*anilist.co/activity/.*"
		specific activities
	".*anilist.co/(anime|manga)/.* /[0-9a-zA-Z-]* /social"
		social tab on an entries page
	".*anilist.co/user/.* /(animelist|mangalist)"
		a user's list
	".*anilist.co/(anime|manga)/.* /[0-9a-zA-Z-]*"
		any anime or manga page
	".*anilist.co/user/.* /stats"
		a user's stats page
	".*anilist.co/forum/.*"
		all pages in the forum
*/
  .logo
  {
    margin-left: -60px!important;
/*the compact layout uses more of the space to the side, so we line up the logo to the left*/
  }

  .footer
  {
    margin-top: 0px !important;
/*less space wasted over the footer*/
  }

  .container
  {
    padding-left: 10px;
    padding-right: 0px;
  }

  .hide
  {
    top: 0px!important;
/*stop that top bar from jumping all over the place*/
  }

  .notification
  {
    margin-bottom: 10px!important;
  }
/*legacy code, expand popper*/
/*no longer needed with the current following|global system*/
  .section-header > .el-dropdown > .el-dropdown-menu
  {
    display: inline!important;
    background-color: transparent!important;
    margin-left: -500px;
    margin-top: -20px;
  }

  .section-header > .el-dropdown > .el-dropdown-menu > .el-dropdown-menu__item
  {
    display: inline;
    list-style-type: none;
    background-color: transparent!important;
  }

  .section-header > .el-dropdown > .el-dropdown-menu > .el-dropdown-menu__item:hover
  {
    background-color: transparent!important;
  }

  .section-header > .el-dropdown > .el-dropdown-selfdefine
  {
    display: none;
  }
/*end legacy code, expand popper*/

  .notification-dot.router-link-exact-active
  {
    color: #fff2f2!important;
  }

  .fa-frown
  {
    color: red;
  }

  .fa-smile
  {
    color: green;
  }
@keyframes animatedBackground {
	from { background-position: 0 0; }
	to { background-position: 100% 0; }
}

/*Dropdown menus are site theme based*/
  .quick-search .el-select .el-input .el-input__inner,
.quick-search .el-select .el-input.is-focus .el-input__inner,
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before
  {
    background: rgba(var(--color-foreground));
  }

  .el-select-dropdown__item.hover,
.el-select-dropdown__item:hover
  {
    background: rgba(159, 173, 189, .2);
  }

  .el-dropdown-menu__item--divided
  {
    border-color: rgba(var(--color-background));
  }

  .el-select-group__wrap:not(:last-of-type)::after
  {
    background: rgba(var(--color-foreground));
  }

  .el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after
  {
    border-bottom-color: rgba(var(--color-foreground));
  }

  .el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after{
    border-top-color: rgba(var(--color-foreground));
  }


/*Additions to dark theme dropdown*/
  .site-theme-dark .quick-search.el-select .el-input.el-input__inner,
.site-theme-dark .quick-search .el-select .el-input.is-focus .el-input__inner,
.site-theme-dark .el-select-dropdown,
.site-theme-dark .el-dropdown-menu,
.site-theme-dark .el-dropdown-menu__item--divided::before{
    background: rgba(17, 22, 29);
  }

  .site-theme-dark .el-select-group__wrap:not(:last-of-type)::after{
    background: rgba(17, 22, 29);
  }

  .site-theme-dark .el-popper[x-placement^="bottom"] .popper__arrow,
.site-theme-dark .el-popper[x-placement^="bottom"] .popper__arrow::after{
    border-bottom-color: rgba(17, 22, 29);
    opacity: 1;
  }

  .site-theme-dark .el-popper[x-placement^="top"] .popper__arrow,
.site-theme-dark .el-popper[x-placement^="top"] .popper__arrow::after{
    border-top-color: rgba(17, 22, 29);
    opacity: 1;
  }
.categories .category{
	text-transform: none;
	white-space: nowrap;
}
.like-wrap .users{
    overflow-y: scroll!important;
    scrollbar-width: none;
	-ms-overflow-style: none;
}
.like-wrap .users::-webkit-scrollbar{
    width: 0px;
    background: transparent;
}
}

@-moz-document regexp(".*anilist.co/user/.*") {
/*changes to the profile page*/
.user-social .user-follow > div.wrap{
    grid-template-columns: repeat(auto-fill,75px)!important;
    grid-template-rows: repeat(auto-fill,75px)!important;
}
.like-wrap .users{
    overflow-y: scroll!important;
    scrollbar-width: none;
	-ms-overflow-style: none;
}
.like-wrap .users::-webkit-scrollbar{
    width: 0px;
    background: transparent;
}

  .favourites > div > .wrap > div,
.favourites > div > .wrap > a
  {
/*make the spaces in the grid even*/
    margin-bottom: 0px!important;
    margin-right: 0px!important;
    column-gap: 10px!important;
  }
.el-dropdown-menu{
    box-shadow: none!important;
}

  .favourites > div > .wrap
  {
    padding: 0px!important;
    display: grid;
    grid-gap: 10px;
    column-gap: 10px!important;
    grid-template-columns: repeat(auto-fill,85px);
    grid-template-rows: repeat(auto-fill,115px);
    background: rgb(0,0,0,0) !important;
    width: 470px;
  }

  .activity-replies
  {
    margin-top: 5px!important;
    margin-left: 30px!important;
    margin-right: 0px!important;
  }

  .activity-entry
  {
    margin-bottom: 10px!important;
  }

  .reply
  {
    padding: 8px!important;
    margin-bottom: 5px!important;
  }

  .user .overview
  {
    grid-template-columns: 460px auto!important;
  }

  .shadow
  {
    display: none;
  }
/*wae's additions:*/
.genres, .statuses {
    grid-template-columns:repeat(auto-fill,auto);
    grid-gap:3px
}
.genre .name, .status .name {
    padding:6px 8px;
}
.overview .genre-overview .genre:last-child,
.overview .statuses .status:last-child {
    display:unset;
}
.user > .name{
    padding: 5px;
}
.favourite.studio{
    cursor: pointer;
    min-height: 115px;
    font-size: 15px;
    display: grid;
    grid-gap: 10px;
    padding: 2px!important;
    padding-top: 8px!important;
    background-color: rgba(var(--color-foreground))!important;
    text-align: center;
    align-content: center;
}
.site-theme-dark .favourite.studio{
    background-color: rgb(49,56,68)!important;
}
