    max-width: 1240px  !important;
    width: 100% !important;
}

.featured-container, .main-content-bg {
    padding: 10px;
}

.main-container {
    padding: 0 0;
}

/* MENU and other link color */

.menu-holder {
    display: inherit !important;
}

    /* Global link color */
    a {
        color: rgb(225, 225, 225);
    }

.main-menu-container .main-menu .menu-holder,
.main-menu-container .main-menu .dropdown-content,
