    top: -9px;
    border-color: transparent transparent rgb(45, 45, 45) transparent;
}
.main-menu-container .main-menu .dropdown-content::after {
    border-color: transparent transparent rgba(255, 255, 255, 0.05) transparent;
}
/* Fix for Google Chrome to cover 1px */
.lajkhr-bg-hover:hover {
    z-index: 200;
}
/* Remove forum link */
li.main-bg-hover:nth-of-type(7) > [href="http://www.index.hr/indexforum/"] {
    display: none;
}
/* Currency conversion background color */
li.main-bg-hover:nth-of-type(8) > [href="/info/tecaj"]:hover {
