css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Hyun Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base {
    background: url("https://image.ibb.co/jea9GT/Screenshot_21.png") -15px top no-repeat !important;
    background-size: auto 85px !important;
}
#pet-portrait {
    background: url("https://pre00.deviantart.net/6feb/th/pre/f/2018/146/0/9/hyun__corazon_de_melon_university__by_errorll404ll-dcclo8l.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Hyun" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Hyun te ama cada vez más";
}
#pet-info-racegender:before {
    content: "Humano" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img {
    background: url("https://image.ibb.co/mu3tAo/Screenshot_20.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: " Café Cosy Bear" !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Hyun en tu perfil*/

#player-display-pet {
    background: url("https://image.ibb.co/bNUuGT/515_full.png") no-repeat;
    background-size: auto 100%;
    height: 524px !important;
    width: 194px !important;
}
#player-display-pet > img {
    display: none !important;
}
