css:
/* i really want this to be global */
html.lifting {
    background: none !important;
}
body {
    background: #d5d5d5 !important;
}
#header {
    background: #fff !important;
}
body.lifting #main-nav ul.first-level li a.btn-gray-gradient.ib30-btn-gray-gradient:after {
	display: none;
}
body.lifting #main-nav ul.first-level li a.btn-gray-gradient.ib30-btn-gray-gradient {
	font-family: Helvetica !important;
    background: #1CA146 !important;
    color: #fff !important;
   padding-left: 5px;
    padding-right: 5px;
    border-radius: 2px;
     
    
}
body.lifting #main-nav ul.first-level li a.btn-gray-gradient.ib30-btn-gray-gradient.btn-gray-gradient-muted {
    background: #fff !important;
    color: #444 !important;    
}

body.lifting #main-nav ul.second-level li.active a {
    background: #DB0E16;
    padding-left: 5px;
    padding-right: 5px;
    border-radius: 2px;
    color: #fff;
    margin-bottom:2px;
}
body.lifting #main-nav ul.second-level li.active .underline-decoration {
    display: none !important;
}

body.lifting #main-nav ul.second-level {
	border-bottom: 0px;
}

body.lifting #massistant-app-view #massistant-list .list-view .pfm-list-group.assets>.header, body.lifting #massistant-app-view #massistant-list .list-view .pfm-list-group.liabilities>.header {
	background: #fff;
}
