css:
@-moz-document url-prefix("https://warwick.ac.uk") {
body, .pace-done {
    background-color: #212121;
}
.id7-left-border, .id7-right-border {
    filter: brightness(25%);
    -webkit-filter: brightness(25%);
}
.id7-masthead {
    filter: invert(85%);
    -webkit-filter: invert(85%);
}
.id7-navigation.affix + .id7-navigation-marker {
    background-color: #424242;
}
header {
    background-color: #424242;
}
.navbar-tertiary {
    background-color: #616161;
}
.navbar-tertiary .navbar-nav > li {
    color: white;
}
.navbar-tertiary .navbar-nav > li:hover {
    color: white;
    background-color: #797b7e;
}
.id7-main-content-area {
    background-color: #424242;
}
.id7-main-content {
    background: #424242;
    color: white;
}
.id7-main-content a:link {
    color: white;
    background-color: inherit !important;
}
.id7-main-content a:visited {
    color: #BDBDBD;
    background-color: inherit !important;
}
.id7-main-content a:hover {
    color: #E0E0E0;
    text-decoration-color: currentcolor;
}
main h1, h2, h3, h4, h5, h6 {
    filter: brightness(150%);
    -webkit-filter: brightness(150%);
}
#main-content h1, #main-content a {
    filter: brightness(150%);
    -webkit-filter: brightness(150%);
}
#main-content p {
    color: white;
}
.boxstyle_.box1, .boxstyle_.box2 {
    background-color: #616161;
}
.id7-page-footer.id7-footer-coloured .id7-site-footer {
    color: white;
    background-color: #616161;
}
.id7-page-footer.id7-footer-coloured .id7-site-footert a:link {
    color: white;
}
.id7-page-footer.id7-footer-coloured .id7-site-footert a:visited {
    color: #BDBDBD;
}
.id7-page-footer.id7-footer-coloured .id7-site-footer a:hover {
    color: #E0E0E0;
}
.id7-page-footer.id7-footer-coloured .id7-app-footer .id7-logo-bleed:before, .id7-page-footer.id7-footer-coloured .id7-app-footer .id7-logo-bleed:after {
    border-top-color: #616161;
}
.table {
    background-color: #616161 !important;
}
table.sitebuilder_sortable > tbody > tr:first-child {
    background-color: #616161;
}
table.sitebuilder_sortable th {
    color: white;
}
.odd {
    background-color: #757575 !important;
}
.current-page-count {
    color: white;
}
}

@-moz-document domain("tabula.warwick.ac.uk") {
main h1, h2, h3, h4, h5, h6 {
    color: white;
}
h3 small, h4 small {
    color: #BDBDBD;
}
.id7-left-border, .id7-right-border, .cog {
    filter: invert(85%);
}
.id7-masthead {
    filter: invert(90%);
}
.btn-brand:not(.btn-link) {
    filter: invert(100%)
}
header {
    background-color: #424242;
}
.navbar-tertiary {
    background-color: #616161;
}
.navbar-tertiary .navbar-nav > li {
    color: white;
}
.navbar-tertiary .navbar-nav > li:hover {
    color: white;
    background-color: #797b7e;
}
.id7-main-content {
    background: #424242;
    color: white;
}
.id7-main-content-area {
    background-color: #424242;
}
.calendar {
    background-color: #424242 !important;
}
.fc-state-highlight {
    background-color: #757575 !important;
}
.fc-ltr .fc-event-hori.fc-event-end, .fc-rtl .fc-event-hori.fc-event-start {
    filter: brightness(90%);
}
.popover {
    background-color: #616161;
    color: white;
}
.popover-title {
    background-color: #424242;
}
table.event-info td {
    color: #E0E0E0;
}
table.event-info th {
    color: white;
}
.btn-default {
    color: white !important;
    background-color: #616161;
}
.striped-section {
    background-color: #616161;
}
.module-info .section-title, .striped-section .section-title {
    color: white !important;
}
.module-info .section-title i.fa-chevron-right, .striped-section .section-title i.fa-chevron-right, .module-info .section-title i.fa-chevron-down, .striped-section .section-title i.fa-chevron-down, .module-info .section-title i.fa-refresh, .striped-section .section-title i.fa-refresh {
    color: white !important;
}
.mod-code, .route-code {
    color: white;
}
.mod-name, .route-name, .group-name {
    color: #E0E0E0;
}
.striped-section-contents .item-info:nth-of-type(odd) {
    background-color: inherit;
}
.panel-body {
    background-color: #616161;
}
label {
    color: white;
}
a:link {
    color: white;
}
a:visited {
    color: #BDBDBD;
}
a:hover {
    color: #E0E0E0;
}
.very-subtle {
    color: #E0E0E0;
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #757575;
}
.table {
    background-color: #616161;
}
.table-hover tbody tr:hover {
    background-color: #757575;
}
.well {
    background-color: #616161;
    color: white;
}
.dropdown-menu {
    background-color: #424242 !important;
}
.form-control, .input-group-addon {
    background-color: #616161;
    border: unset;
    color: white;
}
.open > .dropdown-toggle.btn-default, .open > .dropdown-toggle.btn-default:hover, .dropdown-toggle.btn-default:hover {
    background-color: #757575;
}
.typeahead li a div {
    color: white;
}
.dropdown-menu > .active > a, .dropdown-menu > .active > a:hover {
    background: #616161;
}
.fc-state-default {
    background: #616161;
    color: white;
}
.fc-state-active {
    background-color: #757575;
    color: white;
}
.monitoring-points-profile .missed-info, .monitoring-points-profile .attendance-note-info, .fa-check:before {
    color: #66BB6A;
}
.monitoring-points-profile .missed-info .missed, .monitoring-points-profile .attendance-note-info .missed, .seminar-attendance-profile .missed-info .missed {
    color: #F44336;
    text-shadow: 0px 0px 10px rgba(0, 0, 0, 0.75);
}
.fa-close:before, .fa-remove:before, .fa-times:before, .fa-exclamation-triangle:before, .fa-warning:before {
    color: #F44336;
}
.fa-times-circle-o:before {
    color: #00ACC1;
}
.label-success, .progress-bar-success {
    background-color: #43A047;
}
.progress-bar-warning {
    background-color: #F44336;
}
.fa-question-circle:before {
    color: #2196F3
}
}

@-moz-document domain("websignon.warwick.ac.uk") {
body {
    background-color: #212121;
}
.id7-masthead {
    filter: invert(25%);
    -webkit-filter: invert(85%);
}
.id7-main-content-area {
    background-color: #424242;
}
label {
    color: white;
}
.input-group-addon, .form-control, .btn-default {
    background-color: #616161;
    border: 1px solid #757575;
    color: white !important;
}
.form-control:focus {
    border-color: #673677;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(103, 54, 119, 0.6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(103, 54, 119, 0.6);
}
.btn-primary:hover {
    background-color: #673677;
    border-color: #673677;
}
.btn-default:hover {
    background-color: #757575;
    border-color: #757575;
}
li a:link, .id7-horizontal-divider {
    filter: brightness(150%);
    -webkit-filter: brightness(150%);
    text-decoration-color: #5b3069;
}
li a:hover {
    color: #5b3069;
    filter: brightness(200%);
    -webkit-filter: brightness(200%);
    transition: .3s;
}
.id7-page-footer .id7-app-footer a:link {
    color: white;
}
.id7-page-footer .id7-app-footer a:hover {
    text-decoration-color: white;
}
.id7-page-footer .id7-app-footer {
    background-color: #424242;
}
}
