css:
@-moz-document domain("geekweek.pl") {
.news--target .news__link  {
    padding-top: 5%;
    border: 1px solid #e3e3e3!important;
  
}
.news-list .news {
    margin-bottom: 10px;
}

.section--slider {
   /* background: #fff; */
    margin-bottom: -4px;
}

.section--slider .news__figure {
    padding-left: 20%; 
    padding-right: 20%;
}


.news__figure::before {
    width: 100%;
    margin: 0;
    position: relative;
    z-index: 1;
    background:transparent;
   
    opacity: 0;
    transition: opacity .3s cubic-bezier(.4,0,.2,1);
}

.news__figure {
    padding-bottom: 3%;
}
.news-list--3-in-row .news:nth-of-type(3n+1) {
    padding-bottom: 3%;
}
.news-list--3-in-row .news:nth-of-type(2n+1) {
    padding-bottom: 3%;
}
.news-list--3-in-row .news:nth-of-type(2n+1) {
      padding-bottom: 3%;
}

.news-list--2-in-row .news:nth-of-type(2n+1) {
     padding-bottom: 3%;
}

.section {
     padding-left: 10%;
   padding-right: 10%;
}
.news--target .news__figure {
    padding-left: 10%;
   padding-right: 10%;
}

.news--over .news__header {
    position: relative;
}

.news--target .news__img {
       padding-left: 10%;
   padding-right: 10%;
    position: absolute;
    top: 0;
    left: 0;
     background-color: #fff;
    /*
    width: 80%;
    height: 80%;
     */
    object-fit: cover;
   
}

.news--target .news__title {
   margin-top: 0.2%;
    margin-left: 10.6%;
    margin-right: 10.6%;
   padding-left: 4%;
   padding-right: 4%;
}

.news__preview {
    clear: both;
    font-size: .8125em;
}

.news--target .news__preview {
   padding-left: 4%;
   padding-right: 4%;
    margin-left: 10.6%;
    margin-right: 10.6%;
     font-size: 0.64em;
     font-style: italic;
}

.news--target .news__description {
    font-size: 1.05em;
    padding-top: 2.5%;
    padding-bottom: 2%;
   padding-left: 4%;
   padding-right: 4%;
        background-color: #f4f4f4;
    margin-left: 10.6%;
    margin-right: 10.6%;
}

body.plugin {
       margin-left: 10.6%;
    margin-right: 10.6%;
}

.news--target .news__time {
    margin-top:0;
    margin-left: 85px;
}

.news__time {
    margin: 0;
    font-size: 0.4em;
    font-style: italic;
    float: none; 
    color: #b4b4b4;
    background: transparent;
}

.news__label {
    float: right;
    
}

.news--target .news__label {
    margin-right: 0;
}

.tags {
    margin: 1.3em 7.8em 3em 9.1em;
   
}

.news--target .news__figure--vod .news__video-wrapper {
    position: absolute;
    top: 0;
    left: 0;
    width: 83.3%;
  
}
.news__figure.news__figure--vod {
    background-color: #fff;
}
}
