css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mintlinux.ru") {
#header {
    width: 100%;
    height: 60px !important;
    position: relative;
    min-width: 900px;
}

/*отключаем лого в шапке = бесят такие логотипы на пол экрана*/
#header > div:nth-child(2),
#header > div:nth-child(3) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
#header > div:nth-child(3) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3),

/*
tr.posts_table_tr:nth-child(2),
#center > div:nth-child(3) > div:nth-child(2),
*/

/* Второе сообщение в ветке форума-"На заметку",
раньше я его скрывал через стили CSS ,но лучше  подключить скрипт через 
Custom Style Script , Greasemonkey или Tampermonkey   ,
он скроет/удалит все такие посты с рекламой ,т.к. их бывает несколько на одной странице
и через ADBlock Ublock их не убрать

document.querySelectorAll('.post_userlink').forEach(a=>a.innerText=='На заметку'&&a.parentElement.parentElement.parentElement.remove());

*/

#footer,
#center > div:nth-child(4)/*тут тоже ничего полезного */
{
    display: none !important;
}

/*отключаем отступы   правая/левая  панель*/
#main {
    margin: 0 !important;
}
#center > div:nth-child(3) > div:nth-child(1),
#center > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) {
    margin: 0 !important;
}

/***** COPY AND PASTE THE URL OF YOUR BACKROUND-IMAGE INSIDE THE "": *****/
html,#newtab-window     {background: #012 !important}


/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
* {
    color: #999 !important;
    text-shadow: 0 0 3px #000 !important;
    box-shadow: none !important;
    background-color: transparent !important;
    border-color: #444 !important;
    border-top-color: #444 !important;
    border-bottom-color: #444 !important;
    border-left-color: #444 !important;
    border-right-color: #444 !important;
}
body {
    background: transparent !important;
}
*:before,
*:after {
    background-color: transparent !important;
    border-color: #444 !important;
}
a,
a * {
    color: #9ce !important;
    text-decoration: none !important;
}
a:hover,
a:hover *,
a:visited:hover,
a:visited:hover *,
span[onclick]:hover,
div[onclick]:hover,
[role="link"]:hover,
[role="link"]:hover *,
[role="button"]:hover *,
[role="menuitem"]:hover,
[role="menuitem"]:hover *,
.link:hover,
.link:hover * {
    color: #fa0 !important;
    text-shadow: 0 0 5px rgba(255, 255, 200, 0.9) !important;
}
a:visited,
a:visited * {
    color: #7ac !important;
}
a.highlight,
a.highlight *,
a.active,
a.active *,
.selected,
.selected *,
[href="#"] {
    color: #adf !important;
    font-weight: bold !important;
}
h1,
h2,
h3,
h4,
h5,
h6,
h1 *,
h2 *,
h3 *,
strong,
[id*="headline"],
[class*="headline"],
[id*="header"],
[class*="header"],
[class*="header"] td {
    color: #DDD !important;
}
a h1,
a h2,
a h3,
a h4,
a h5,
a h6,
h1 a,
h2 a,
h3 a,
a strong,
a[id*="headline"],
a[class*="headline"],
a[id*="header"],
a[class*="header"] {
    text-decoration: underline !important;
}
[class*="error"],
[class*="alert"],
code,
span[onclick],
div[onclick] {
    color: #900 !important;
}
 ::-moz-selection {
    background: #ccc6 !important;
    color: #000 !important;
}
 ::selection {
    background: #ccc6 !important;
    color: #000 !important;
}
 :focus {
    outline: none !important;
}

/*----- MENU & CO BACKGROUND-COLORS -----*/
div[style="display: block;"],
div[role="navigation"] {
    background: rgba(0, 0, 0, .5) !important;
}
table {
    background: rgba(40, 30, 30, .6) !important;
    border-radius: 6px !important;
}
table > tbody > tr:nth-child(even),
table > tbody > tr > td:nth-child(even) {
    background-color: rgba(0, 0, 0, .2) !important;
}
iframe,
embed,
header,
nav,
footer,
label [onclick],
nav ul,
div[style*="position:"][style*="left:"][style*="visible"],
div[style*="z-index:"][style*="left:"][style*="visible"],
div[style*="-moz-user-select"],
div[role="menu"],
div[role="dialog"],
span[class*="script"] div,
[id*="menu"],
[class*="dropdown"],
[class*="popup"],
[class="title"],
ul[style*="display:"],
ul[style*="visibility:"] ul,
[id*="nav"] ul,
[class*="nav"] ul,
ul[class*="menu"],
a[onclick][style*="display"],
a[id*="ghosteryfirefox"],
#ghostery-purple-bubble,
#translator-popup,
.menu,
.tooltip,
.hovercard,
.vbmenu_popup {
    background: rgba(5, 5, 5, .9) !important;
    border-radius: 5px;
    box-shadow: 1px 1px 5px #000 !important;
}
[id*="overlay"],
[id*="lightbox"],
blockquote {
    background-color: rgba(35, 35, 35, .9) !important;
    border-radius: 5px;
}
pre,
dl,
.Message code {
    background-color: rgba(5, 5, 5, .5) !important;
}

/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input,
select,
button,
[role="button"],
a.button,
a.submit,
a.BigButton,
a.TabLink,
.install[onclick] {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    transition: border-color 0.3s !important;
    background: #060606 !important;
    color: #BBB !important;
    text-shadow: 0 1px #000 !important;
    border: 2px solid #333 !important;
    border-radius: 4px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, .9) !important;
}
a[href="javascript:;"],
a[class*="button"]:not(:empty),
a[id*="button"]:not(:empty),
a[id*="Button"]:not(:empty),
div[class*="button"][onclick] {
    transition: border-color 0.3s !important;
    background: #060606 !important;
    color: #BBB !important;
    text-shadow: 0 1px #000 !important;
    border-color: #333 !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, .9) !important;
}
a[href="javascript:;"]:hover,
a[class*="button"]:not(:empty):hover,
a[id*="button"]:hover,
a[id*="Button"]:not(:empty):hover,
div[class*="button"][onclick]:hover {
    background: #151515 !important;
    color: #fa0 !important;
}
input *,
select *,
button *,
a.button *,
a.submit * {
    color: #BBB !important;
    text-shadow: none !important;
}
input:hover,
input[type="button"]:hover,
input[type="checkbox"]:hover,
input[type="radio"]:hover,
select:hover,
button:hover,
[role="button"]:hover,
a.button:hover,
a.submit:hover,
a.BigButton:hover,
a.TabLink:hover {
    border: 2px solid #555 !important;
    border-top-color: #555 !important;
    border-bottom-color: #555 !important;
    border-left-color: #555 !important;
    border-right-color: #555 !important;
}
input:focus,
select:focus {
    box-shadow: 0 0 5px #077 !important;
}
input *:hover * {
    color: #F0F0F0 !important;
    text-shadow: 0 0 2px #FFF !important;
}
input[type="checkbox"],
input[type="checkbox"]:focus {
    border: 2px solid #333 !important;
    background-color: #111 !important;
    color: #DDD !important;
    border-radius: 4px !important;
}
input[type="radio"],
input[type="radio"]:focus {
    border: 2px solid #333 !important;
    background-color: #111 !important;
    color: #DDD !important;
}
input[type="checkbox"],
input[type="radio"] {
    min-width: 10px;
    min-height: 10px;
}
select input[type="button"],
select button {
    border: none !important;
}
select button {
    min-height: 8px !important;
}
button:active,
input[type="submit"]:active,
input[type="button"]:active,
a.button:active,
a[class*="button"]:not(:empty):active,
a.submit:active,
a.BigButton:active,
a.TabLink:active,
.Active .TabLink {
    background: #292929 !important;
    color: #FFF !important;
}
textarea {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background: rgba(0, 0, 0, .3) !important;
    border-radius: 3px !important;
    border: 1px solid #000 !important;
    box-shadow: inset 0 0 8px #000 !important;
    transition: border-color, background, 0.3s !important;
}
textarea,
textarea * {
    color: #C8C8C8 !important;
    text-shadow: 0 0 1px gray !important;
}
textarea:hover,
textarea:focus:hover {
    border-color: #333 !important;
}
textarea:focus {
    background: rgba(0, 0, 0, .5) !important;
    border-color: #222 !important;
}
textarea:focus,
textarea:focus > * {
    text-shadow: none !important;
    box-shadow: none !important;
}
option {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background: none !important;
    color: #666 !important;
}
option:not([disabled]):hover,
option:focus,
option:checked {
    background: linear-gradient(#333, #292929) !important;
    color: #DDD !important;
}

/* webkit checkbox & select fix */

/*----- SCROLLBAR CHANGES -----*/
scrollbarbutton {
    -moz-appearance: none !important;
    background-color: transparent;
    border: none !important;
}
thumb {
    -moz-appearance: none !important;
    border: 2px solid transparent !important;
    background: #999 !important;
    background-clip: padding-box !important;
    border-radius: 3px !important;
    box-shadow: inset 0 0 3px #000 !important;
}
scrollbar {
    -moz-appearance: none !important;
    background: transparent !important;
    box-shadow: inset #000 0 0 8px !important;
}
scrollbarbutton:hover,
thumb:active,
thumb:hover {
    opacity: 0.5 !important;
}
scrollbar[orient="vertical"] thumb {
    min-width: 12px !important;
    max-width: 12px !important;
    min-height: 14px !important;
}
scrollbar[orient="horizontal"] thumb {
    min-height: 12px !important;
    max-height: 12px !important;
    min-width: 14px !important;
}
scrollbar[orient="vertical"] {
    min-width: 12px !important;
    max-width: 12px !important;
}
scrollbar[orient="horizontal"] {
    min-height: 12px !important;
    max-height: 12px !important;
}

/*----- IMAGE CHANGES -----*/
body,
*:not(:empty):not(html):not(span):not(a):not(b):not(option):not(img):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([class*="icon"]):not(.text):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(#globalsearch):not(.sp),
.r3_hm,
.gmbutton2 b,
.gtab-i,
.ph,
.bstab-iLft,
.csb,
#pagination div,
[style*="sprite2.png"],
#mw-head-base,
#mw-page-base {
    background-image: none !important;
}
img {
    opacity: .7 !important;
    transition: opacity .2s;
}
img:hover,
a:hover img {
    opacity: 1 !important;
}

/*----- ABOUT PAGES BACKGROUND -----*/
#newtab-window,
#addons-page {
    background: #151515 !important;
}
* {
    background-color: transparent;
}
body > * {
    background-color: rgba(0, 0, 0, .5) !important;
}
#list-view {
    background: #111 !important;
}
#detail-view {
    background: #222 !important;
}
#addons-page > * > *,
#addons-page > * > * > * {
    border-color: #333 !important;
}

/*----- RIGHT-CLICK MENUS & TOOLTIP STYLE -----*/
#contentAreaContextMenu menuitem[disabled="true"],
#contentAreaContextMenu menuitem[disabled="true"] menuseparator {
    display: none !important;
}
menupopup,
popup,
popup > menu > menupopup,
menupopup > menu > menupopup {
    -moz-appearance: none !important;
    background: rgba(15, 15, 15, .9) !important;
    border: 1px solid #000 !important;
    border-radius: 3px !important;
    box-shadow: inset 0 0 1px #777, inset 0 0 10px #000 !important;
    padding: 2px 0 !important;
}
menupopup * {
    box-shadow: none !important;
    color: #777 !important;
}
menupopup menuitem:hover * {
    color: #F0F0F0 !important;
    text-shadow: 0 0 5px rgba(255, 255, 200, 0.9) !important;
}
menupopup menuseparator {
    -moz-appearance: none !important;
    background: transparent !important;
    margin: 2px 0 2px -3px !important;
    padding: 0 !important;
    border-top: none !important;
    border-color: transparent !important;
    box-shadow: 0 0 3px #666 !important;
}
menupopup menuitem {
    -moz-appearance: none !important;
    background: transparent !important;
    text-shadow: 0 1px #000 !important;
}
menupopup menuitem:hover,
menuitem[_moz-menuactive="true"] {
    background: #333 !important;
}
tooltip {
    -moz-appearance: none !important;
    color: #CCC !important;
    background: rgba(0, 0, 0, .8) !important;
    border: 1px solid #555 !important;
    border-radius: 3px !important;
}

/*----- FIX WHITE NEW TAB FLASH -----*/
browser[type="content-primary"] {
    background: #151515 !important;
}
}
