css:
@-moz-document url("https://play.google.com/books"), url("https://play.google.com/books/audiobooks") {

/*
Author: TonyStr
Website: https://TonyStr.net
*/

body,
div[role="banner"] div, 
div[role="navigation"] div,
.nav{
    background-color:#1E2026;
    color:#ffffff;
}

.sub-nav-option a{
    color:#ffffff;
}

.nav .books .selected .hover-target{
    background-color:transparent;
}

.action-bar{
    border-bottom:4px solid #2D313D;
}
.action-bar-link,
.action-bar-link:visited{
    color:#ffffff;
}
.action-bar-link:hover{
 	background-color:#2D313D;
}
.action-bar-link.chosen{
 	border-color:#F14165;   
}

.nav{
 	box-shadow:none;   
}

.card-content{
    box-shadow:none;
 	background-color:transparent;   
}

.card-content a{
    color:#ababab;
}
.card-content .title{
    color:#ffffff;
}
.card-content .cover{
    background-color: #000000;
 	box-shadow:0 0 8px #000000;   
}

.gb_me .gb_1a{
    content:url(https://TonyStr.net/img/play_logo_white.png);
}

.gb_Tc.gb_Uc.gb_hc,
.empty-cluster{
 	background-color:#2D313D;
    box-shadow:none;
}
.empty-cluster h2{
    color: #ffffff;    
}

.play-button{
    border: 1px solid #F14165;
}

.play-button img{
    filter:invert(100%);
}

a[role="button"]{
    filter:invert(100%);
}

.gbqfqwb input{
    color:#ffffff;
}

button.gbqfb, #gbqfb,
.play-button.books{
 	background:#F14165;   
}
}

@-moz-document url-prefix("https://books.googleusercontent.com/books/reader") {

/*
Author: TonyStr
Website: https://TonyStr.net
*/

body,
.gb-pagecontrol-input,
.gb-tab{
 	color:#ffffff;
}

.gb-reader-container,
.gb-topbar,
.gb-statusbar,
.gb-layer-popup,
.gb-page-wrapper-body{
 	background-color:#1E2026 !important;
}

.gb-statusbar,
.gb-topbar{
 	opacity:0;
    transition:.1s;
}

.gb-statusbar:hover,
.gb-topbar:hover{
 	opacity:1;
}

.gb-slider-indicator{
   	background-image:none;
    background-color:#a6a7aa;
    border-radius:50%;
}

.gb-slider-content>div{
 	background-color:#44454b;   
}

.gb-topbar-controls-cell div div span{
    
    filter:invert(100%);
    -webkit-filter:invert(100%);
}

.gb-reader-settings-button>div{
 	filter:brightness(90%);
}

/* menus */
.gb-layer-popup,
.gb-slider-tooltip-body,
.gb-sidebar-popup-spacer>div,
.gb-sidepanel{
    box-shadow:none;
    border: 1px solid #7f7f7f;
 	background-color:#1E2026 !important;
}

.ocean-sliced-element{
    font-family: Arial, "Helvetica Neue", Helvetica, sans-serif !important;   
}

.gb-reader-settings-menu-item{
 	background-color:#1E2026;
    border:none;
}

.goog-menu.goog-menu-vertical.gb-reader-settings-menu{
    border:none;
    border:1px solid #2D313D;
}

.figure_heading{
    box-shadow:0 1px 6px rgba(0, 0, 0, .3);
}
.gb-reader-container img{
    filter:invert(100%);
    -webkit-filter:invert(100%);
}
.gb-reader-container img:hover{
    filter:none;
    -webkit-filter:none;
}
}
