css:
@-moz-document domain("chat.google.com") {

:root {
    --base04: rgba(0, 23, 29, 1);
    --base03: rgba(0, 43, 54, 1);
    --base02: rgba(7, 54, 66, 1);
    --base01: rgba(88, 110, 117, 1);
    --base00: rgba(101, 123, 131, 1);
    --base0: rgba(131, 148, 150, 1);
    --base1: rgba(147, 161, 161, 1);
    --base2: rgba(238, 232, 213, 1);
    --base3: rgba(253, 246, 227, 1);
    --yellow: rgba(181, 137, 0, 1);
    --orange: rgba(203, 75, 22, 1);
    --red: rgba(220, 50, 47, 1);
    --magenta: rgba(211, 54, 130, 1);
    --violet: rgba(108, 113, 196, 1);
    --blue: rgba(38, 139, 210, 1);
    --cyan: rgba(42, 161, 152, 1);
    --green: rgba(133, 153, 0, 1);
}

body {
    background-color: var(--base04);
    color: var(--base2);
}

/* Loaging Screen */
.bzJiD:not(.eO2Zfd):before {
    background-color: var(--base01);
    opacity: 0.96;
}

/* Header Bar */
.DWzK6d {
    background-color: var(--base02);
}

/* Chat Logo */
#XMLID_8_-Clipped {
    fill: var(--base3) !important;
    opacity: 0.7
}

/* Group Name */
.dKv0S .i5PBGe {
    color: var(--base2);
    opacity: 0.7
}
.i5PBGe.iWO5td,
.i5PBGe:active,
.i5PBGe:focus,
.i5PBGe:hover {
    background-color: var(--base01);
    color: var(--base3);
}
.xzTN8b {
    background-color: var(--base02);
}
.i5PBGe:hover .xzTN8b,
.i5PBGe:focus .xzTN8b,
.i5PBGe.u3bW4e .xzTN8b,
.i5PBGe.iWO5td .xzTN8b,
.S6YNQd {
    color: var(--base3);
    fill: var(--base3);
}
.UZl8cf {
    color: var(--base3);
}

/* Switch Group Dropdown */
.JPdR6b {
    background-color: var(--base02);
}

/* Groups */
.z80M1 {
    color: var(--base3);
}

/* Groups Hovered */
.z80M1.FwR7Pc {
    background-color: var(--base01);
}

/* Group Icons */
.o50UJf {
    fill: var(--cyan);
}

/* Search/Gear Icons */
.Nj8aCb:hover,
.Nj8aCb:focus {
    color: var(--base2);
    fill: var(--base2);
    background-color: var(--base01);
}

/* Feedback Wrapper */
.TaudIc {
    color: var(--base0);
    fill: var(--base0);
}

/* Left Nav Bar Wrapper */
.X9KLPc {
    background-color: var(--base03);
    border-right-color: var(--base1);
}

/* Find People Hover */
.GbZFNe:focus,
.GbZFNe:hover {
    background-color: var(--base02);
}

/* Group Label */
.t5F5nf {
    color: var(--base1);
}

/* Group Label w/ New Message */
.PL5Wwe.H7du2 .t5F5nf {
    color: var(--base3);
}

/* Group */
.PL5Wwe:focus,
.PL5Wwe:hover:not(.tuKyod),
.tuKyod:hover .LoYJxb {
    background-color: var(--base02);
}

/* Selected Group */
.PL5Wwe.KKjvXb:not(.tuKyod),
.PL5Wwe.qs41qe:not(.tuKyod) {
    background-color: var(--base01);
}
.PL5Wwe.KKjvXb .t5F5nf,
.PL5Wwe.KKjvXb .sFuxxd,
.PL5Wwe.qs41qe .t5F5nf,
.PL5Wwe.qs41qe .sFuxxd,
.GSEpfc .t5F5nf {
    color: var(--base3);
}

/* Keyboard shortcut tooltip */
.RM9ulf {
    color: var(--base03);
}
.R8qYlc {
    background-color: var(--base3);
}

/* Left Add Person Bar */
.d6pS5.i6oIBe .W2x1cc {
    background: var(--base02);
}
.D3DXDc {
    fill: var(--base2);
}
.Uk0Bfe .snByac {
    color: var(--base2);
}
.yoV6yd {
    background: var(--base03);
}
.tOToeb {
    border-bottom-color: var(--base2);
}
.dQ2Tsf .NXfF8b,
.dQ2Tsf .ZTmjQb {
    color: var(--base2);
}
.jAwRLb {
    fill: var(--cyan);
}

/* Main Section */
.WQKmIb .Bl2pUd,
.Bl2pUd {
    background-color: var(--base04);
}
.A2BXPe {
    color: var(--base2);
}
.ZlQIRd,
.ZGAbn {
    fill: var(--cyan);
}
.pihjkd {
    color: var(--cyan);
}

/* Room Header */
.tCUB4c {
    color: var(--base3);
}

/* Room Created Note */
.qnDW3d {
    color: var(--base1);
}

/* Conversations */
.dsoUjb {
    background-color: var(--base03);
}

/* Convos in Groups */
.WQKmIb .Bl2pUd .dsoUjb {
    background-color: var(--base04);
}
.XbbXmb.zzVqCe .nF6pT,
.jGyvbd .jO0Dzb {
    background-color: transparent;
}
.oGsu4,
.zPVzl {
    background-color: transparent;
}
.cmEq8b .Zc1Emd,
.cmEq8b .Z4BnXb,
.zPVzl {
    color: var(--base2);
}
.nF6pT.FwR7Pc .Hj5Fxb,
.hu21Y:hover {
    background-color: var(--base03);
}

/* Message Group Icons */
.orLAid {
    fill: var(--base00);
    color: var(--base00);
}
.orLAid:hover,
.orLAid.u3bW4e {
    fill: rgba(255, 255, 255, 0.54);
    color: rgba(255, 255, 255, 0.54);
}
.nF6pT.FwR7Pc .orLAid,
.hu21Y:hover .orLAid,
.orLAid:focus {}

/* Code Blocks */
.FMTudf {
    background-color: var(--base02);
    color: var(--base2);
    border-color: var(--base0);
}

/* Notification Icon */
.otWEZc {
    fill: var(--red);
}

/* New Message Box */
.bzJiD .IEIJqd.qs41qe .XganBc,
.IEIJqd .I0LFzc {
    background-color: var(--base03);
}
.BScnzc {
    color: var(--base3);
}
.XweIZc {
    color: var(--base1);
}

/* Reply Box */
.vmpzOc.eO2Zfd {
    background-color: var(--base03);
}

/* History Tab */
.IEIJqd.Myox3c .D6C1Qe {
    background-color: var(--base03);
    display: none;
}

/* Activer / Desactiver l'historique + Visioconférence */
.LsSwGf.wmbLw,
.mUbCce.fKz7Od.giBSw {
    display: none;
}

/**/
.lljjAe {
    color: var(--cyan);
    display: none;
}

/**/
.lljjAe.X0MG1d {
    color: var(--green);
}

/* Applique la même couleur de fond pour la div sur notre photo */
.gb_jb.gb_bd.gb_Pg.gb_R.gb_Mb {
 background: var(--base02)!important; 
}

/* Masque la div "Historique activé" */
.deR9Se, .IFk8Lb {
	display: none!important;
}

/* Menu du haut */
.QTQg5e {
 background: inherit!important;
}

span.nk7hB, .aOHsTc, .t5F5nf {
 color: inherit!important;
}

/* div qui englobe le chat */
.byY7Yb.cFc9ae, .XganBc {
 background: var(--base04)!important;
}

div.EFNTcf svg {
 fill: #5f6368!important;
}

div.IEIJqd.vbJQj.Myox3c.qs41qe {
 padding-top: 0!important;
}

.I0LFzc {
 background: transparent!important;
}

.dJ9vNe {
 background: var(--base03)!important;
}

.cAW0Vc {
 color: inherit!important;
}

}
