css:
@-moz-document domain("vk.com") {
#l_pr, #l_nwsf, #l_msg, #l_fr, #l_gr, #l_mk, #l_doc,
.audio_friends_list_wrap._audio_friends_list_wrap, .audio_page__shuffle_all, #chat_onl_wrap,
.audio_page_player_btn.audio_page_player_status._audio_page_player_status,
.audio_page_player_btn.audio_page_player_recoms,
.audio_page_player_btn.audio_page_player_shuffle._audio_page_player_shuffle,
._audio_section_tab__updates, ._audio_section_tab__recoms,
.left_menu_nav_wrap, .more_div.l_main
{
	display:none !important;
}
#page_body, #side_bar_inner {
	margin-top: 0 !important;
}

.audio_page__rows_wrap {
	width:100%!important;
}
.audio_page_player._audio_page_player._audio_row.clear_fix.audio_page_player_fixed.audio_page_player_fixed_shown {
	height: 54px !important;
}
}

@-moz-document domain("vk.com") {
#top_nav .head_nav_item {
	display: none !important;
}
.head_nav_item_player {
    display: block !important;
    width: 300px !important;
    position: absolute;
    top: 0;
    left: 45%;
    margin: auto;
}

#page_layout {
	padding-top: 30px;
}
}
