css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/"){
  
  
body {
   background:url(https://i0.wp.com/www.solidcolore.com/3840/ffd2e8_background.jpg?ssl=1)no-repeat fixed  !important;
   background-color: #ffd2e8 !important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

  
  


div.l-content.l-content.l-content {  
   font-family: Arial; color: #ff85c1;
   background: #ffd2e8 !important;

 }



/*header's background*/
.l-header-container {
background:transparent!important;
box-shadow:none!important;
}
#Compose-button {
border:none!important;
fill:#ffd2e8!important;
color:#fff!important;
}



  
.notification  {
  	background: #ffd2e8 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #ffffff !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: #ffd2e8 !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: #ffd2e8 !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important;
background:transparent!important; 
}


.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Followers" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(209, 141, 154, 0.25) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}

/*posts text color*/
#left_column .post_header *:not(:hover),
#left_column .post_body, 
#left_column .post_body *,
#left_column .post_tags *,
#left_column .post-source-footer *,
#left_column .note_link_current,
.posts_view_masonry *,
#toaster *,
.new_post_label_text,
.info-container *,
.post_title,
.notification_pane *,
.settings_group_wrapper *,
#left_column .reblog-list *,
#new_post_buttons .post-header *,
#new_post_buttons .post-content *,
#new_post_buttons .post-form--tag-editor *,
.radar_controls .note_link_current {
;
}

.note_item, .note_item .name, strong {
color:grey!important;
}

/*right column text color*/
li.section_header,
a.currently_selected_blog,
a.open_blog_link,
a.posts div.hide_overflow,
a.followers div.hide_overflow,
a.activity div.hide_overflow,
a.queue div.hide_overflow,
a.customize div.hide_overflow,
a.likes div.hide_overflow,
a.following div.hide_overflow,
a.spotlight div.hide_overflow,
a.drafts div.hide_overflow,
a.has_follow_button span, a.has_follow_button small,
span.count,
#right_column i,
a.controls_item_anchor .hide_overflow,
.radar_tumblelog_label .name,
.icon_star_12,
.radar_tumblelog_label .subheading,
.small_links a,
.messages, .delete_all_messages, .no_push .message,
right_column .tag,.track_tag,
.search_control_items span, .search_control_items i,
span.follow_icon, a.icon_plus,
#dashboard_controls a,
#dashboard_controls .control,
.dashboard_controls_arrow_right,
.dashboard_controls_arrow_left,
.search_query,
.sidebar_link,
.notification *,
h1.discover-section-title {
color: #ffffff !important;
opacity:1 !important;
}

/* .identity, .identity-refresh ,*/ 
  .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .l-header-container--refresh, .identity-refresh .controls_section li:not(.section_header):hover, .identity-refresh .post_avatar, .collection_carousel .blogs_wrapper_carousel:before, .collection_carousel .blogs_wrapper_carousel:after, .discover-controls-wrapper, .discover .blog_row:hover, #search_actions_search .elevator, #discover_actions_discover .elevator, .identity .controls_section li:not(.section_header):not(.selected_blog):hover, .rapid-recs-container .rapid-recs {
  background: transparent;
}
    
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after, .identity-refresh .radar .radar_footer .radar_avatar:before, .discover .blog_row .info:before, .discover .blog_row:hover .info:before {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_full .post_permalink, .identity-refresh .post_full .post_permalink:not(.no-transition--dogear) {
  border-color: #ccc;
  background: transparent;
}

.identity-refresh .post_avatar .post_avatar_link, #toaster .toast .toast_border
{
  background-color: transparent !important;
}

.dashboard_options_form, .tag_psa {
  border-color: transparent;
}


.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {
  color: #ff85c1;
  border-color: #ff85c1;
}

.plus-follow-button:hover, .identity-refresh .item:hover .follow:hover, .identity-refresh .search_results_container .tumblelog_mask_item, #pagination a, #pagination a:hover {
  background-color:#ff85c1;
  border-color: #ff85c1;
}

.compose-button, .compose-button:active, .compose-button:focus {
  background-color:#fff;
  
}

#dashboard_controls a, #dashboard_controls .control {
  color: #aaa;
  text-shadow: #333 1px 1px 1px;
}

.dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.time {
  color: #aaa;
}

#dashboard_controls a:hover, #dashboard_controls .control:hover, .dashboard_options_form, .tag_psa, .post_full .post_avatar .queue .publish_info.day {
  color: #ccc;
}

.identity-refresh .tab_notice {
  background-color: #fff;
opacity: 0.8;
}
}
