css:
@-moz-document url("https://evolution-host.com/controlPanel/") {

body {
    background: #131313;
    font-family: Arial;
    font-size: 11pt;
    font-weight: normal;
    color: #131313;
    margin: 0;
}

element.style {
}
#client_center_enc {
    width: 1000px;
    height: 500px;
    display: table;
    background: #131313;
}
user agent stylesheet
div {
    display: block;
}

element.style {
    margin-top: 30px;
}

.page_title_text {
    height: 70px;
    line-height: 70px;
    font-family: Arial;
    font-size: 16pt;
    color: #826ada;
    float: left;
}
user agent stylesheet
div {
    display: block;
}

.cfg_table {
    text-align: left;
    margin-top: 10px;
    font-size: 10pt;
    color: #826ada;
}


element.style {
}

.box_content {
    width: 100%;
    height: 200px;
    background: #131313;
    padding: 10px;
    display: table;
    border: 1px solid #CCC;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}
user agent stylesheet
div {
    display: block;
}

#homeic_boxes_client {
    height: 125px;
    margin-bottom: 10px;
    display: table;
    color: #826ada;
    text-align: left;
}

element.style {
}
#client_center_def {
    width: 100%;
    height: 110px;
    background: #131313;
    border-radius: 8px;
    box-shadow: 0 2px 2px #CCC;
    padding-left: 10px;
}
user agent stylesheet
div {
    display: block;
}

.homeic_box_client {
    width: 95px;
    height: 80px;
    background: #131313;
    padding: 10px;
    text-align: center;
    font-weight: bold;
    font-size: 10pt;
    cursor: pointer;
    float: left;
    margin-top: 3px;
    margin-right: 10px;
    margin-bottom: 10px;
    border-radius: 8px;
    box-shadow: 0 2px 4px #826ada;
}
user agent stylesheet
div {
    display: block;
}

element.style {
}

.box_title {
    width: 100%;
    height: 25px;
    line-height: 25px;
    padding: 11px;
    text-indent: 20px;
    font-weight: bold;
    color: #826ada;
    display: table;
    background: #f1f1f1;
    background: -webkit-gradient(linear,left top,left bottom,from(#505050),to(#000000));
    background-image: -moz-linear-gradient(top,#f1f1f1,#CCC);
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}
user agent stylesheet
div {
    display: block;
}

element.style {
}
.button {
    width: 200px;
    height: 35px;
    line-height: 35px;
    text-align: center;
    color: #826ada;
    font-family: Arial;
    font-size: 10pt;
    font-weight: bold;
    margin-top: 15px;
    margin-bottom: 10px;
    cursor: pointer;
    background: #4a4a4a;
    background: -webkit-gradient(linear,left top,left bottom,from(#505050),to(#000000));
    background-image: -moz-linear-gradient(top,#CCC,#999);
    border-radius: 6px;
    border-color: #826ada;
    box-shadow: 1px 2px 5px #826ada;
}

#panel_center {
    width: 980px;
    float: left;
    margin-left: 10px;
    font-size: 13pt;
    color: #826ada;
    display: table;
    padding-bottom: 100px;
}

element.style {
}
.box_table td {
    height: 25px;
    border-bottom: 1px solid #504186;
}

element.style {
}
#panel_top_client {
    width: 100%;
    height: 50px;
    line-height: 50px;
    background: #000000;
    font-size: 18pt;
    text-align: center;
    color: #826ada;
    border-bottom: 1px solid #584796;
    margin-bottom: 5px;
}
user agent stylesheet
div {
    display: block;
}



.tab {
    width: 180px;
    height: 45px;
    line-height: 45px;
    text-indent: 10px;
    float: left;
    margin-left: 5px;
    cursor: pointer;
    font-family: Arial;
    font-size: 11pt;
    font-weight: bold;
    color: #826ada;
    display: table-cell;
    background: #CCC;
    background: -webkit-gradient(linear,left top,left bottom,from(#505050),to(#000000));
    background-image: -moz-linear-gradient(top,#CCC,#999);
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    box-shadow: 2px 5px 10px #826ada;
}

.tab:hover {
    background: #306eff;
}

textarea {
    -webkit-appearance: textarea;
    height: 800px;
    background-color: #232323;
    color: #826ada;
    -webkit-rtl-ordering: logical;
    flex-direction: column;
    resize: auto;
    cursor: text;
    white-space: pre-wrap;
    word-wrap: break-word;
    border-width: 1px;
    border-style: solid;
    border-color: initial;
    border-image: initial;
    padding: 2px;
}

.srv_table_ld_td:hover {
    background: #3b315f;
}
.inputs:read-write {
      background: #232323;
    font-size: 9pt;
    font-weight: bold;
    color: #826ada;
}
.inputs:hover {
    background: #232323;
    font-size: 9pt;
    font-weight: bold;
}

.inputs {
    width: 250px;
    height: 35px;
    line-height: 35px;
    text-indent: 5px;
    background: #232323;
    font-family: Arial;
    font-size: 10pt;
    color: #bdabff;
    border: 1px solid #826ada !important;
    border-radius: 6px;
}

.links {
    color: #4f84ff;
    font-family: Arial;
    font-size: 11pt;
    font-weight: bold;
    cursor: pointer;
    text-decoration: none;
}
#srv_outputbox
{
    width: 800px!important;
    height: 900px!important;
    border: 1px solid #4c3c86!important;
    border-top-color: rgb(76, 60, 134);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(76, 60, 134);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(76, 60, 134);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(76, 60, 134);
    border-left-style: solid;
    border-left-width: 1px;
    border-image-source: initial;
    border-image-slice: initial;
    border-image-width: initial;
    border-image-outset: initial;
    border-image-repeat: initial;
    font-family: Monospace,Verdana,Arial;
    font-size: 10pt;
    font-weight: normal;
    border-radius: 6px;
    padding: 2px;
    margin-top: 10px;
    margin-bottom: 5px;
}
#footer
{
    -webkit-box-shadow: 0px -2px 18px rgba(50, 50, 50, 1);
    -moz-box-shadow: 0px -2px 18px rgba(50, 50, 50, 1);
    -o-box-shadow: 0px -2px 18px rgba(50, 50, 50, 1);
    box-shadow: 0px -2px 18px rgba(50, 50, 50, 1);
    
    left: 0px;
    bottom: 0px !important;
    height: 0px;
    width: 0% !important;
    background: #000000 !important;
    z-index: 3;
}

.box_content {
    width: 100%;
    height: 200px;
    background: #131313;
    padding: 10px;
    display: table;
    border: 1px solid #826ada;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

#client_center_def {
    width: 100%;
    height: 110px;
    background: #131313;
    border-radius: 8px;
    box-shadow: 0 2px 2px #826ada;
    padding-left: 10px;
}
}
