css:
@-moz-document regexp("https?://www\\.sololearn\\.com(?!//.../#../).*") {
html, body {
     background: #333!important;
}
 .materialInput input, h1, h2, span, h1 a, h1, .recentArticles ul li a, .archives ul li a, .articleContent p, .courseItem a:hover .courseDescription div, .courseItem a:hover .courseDescription h1, .codeBlock .tooltip, .noteBlock .tooltip, #replies .answer .postDetails .message, #replies .answer .postDetails .message, .postDetails .message, .takeCourse p, .courseReviewTitle {
     color: #eee!important;
}
#leaderboard .leaderboardList a {
    color: #abf;
}


#header {
    background-color: gray;
}


 #output,
#leaderboard .rank {
     color: #ddd;
}
#quizSelector {
    color: #c2c2c2;
}
 #textView #textContent, .userAchievements .achievement .details {
     color: #C2C2C2!important;
}
 .noCode .description {
     color: #C2C2C2!important;
}
 .userCourses .courseXp {
     color: #eee!important;
}
 .userAchievements .achievement .title {
     color: #eee!important;
}
 .questionTabs .tabs li a {
     color: #eee!important;
}
 .questionTabs .tabs li a:hover {
     background-color: #666!important;
    ;
}
#comments .commentContainer {
    background: #414141;
}
#comments .comment {
    border-left: solid 3px #2297b7;
    border-bottom: solid 1px #333333;
    border-top: solid 1px #8e8e8e;
}

#comments .comment .name {
    color: #ddd;
}
#comments .comment .text {
      color: #aaa;
}
 select {
     background: #222!important;
}
 .materialButton.primary.elevated.addQuestion {
     background-color: #444!important;
}
.placeholderDragAndDropQuiz .answers {
    margin-top: 20px;
    background: #4d4d4d;
    box-shadow: 0 1px 3px rgba(0,0,0,.12), 0 1px 2px rgba(0,0,0,.24);
}


 .questionTabs .tabs li.active a {
     color: #1abc9c;
     background-color: #5f5f5f;
     border: solid 1px #ff8282!important;
}
 .authorDetails .date {
     font-size: 12px;
     color: #B1B1B1;
}
 #textView .noteBlock {
     border: solid 1px #6a5a5a!important;
     background: #5d514c!important;
}
#textView .note {
    border-left: 5px solid #c9c54c;
}


 .courseDescription p {
     color: #b9b9ba!important;
}
 .courseDescription div, .courseDescription h1 {
     color: #C9D6F5!important;
}
 .postDetails .title a {
     color: #E8E4E4!important;
}
 .postVotes, .postAnsewers {
     color: #FC6565!important;
}

.block {
    background-color: #414141 !important;
}
 .codeTabs .tabs li a:hover {
     background-color: #757575!important;
}
 .codeTabs .tabs li.active a {
     color: #9ca4a2!important;
     background-color: #404040!important;
     border: 1px solid #ff6e6e;
}
 #app, #textView .textContentContainer {
     background-color: #414141!important;
}
 .courseModule, .lessonVideo, .lessonChecks {
     background-color: #000!important;
}
 .shortcutContent .shortcut {
     background-color: #eb6654!important;
     color: #ddd!important;
}
 #quizView .quizQuestion {
     color: #DBD6D6!important;
}

.multipleChoiceQuiz .answer, .strikeOutQuiz .answer {
    border-top: solid 1px #484848 !important;
    background: #323335 !important;
}

#textView .codeBlock {

    border: solid 1px #444 !important;
    background: #353535 !important;

}
/*
 .navigation-menu ul li a, .slider a, .courseStores a {
     background-color: #0c6855!important;
}
*/
 .appLesson.disabled {
     background: #3b3b3b !important;
}
#footer {
    background-color: #5d5d5d !important;
}
#footer .copyright {
    color: #c3c3c3;
}
#footer .about p {
    color: #dedede;
}











/*
#socialElements, .reorderQuiz .answer, .appLesson, .footerSocial {
    background-color: #b4b4b4 !important;
}
*/

#socialElements, .reorderQuiz .answer, .appLesson, .footerSocial {
    background-color: #9c9c9c !important;
}
.appLesson .info {
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    background: #575c52;;
    color: #fff;
}
.appLesson {
    color: #fff;
    text-align: center;
    cursor: pointer;
}
.appModuleCircle {
    height: 100px;
    width: 100px;
    border-radius: 56px;
    background: #8f8989;
    margin: auto;
    box-shadow: 0 1px 3px rgba(0,0,0,.12),0 1px 2px rgba(0,0,0,.24);
    cursor: pointer;
    transition: box-shadow ease-in 100ms;
}
#copyright {
    left: -200px;
    right: auto;
}





#appHeader {
    height: 50px;
    position: relative;
    left: 0;
    right: 0;
    z-index: 5000;
    color: #fff;
    overflow: hidden;
    -moz-transition: top .2s ease-in-out;
    -o-transition: top .2s ease-in-out;
    -webkit-transition: top .2s ease-in-out;
    transition: top .2s ease-in-out;
   
}

#appHeader .layer {
    background-color: #9f9e9ee6;
    position: absolute;
    left: 0;
    top: 0;
    height: 50px;
    width: 800px;
   
}
#appHeader .content {
    background-color: #0000;
   
}
#appHeader .layer.lesson {
    left: 100px;
    opacity: 0;
     background-color: #0000;
   
}

#headerPoints {
    display: inline-block;
    width: 25px;
    height: 25px;
    border-radius: 50%;
    background: rgba(255,255,255,.3);
    margin: 10px 25px 0 5px;
    line-height: 26px;
    text-align: center;
    
}

#appHeader .right {
    line-height: 50px;
    position: absolute;
    right: 0;
    top: 0;
    font-size: 10px;
   
}
#appHeader .layer {
    font-size: 20px;
    line-height: 50px;
   
   
}

#appHeader .module .title {
     background-color: transparent!important;
       margin-top: 3px;
    
}
#appHeader .lesson .title {
     background-color: transparent!important;
  
    
}





/*start of menu error*/
.sandwichButton {
    -webkit-filter: opacity(20%); /* Safari 6.0 - 9.0 */
    filter: opacity(20%);
}

.sandwichButton:hover {
    -webkit-filter: opacity(70%); /* Safari 6.0 - 9.0 */
    filter: opacity(70%);
}




.lessonViewInfo {
    background-color: gray!important;
}
.lessonOutline .left {
    float: left;
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}

.lessonOutline .right {
    float: right;
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}

#textView .skipTextButton {
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}
.appModule.certificate .icon {
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}


.discussionButton .badge {
    background-color: #c24b4b;
}

#commentsContainer .commentsFilter {
    background: #808080;
}
#comments .comment {

    border-left: solid 3px #808080;
    border-bottom: solid 1px #808080;
    border-top: solid 1px #808080;

}





#textView .runCode {
    background-color: #6e8d5b;
}
#comments .comment:hover, #comments .comment.elevated {
    border-left-color: #4f8593;
}

.certificates .title {
    color: #fff;
}
.certificates .download {
    color: #fff;
}
.noCode .title {
    color: #fff;
}
.userProfile .details {
    color: #fff;
}
.userProfile .avatar {
    box-shadow: 1px 1px 1px #37363699,-0.5px -0.5px 1px #75757566;
    margin-bottom: 4px;
    margin-top: 4px;
}

.certificates .date {
    color: #ccc;
}
.chart {
    -webkit-filter: contrast(50%); /* Safari 6.0 - 9.0 */
    filter: contrast(50%);
}
hr {
    background: #5f5f5f;
}
.code {  
    border-bottom: 1px solid #5f5f5f;
}


.codeName a.nameLink {
    color: #fff;
}

.textView #addComment.show, .videoView #addComment.show, .quizView #addComment.show {
    visibility: visible;
    opacity: 1;
     -webkit-filter: grayscale(70%); /* Safari 6.0 - 9.0 */
    filter: grayscale(70%)
}

#quizResultOverlay.correct #quizResult {
    background: #4f534b;
}
#quizResultOverlay.correct .continueButton {
    -webkit-filter: grayscale(70%); /* Safari 6.0 - 9.0 */
    filter: grayscale(70%);
    
}
#quizView .checkButton {
     -webkit-filter: grayscale(40%); /* Safari 6.0 - 9.0 */
    filter: grayscale(40%);
    
}

.multipleChoiceQuiz, .strikeOutQuiz {

    background: #2c2c2c;
   
}
.materialRadio .fill {
    background-color: #627b6f;
}
}
