css:
@-moz-document domain("gist.github.com"), regexp("^https://github\\.com/.*") {
@media screen and (prefers-color-scheme: light) {

[role=banner], body > div:first-of-type > div:first-of-type {
  border-bottom: 1px solid #E5E5E5 !important;
  background-color: #F5F5F5 !important;
}

[role=banner] .header-logo-invertocat,
[role=banner] .header-logo-wordmark {
  color: #555 !important;
}

[role=banner] .header-logo-invertocat:hover,
[role=banner] .header-logo-wordmark:hover {
  color: #4078C0 !important;
}

[role=banner] .header-nav-link,
[role=banner] .header-navlink,
[role=banner] .HeaderNavlink,
[role=banner] div.HeaderNavlink a {
  color: #555 !important;
}

[role=banner] .header-nav-link:hover,
[role=banner] .header-nav-link:focus,
[role=banner] .header-navlink:hover,
[role=banner] .header-navlink:focus,
[role=banner] .HeaderNavlink:hover,
[role=banner] .HeaderNavlink:focus,
[role=banner] div.HeaderNavlink a:hover,
[role=banner] div.HeaderNavlink a:focus {
  color: #4078c0 !important;
}

[role=banner] .header-nav-link:hover .dropdown-caret,
[role=banner] .header-nav-link:focus .dropdown-caret,
[role=banner] .header-navlink:hover .dropdown-caret,
[role=banner] .header-navlink:focus .dropdown-caret {
  border-top-color: #4078c0 !important;
}

[role=banner] .notification-indicator .mail-status {
  border-color: #f3f3f3 !important;
}

[role=banner] .notification-indicator .octicon-bell {
  fill: #555 !important;
}

[role=banner] .header-search-scope {
  color: #767676 !important;
  border-right-color: #EEE !important;
}

[role=banner] .header-search-wrapper {
  color: #333 !important;
  background-color: #FFF !important;
  border: 1px solid #DDD !important;
  box-shadow: inset 0 1px 2px rgba(0,0,0,0.075) !important;
}

[role=banner] .header-search-wrapper.focus {
  background-color: #FFF !important;
  box-shadow: inset 0 1px 2px rgba(0,0,0,0.075), 0 0 5px rgba(81,167,232,0.5) !important;
  border-color: #51A7E8 !important;
}

[role=banner] .header-search-wrapper.focus .header-search-scope {
  color: #4078C0 !important;
  background-color: #EDF2F9 !important;
  border-right-color: #C6D7EC !important;
}

[role=banner] .header-search-input {
  color: #333 !important;
}

[role=banner] .header-search-input::-webkit-input-placeholder {
  color: #999 !important;/*AAA*/
}
[role=banner] .header-search-input::-moz-placeholder {
  color: #999 !important;/*AAA*/
}
[role=banner] .header-search-input:-ms-input-placeholder {
  color: #999 !important;/*AAA*/
}
[role=banner] .header-search-input::placeholder {
  color: #999 !important;/*AAA*/
}

[role=banner] .header-search-key-slash {
  filter: invert(100%) brightness(120%) opacity(50%)
}

[role=banner] .Header-link {
  color: #000000 !important;
}

/* README.md filename background */
.Box .bg-white {
  background-color: #f1f8ff !important;
}

nav > ul  {
  scrollbar-color: #eeeeee transparent;
}

}

@media screen and (prefers-color-scheme: dark) {

nav > ul  {
  scrollbar-color: #111111 transparent;
}

}

[role=banner] .header-search {
  font-size: 14px !important;
}

[role=banner] .form-control.header-search-wrapper {
  line-height: 21px !important;
}

[role=banner] .header-search-scope {
  font-size: 12px !important;
  line-height: 20px !important;
}

[role=banner] .header-search-wrapper {
  min-height: 0 !important;
  font-size: 14px !important;
}

[role=banner] .header-search-input {
  min-height: 26px !important;
  font-size: 14px !important;
}

/* new search box as 2023-05-02 */
.search-with-dialog {
  background-color: #FFF;
  border: 1px solid #DDD;
}

.header-search-button.placeholder {
	color: rgba(3,3,3,.3);
}

/* align the header to the content as before */
[role=banner] {
  max-width: 1280px;
  margin: auto;
}

.application-main, .footer {
  max-width: 1280px;
  margin: auto;
}

body {
  font-family: sans-serif;
}

code, pre, tt, .blob-code-inner, .blob-num {
  font-family: monospace;
}

.Box-header .text-mono{
  font-family: monospace !important;
}

.Box-header--blue, .Box-body {
  padding-top: .5em;
  padding-bottom: .5em;
}

.pagehead {
  margin-bottom: 16px !important;
}

.avatar, .avatar-user, .rounded-1 {
  border-radius: 0 !important;
}

/* only slightly round buttons */
.btn {
  border-radius: 2px;
}

/* last changed date is longer than pre-defined 100px when using custom fonts. */
.text-right[role="gridcell"] {
  width: 120px !important;
}

/* do not hide commit info */
h2 ~ .Details--on .Details-content--shown, h2 ~ .Details:not(.Details--on) .Details-content--hidden {
  display: block !important;
}

/* hid the toggle for commit info*/
.Box-header > div > div > .hidden-text-expander {
  display: none !important;
}

/* top bar too much padding */
.Box-header {
  padding-top: 7px;
  padding-bottom: 7px;
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
}

/* narrower between repo name and "Code" "Issues" tabs */
.repohead > .mb-3 {
  margin-bottom: 2px !important;
}

/* show all nav items, not first three */
nav > ul > li > a {
  visibility: visible !important;
}
nav > ul  {
  overflow-x: auto;
  overflow-y: hidden;
  scrollbar-width: thin;
}

}
