css:
@-moz-document domain("mybankofinternet.com") {
 #appContainer, .header-container {
    font-size: 95%;
 }
 .header-container, #appContainer * {
    font-family:'-apple-system', 'system-ui', 'BlinkMacSystemFont', 'Segoe UI', 'Roboto', 'Helvetica Neue', 'Arial', 'sans-serif', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol' !important;
 }
 .header-container {
    height: 35px;
 }
 .logo, .header .logo img {
    display: none !important;
 }
 .container {
    width: 100%;
 }
 .layout {
    min-height: calc(100vh - 85px);
 }
 #appContainer {
    min-height: calc(100vh - 85px);
 }
 #mainApplication {
    min-height: calc(100vh - 85px);
 }
 .di-container {
    padding: 10px;
    font-size: 12px;
 }
 .di-container h1 {
    font-size: 18px;
 }
 .di-calendar {
    display: none;
 }
 #calculatorContainer {
    display: none;
 }
 #unEnrolledContainer {
    display: none;
 }
 #messageOfTheDayContainer {
    display: none;
 }
 .brand-type-a {
    padding: 5px 10px;
    margin: 0 0 10px 0;
    background-color: #C1CCD7 !important;
 }
 .brand-type-a * {
    color: #333 !important;
 }
 #outsideAccounts {
    line-height: 90%;
 }
 #outsideAccounts h2 {
    font-size: 110%;
    font-weight: bold;
 }
 .btn, input, button, .di-checkbox {
    font-size: 16px !important;
    height: 32px !important;
 }
 iframe {
    height: 100% !important;
 }
 .promo_msg_nhp_btm_content_1 {
    display: none;
 }
 .main-navigation-container, .navbar-collapse, .quickLinks-nav-list {
    background: #2F517A !important;
 }
 li .item, .quickLinks-nav-list .item {
    color: #2F517A !important;
 }
 .last-login {
    display: none;
 }
 #footerSibling, #footer-container {
    display: none !important;
    padding: 0;
 }
 #pageHeaderDiv {
    height: 800px;
    width: 100%;
 }
 @media(min-width: 1450px) {
    #pfmWidget {
       height: 800px;
       width: 800px;
    }
 }
 #pageHeaderDiv iframe {
    height: 700px !important;
 }
 #hamburgerMenuLabel, .hamburger-icon, .alerts .di-svg {
    color: white !important;
 }
 .hamburger-icon {
    margin-top: 28px;
 }
 .alerts .di-svg {
    margin-top: 48px;
    margin-right: 5px;
    right: 0;
    position: absolute;
 }
 .mobile-nav {
    margin-top: 45px;
    background: #035E7B !important;
 }
 .mobile-nav * {
    color: white !important;
 }
}
