css:
@-moz-document domain("hupu.com") {
body {
  font-family: "更纱黑体 SC", "Source Han Sans SC", "Helvetica Neue", Helvetica, Arial, "PingFang SC", "Hiragino Sans GB", "Heiti SC", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif !important;
}

div,
a {
  font-family: "更纱黑体 SC", "Source Han Sans SC", "Helvetica Neue", Helvetica, Arial, "PingFang SC", "Hiragino Sans GB", "Heiti SC", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif !important;
}

div.floor-show {
  background-color: white;
  border-radius: 4px;
}

div.floor {
  border-top: none !important;
  border-bottom-style: solid;
  border-bottom-color: #f6f6f6;
  border-bottom-width: 1px;
}

div.floor-show .floor_box {
  background-color: white;
}

.hp-mainNav {
  border: 0px;
  font-size: 14px;
  font-weight: 300;
}

.hp-mainNav-B .hp-nav {
  border: none;
}

.hp-mainNav-B .hp-nav li.on {
  background: hsl(0, 0%, 96%) !important;
}

.hp-mainNav-B .hp-subNav {
  background: hsl(0, 0%, 96%) !important;
  border: none;
}

.hp-mainNav-B .hp-subNav .on {
  font-weight: 400;
  color: #D55255;
}

.hp-nav li:hover {
  background: #D25255 !important;
}

.line {
  display: none !important;
}

.nba_er {
  display: none;
}

a:hover {
  text-decoration: none !important;
}

#hp-topbarNav {
  height: 36px;
  font-size: 14px;
  display: flex;
  align-items: center;
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.topFollowBlog {
  display: none;
}

.hp-drapDown {
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.hp-set:hover {
  border: none !important;
}

.shadow-1 {
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.hupu-shihuo-hot {
  display: none;
}

.hp-threeNav .hp-threeNav-bd .hp-threeNav-item li a {
  border: none;
  background: hsl(0, 0%, 96%);
  font-size: 14px;
}

.breadCrumbs {
  font-size: 14px;
}

.pl_nav {
  font-size: 14px !important;
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.bbsHotPit .bbs_head .pl_nav #navigation {
  border-top: none;
}

.bbsHotPit .show-list .for-list li .titlelink {
  font-size: 14px;
}

.floor_box .author .left a.u {
  font-size: 14px;
}

.floor_box .case td .f999 {
  display: none;
}

.hp-wrap p {
  padding: 6px 12px;
}

.details #container #container_nopadd {
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.details #container #container_nopadd #sub_line #t_main {
  border: none;
}

#sub_sidebar {
  border: none;
}

.user_dropmenu {
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.floor .user .headpic img {
  background: none;
  border-radius: 50%;
}

.floor_box .quote-content {
  text-align: left;
  line-height: 28px;
  font-size: 16px;
}

.floor_box .case td {
  vertical-align: top;
  line-height: 28px;
  font-size: 15px;
}

.floor_box .author {
  border: none;
  padding-top: 8px;
}

.floor_box .case {
  padding: 0px;
}

.floor_box .author .right {
  margin-right: 10px;
}

.feedhome.sidebar-col-A h3 {
  border-top: none;
}

.bbsHotPit .show-list .for-list li {
  border-bottom-style: solid;
  border-bottom-color: #f6f6f6;
  border-bottom-width: 1px;
}

.floor_box .case td .subhead {
  display: none;
}

body .bbs-content #container .container_padd {
  border: none;
  background: white;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.showPlate .bbs-showMyPlate li .selected {
  background: hsl(0, 0%, 96%);
}

.showPlate .bbs-showMyPlate li a:hover {
  background: hsl(0, 0%, 96%);
}

.showPlate .bbs-showMyPlate li a {
  font-size: 14px;
}

.floor .badge {
  display: none;
}

.showPlate .bbs_banner li a {
  background: none;
  font-size: 14px;
}

.showPlate .bbs_banner li .bbs_banner_over {
  background: white;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
  border: none;
  border-radius: 4px;
}

.showPlate .bbs_banner li .bbs_banner_over .bbs_banner_bg dl dd {
  border: none;
}

.showPlate .bbs_banner li .bbs_banner_over .bbs_banner_bg dl dd .tit {
  font-size: 14px;
}

a:hover,
.bbsHotPit .bbs_head .pl_nav .pl_top a:hover,
.bbsHotPit .show-list .for-list li .author .aulink:hover,
.bbsHotPit .bbs_head .pl_nav .m5 a:hover,
.bbsHotPit .bbs_head .pl_nav .m5 a:hover,
.hp-wrap a:hover,
.floor_box .author .left a.u:hover {
  background: none;
  color: #005eac;
}

.blue:hover {
  background: none !important;
  color: #005eac !important;
}

.clearfix.tabs_border {
  display: none;
}

.bbsHotPit .tabs_header .left .selected {
  border-bottom: 2px #d94747 solid;
  border-top: none;
}

.bbsHotPit .tabs_header .left li,
.bbsHotPit .tabs_header .left li a {
  border: none;
}

.bbsHotPit .tabs_header .left li a {
  background: none;
}

.floor_box .liangShare,
.floor_box .cm {
  display: none;
}

blockquote {
  border: none !important;
}

.feedhome h3 {
  font-size: 14px;
}

.w_reply {
  border: none;
}

.w_reply h5,
body .bbs-content #container .container_padd .bbsPaList {
  border: none;
}

.bbsHotPit .list ul li .textSpan a,
.bbsPaList .list ul li,
.bbsPaList h3 {
  font-size: 14px;
}

a.floornum::before {
  color: #666;
  display: inline;
  content: "#";
}

a.floornum {
  font-size: 14px;
}

a[rel="nofollow"],
a.reply-sponsor-button {
  display: none;
}

.light_r.warriors-light {
  display: none;
}

.bbs-icon-gold {
  display: none;
}

.multipage {
  background: none;
  padding-left: unset;
}

#sub_sidebar {
  display: none;
}

.tips-new-comment {
  border: none;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

.like,
.sign {
  display: none;
}

a.tcdNumber,
a.nextPage,
a.prevPage {
  border: none !important;
}

#container .page a:hover,
#container .page a:hover,
#container .page a:hover {
  background-color: #95071b1a !important;
  color: #95071B;
}

.showPlate .bbs_banner li .bbs_banner_over .bbs_banner_bg dl dd .title:hover {
  background: none;
}

.btns4,
.btns2,
.btnb2,
.btns6 {
  background: #a41f24 !important;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
  border-radius: 4px;
  width: 64px;
}

.btns4:hover,
.btns2:hover,
.btnb2:hover,
.btns6:hover {
  background: #95071bd6 !important;
}

#vote_box,
.vote_box {
  border: none;
  font-size: 14px;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

input[type="checkbox"],
input[type="radio"] {
  cursor: pointer;
}

.bbsHotPit .tabs_header .right .issue_button {
  border-radius: 50em;
  background-color: #a41f24;
  background-image: unset;
  color: white;
}

.bbsHotPit .tabs_header .right .issue_button:hover {
  background-color: #a41f24;
  color: white;
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
  transform: translateY(-1px);
}
#container_nopadd {
  min-height:unset;
}

.red a:link.ilike_icon,
.red a:hover.ilike_icon,
.red a:visited.ilike_icon,
.red a:active.ilike_icon{
  background: unset;
  background-image: url("data:image/png;base64,") !important;
  background-size: contain;
  background-repeat: no-repeat;
  height: 18px;
}
a.ilike_icon,
a:hover.ilike_icon,
a:focus.ilike_icon,
a:hover.ilike_icon {
  background: unset;
  background-image: url("data:image/png;base64,") !important;
  background-size: contain;
  background-repeat: no-repeat;
  height: 18px;
}

/* 回复框*/
div#re,#fastform {
  width: 100%;

}
div#re {
  box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}
#re_top, #re #re_bottom {
  display:none;
}
#re #re_box{
  border:none;
  background-color:unset;
}
#re .face_button{
  display:none;
}
#re #re_box .j_enter {
  border: 2px solid #9b9b9b;
  padding: 4px;
}
#re #re_box .left {
  width: 96px;
}
#re #re_box .left a.headpic img {
  border-radius: 50%
}
#re #re_box .left a.blue{
  margin-top: 32px;
  padding: 5px 15px 3px;
  border-radius: 3rem;
  border: 2px solid #a41f24;
  font-weight: bold;
  font-size: 14px;
  color: #000;
}

/* 通用按钮样式 */
.btns2 ,.btns4, .btns2, .btnb2, .btns6 {
  padding: 5px 15px 3px;
  border-radius: 3rem;
  border: 2px solid #a41f24;
  font-weight: bold;
  font-size: 14px;
  height: auto;
}



}
