css:
@-moz-document url("https://www.inet.se"), regexp("((http|https):\\/\\/).*\\inet.se\\/(?!(datorbyggare))(.*)") {
:root {
  --hoverColor: rgba(255, 255, 255, 1);

  --textColor: rgba(211, 211, 211, 1);
  
  --borderColor: rgba(56, 56, 57, 1);
  --borderColorLight: rgba(81, 81, 82, 1);
  --borderColorInet: #d2d2d2;

  --backgroundColor: rgba(11, 11, 12, 1);
  --backgroundColorLight: rgba(18, 18, 19, 1);
  --backgroundColorLighter: rgba(32, 32, 34, 1);
  --backgroundColorLightest: rgba(54, 57, 62, 1);
}

* {
  color: var(--textColor) !important;
}
body {
  color: var(--textColor);
  background-color: var(--backgroundColor) !important;
}
a {
  color: var(--textColor) !important;
}
a:focus, a:hover {
  color: var(--hoverColor) !important;
}
a[onclick] {
  display: none !important;
}
.breadcrumb {
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColor) !important;
}
.form-control {
  color: var(--textColor) !important;
  background-color: var(--backgroundColorLightest) !important;
}
.contact-container {
  border-bottom: none !important;
  bottom: -11px !important;
  background-color: var(--backgroundColorLighter) !important;
}
.search-bar .btn-primary {
  background-color: var(--backgroundColorLighter) !important;
  border-color: var(--borderColor) !important;
  border-left: none !important;
}
.search-bar .dropdown {
  background-color: var(--backgroundColor) !important;
  border-color: var(--borderColor) !important;
}
.search-bar .dropdown .products {
  background-color: var(--backgroundColor) !important;
}
.front-page-top-list .top-list-wrapper > ol > li,
.form-control, .has-error .form-control, .has-success .form-control {
  border-color: var(--borderColor) !important;
}
footer.inet-footer {
  padding: 0 !important;
  background-color: var(--backgroundColorLighter) !important;
}
.tree-wrapper {
  background-color: var(--backgroundColor) !important;
}
#category-tree ol,
#category-tree ol > li > .tree-item,
#category-tree ol.lvl1,
#category-tree ol.lvl1 > li > .tree-item,
#category-tree ol.lvl2,
#category-tree ol.lvl2 > li > .tree-item,
#category-tree ol.lvl3,
#category-tree ol.lvl3 > li > .tree-item {
  border: none !important;
}
#category-tree ol.lvl1 > li > .tree-item {
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColorLight) !important;
}
#category-tree ol.lvl3 {
  background-color: var(--backgroundColorLight) !important;
}
#category-tree ol {
  background-color: var(--backgroundColorLighter) !important;
}
.page-block > a {
  border: none !important;
}
.top-list-wrapper,
.front-page-top-list .top-list-wrapper > ol > li > a > div:last-child > .product-price {
  background-color: var(--backgroundColorLight) !important;
}
.front-page-top-list .top-list-wrapper > ol > li > a.numberedList::before {
  color: var(--textColor) !important;
  font-weight: bold !important;
}
.inet-navbar {
  color: var(--textColor) !important;
}
.my-pages ul.nav > li a {
  background-color: var(--backgroundColorLighter) !important;
  border: none !important;
  outline: none !important;
}
.my-pages ul.nav > li.active a {
  color: var(--textColor) !important;
  background-color: var(--backgroundColorLightest) !important;
}
.order-list > li.header,
.order-list > li > .info {
  margin: 0 !important;
}
.order-list > li.header {
  color: var(--textColor) !important;
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.order-list > li {
  border: 1px solid var(--borderColor) !important;
  background-color: var(--backgroundColorLighter) !important;
}
.order-list > li .products {
  background-color: var(--backgroundColorLight) !important;
  border-top: none !important;
}
.order-list > li .products > li:hover {
  background-color: var(--backgroundColorLighter) !important;
}
.inet-striped-list {
  border-color: var(--borderColor) !important;
  background-color: var(--backgroundColor) !important;
}
.inet-striped-table {
  border-color: var(--borderColor) !important;
  background-color: var(--backgroundColor) !important;
  margin-bottom: 10px !important;
}
.inet-striped-list .list-header,
.inet-striped-table thead tr th {
  background-color: var(--backgroundColorLightest) !important;
  border-bottom: none !important;
}
.product-list > ul > li {
  background-color: var(--backgroundColor) !important;
}
.search-bar .dropdown .products .product:nth-child(2n+1),
.table-inet-striped tr:nth-child(2n+1),
.user-cart-list .user-cart-list-lines .user-cart-list-line:nth-child(2n),
.specifications li:nth-child(2n+1),
.product-list > ul > li:nth-child(2n+1),
.inet-striped-list .list-row:nth-child(2n+1),
.inet-striped-table tbody tr:nth-child(2n) {
  background-color: var(--backgroundColorLight) !important;
}
.product-list .toolbar > * > select {
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.btn.disabled, [disabled].btn, fieldset [disabled].btn {
  color: var(--textColor) !important;
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColorLight) !important;
}
.with-filter-options .filters {
  border: none !important;
  background-color: var(--backgroundColorLighter) !important;
}
.select header {
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColor) !important;
}
.select .dropdown ul {
  border-color: var(--borderColor) !important;
}
.select .dropdown li {
  background-color: var(--backgroundColorLight) !important;
}
.panel-heading, .panel-title {
  color: var(--textColor) !important;
}
.product-page .stock-box,
.product-page .purchase {
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.cartContent .panel {
  border-color: var(--borderColorLight) !important;
}
.cartContent .panel-heading {
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.cartContent .panel-body {
  background-color: var(--backgroundColorLight) !important;
}
.user-cart-list .cart-container .panel {
  background-color: var(--backgroundColor) !important;
  border-color: var(--borderColor) !important;
}
.user-cart-list .cart-container .panel-heading {
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColor) !important;
}
.cartContent .btn-primary, .cartContent .btn-primary.active, .cartContent .btn-primary:active, .cartContent .open > .dropdown-toggle.btn-primary {
  color: var(--textColor) !important;
  border: none !important;
  background-color: var(--backgroundColorLighter) !important;
}
.cartContent .btn-primary:hover {
  background-color: var(--backgroundColorLight) !important;
}
.product-page .panel {
  border-color: var(--borderColorLight) !important;
}
.product-page .panel-heading {
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.product-page .panel-body {
  background-color: var(--backgroundColorLighter) !important;
}
.product-page .panel-body .review {
  border-color: var(--borderColor) !important;
}
.product-page .accessories li {
  border-color: var(--borderColor) !important;
}
.product-reviews .review .body {
  width: 19.5em !important;
  white-space: nowrap !important;
  overflow: hidden !important;
  max-height: 100% !important;
  text-overflow: ellipsis !important;
  padding-bottom: 20px !important;
}
.product-reviews .review .body .show-more {
  color: var(--textColor) !important;
  background-color: transparent !important;
}
.top-nav-bar {
  background-color: var(--backgroundColorLightest) !important;
  border-color: var(--borderColor) !important;
}
:not(.payment-methods) > .nav + .tab-content {
  background-color: var(--backgroundColorLighter) !important;
}
.nav + .tab-content {
  border: none !important;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover,
.nav-tabs > li > a,
.nav-tabs > li > a:focus,
.nav-tabs > li > a:hover {
  color: var(--textColor) !important;
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
  padding: 10px !important;
}
.nav-tabs > li.disabled.active > a,
.nav-tabs > li.disabled.active > a:focus,
.nav-tabs > li.disabled.active > a:hover,
.nav-tabs > li.disabled > a,
.nav-tabs > li.disabled > a:focus,
.nav-tabs > li.disabled > a:hover {
  color: var(--textColor) !important;
  background-color: var(--backgroundColor) !important;
  border: 1px solid var(--borderColor) !important;
  border-bottom: none !important;
}
.tab-content {
  margin-bottom: 10px !important;
}
.panel {
  background-color: var(--backgroundColorLight) !important;
  border-color: var(--borderColor) !important;
}
.panel-body {
  background-color: var(--backgroundColorLight) !important;
}
.panel-body .btn-primary, .panel-body .btn-primary.active, .panel-body .btn-primary:active, .panel-body .open > .dropdown-toggle.btn-primary {
  color: var(--textColor) !important;
  border: none !important;
  background-color: var(--backgroundColorLighter) !important;
}
.panel-body .btn-primary:hover {
  background-color: var(--backgroundColorLight) !important;
}
.panel-default > .panel-heading {
  background-color: var(--backgroundColorLightest) !important;
  border: none !important;
}
.panel-default {
  border-color: var(--borderColor);
}
.panel-danger > .panel-heading {
  background-color: rgb(139, 12, 0) !important;
  border: none !important;
}
.panel-danger > .panel-body.bg-danger {
  background-color: rgba(139, 12, 0, 0.8) !important;
}
.product-gallery .carousel-gallery {
  background-color: var(--backgroundColorLighter) !important;
  border: none !important;
}
.carousel-gallery .image-slide {
  background-color: #fff !important;
}
.carousel-labels > li {
  border: none !important;
}
.carousel-labels > li.active {
  background-color: var(--backgroundColorLight) !important;
}
.compare h2,
.specifications h2 {
  background-color: var(--backgroundColorLighter) !important;
}
.compare .tableHeader,
.compare .table,
.compare .specification-group {
  background-color: var(--backgroundColorLighter) !important;
}
.compare .tableHeader {
  border-bottom: 1px solid var(--borderColor) !important;
}
.compare .table-inet-striped td:not(:last-child),
.compare .table-inet-striped th:not(:last-child) {
  border-right: 1px solid var(--borderColor) !important;
}
.checkout .shipping-methods > div {
  background-color: var(--backgroundColorLight) !important;
  border: none !important;
}
.checkout .shipping-methods > div.active {
  background-color: var(--backgroundColorLighter) !important;
}
.checkout .nav > li:first-child.active {
  border-left: none !important;
}
.checkout .nav > li > a {
  background-color: var(--backgroundColorLighter)!important;
  border: none !important;
}
.checkout .nav > li.active > a {
  background-color: var(--backgroundColorLight) !important;
}
.modal {
  background-color: rgba(0, 0, 0, 0.5) !important;
}
.modal-content {
  border: 1px solid !important;
  border-color: var(--borderColorLight) !important;
}
.modal-header {
  border: none !important;
  background-color: var(--backgroundColorLightest) !important;
}
.modal-body {
  background-color: var(--backgroundColorLight) !important;
}
.cart-content .price .product-price .active-price {
  color: var(--textColor) !important;
}
.modal .btn-primary, .modal .btn-primary.active, .modal .btn-primary:active, .modal .open > .dropdown-toggle.btn-primary {
  color: var(--textColor) !important;
  border: none !important;
  background-color: var(--backgroundColorLighter) !important;
}
.modal .btn-primary:hover {
  background-color: var(--backgroundColorLight) !important;
}
.modal-footer {
  border-top: none !important;
  background-color: var(--backgroundColorLightest) !important;
}
.dropdown-menu {
  background-color: var(--backgroundColorLighter) !important;
}
.dropdown-menu > li > a:hover {
  background-color: var(--backgroundColorLightest) !important;
}
.well {
  background-color: var(--backgroundColorLighter) !important;
  border-color: var(--borderColor) !important;
}
input::-webkit-input-placeholder,
input::-moz-placeholder,
input::-ms-placeholder,
input::placeholder {
    color: var(--textColor) !important;
}
}
