 body {
        -webkit-font-smoothing: subpixel-antialiased;
        -moz-osx-font-smoothing: auto;
 }

  h1, h2, h3, h4 {
	font-family:"HelveticaNeue-Light",
	/**/
	"Helvetica Neue Light",
	/**/
	"Helvetica Neue",
	/**/
	Helvetica,
	/**/
	Arial,
	/**/
	"Lucida Grande",
	/**/
	"-apple-system",
	/**/
	BlinkMacSystemFont,
	/* MacOS and iOS */
	'avenir next', avenir,
	/* MacOS and iOS */
	'Segoe UI',
	/* Windows */
	'lucida grande',
	/* Older MacOS */
	'helvetica neue', helvetica,
	/* Older MacOS */
	'Fira Sans',
	/* Firefox OS */
	roboto, noto,
	/* Google stuff */
	'Droid Sans',
	/* Old Google stuff */
	cantarell, oxygen, ubuntu,
	/* Linux stuff */
	'franklin gothic medium', 'century gothic',
	/* Windows stuff */
	'Liberation Sans',
	/* Linux */
	'sans-serif';
	/* Everything else */
}
.pod h1, .pod h2, .pod h3, .pod h4 {
	font-weight: 200
}
.pod h1 {
	font-size: 1.602em;
}
.pod h2 {
	font-size: 1.424em;
}
.pod h3 {
	font-size: 1.266em;
}
.pod h4 {
	font-size: 1.125em;
}
.pod, .content {
	font-size: 14pt;
	line-height: 1.618 !important;
	font-family:"Merriweather", Georgia, serif;
	font-weight: 200;
}
.search-results, .content {
	max-width: 42em;
}
.syntaxhighlighter .code {
	font-size: 1.5em !important;
}

code, dt, .nav-list, .breadcrumbs, #index-container .index-border {
	font-family: Menlo, Monaco,
	/* MacOS */
	'Segoe UI Mono, Lucida Console',
	/* Windows */
	'Fira Mono',
	/* Firefox OS */
	'Roboto Mono', 'Noto Mono',
	/* Google stuff */
	'Droid Sans Mono',
	/* Old Google stuff */
	'Ubuntu Mono', 'DejaVu Sans Mono', 'Liberation Mono',
	/* Linux */
	'Courier New',
	/* MacOS and Windows */
	monospace;
	/* Everything else */
}
code {
	background-color: #fff0d0;
	font-size: 1em;
	border-radius: 10px;
	padding: 4px;
}
#index-container .index-border {
	display: block !important;
	overflow: auto;
	max-height: 25em;
	font-size: 12pt;
}
dt {
	font-weight: normal;
	color: #66f;
}
.pod h1, .pod h2, .pod h3, .pod h4, .pod h5, .pod h6, .pod pre, .pod form, .pod fieldset, .pod input, .pod blockquote, .pod th, .pod td, .pod ul, .pod ol {
	margin-top: 30px;
	margin-bottom: 15px;
	margin-left: 0px;
	margin-right: 0px;
}
.pod p, .pod pre, .pod dl, .pod dt {
	margin-top: 15px;
	margin-bottom: 15px;
	margin-left: 0;
	margin-right: 0;
}
.pod dd {
	margin-top: 15px;
	margin-bottom: 15px;
	margin-left: 30px;
	margin-right: 0;
}
.pod h1 + h2, .pod h2 + h3, .pod h3 + h4, .pod h4 + h5 {
	margin-top: 15px;
}
.pod pre {
	padding: 15px 10px;
}
.nav-list {
	line-height: 1.618 !important;
}
.content {
	margin-left: 250px;
}
.nav-list {
	width: 249px;
}
.breadcrumbs {
	margin: 20px 0 15px 0;
	padding-bottom: 15px;
	border-bottom: 1px solid #e9e9e9;
}
.tool-bar-form {
	margin: 15px 0;
	width: auto;
}
}
