css:
@-moz-document url("https://tweetdeck.twitter.com/") {
body {
    background-color: #243447;
}
.js-column-header.js-action-header.flex-shrink--0.column-header, .js-app-columns.app-columns.horizontal-flow-container.without-tweet-drag-handles, .js-stream-item-content.item-box.js-show-detail {
    background-color:#243447;
}
.quoted-tweet, .js-stream-item-content.item-box.js-show-detail {
    color:#dcdcdc!important;
}
.fullname.link-complex-target, .column-heading {
    color:#fcfcfc!important;
}
.quoted-tweet {
    border-color:#151f2a;
    border-width:0;
}
.scroll-styled-v {
    background-color:#243447!important;
}
.scroll-styled-h {
    background-color:#151f2a!important;
}
.stream-item {
    border-color:#151f2a;
    border-width:0;
}
.column-header {
    border-color:#151f2a;
    border-width:0;
}
.scroll-styled-h::-webkit-scrollbar-thumb, .scroll-styled-v::-webkit-scrollbar-thumb {
    background-color: #56788f;
}
.scroll-styled-h::-webkit-scrollbar-track, .scroll-styled-v::-webkit-scrollbar-track {
    border-left: 0 solid #e1e8ed;
}
.js-column-header.js-action-header.flex-shrink--0.column-header, .js-column-options-container.column-options, .accordion-divider-t.button-tray {
    background-color:#1a2533;
}
.facet-type, .with-column-divider-bottom {
    border-bottom: 1px solid #1a2533;
}
.accordion-divider-t {
    border-top: 1px solid #1a2533;
}
}
