css:
@-moz-document domain("g2a.com") {
:root {
    --main: #111;
    --bg1: #151515;
    --bg2: #1a1a1a;
    --bg3: #222;
    --bg4: #252525;
    --bg5: #2a2a2a;
    --bg6: #333;
}
.mp-head-giftcard, #supportHubToolTip{
    display:none;
}
.main-loader{
    visibility:hidden;
}
#container2 {
    padding-bottom: 120px!important;
}
#footer-wrap, .tabs .nav-tabs, .table, .table-grid .bottom, .page-wrapper .container-wrapper, .page-content .content-section, footer, #helpdesc #ticket-search, .extra-description, .ticket .row.row3, .top-menu .category-tags-container, #webinars, #banner, .external-container .rating-container.rating-container-dark, .privacy-container .agreement-box__inner , main.right-menu .main-background>div::after, main.right-menu .main-background>div::before, .right-menu .right-menu-container, main.right-menu #ui-container #category-container, main.right-menu #ui-container #category-container::after, .rc-replay .reply-group .conversation-form-color, .rc-replay .reply-group .reply-group-actions.white-background, .mp_pagers.g2a-framework-pager, .page--product2, #content_checkout2 .cart-fix-inneed{
    background:none;
}
div [id="search-container"], div [id="content_checkout2_l"]{
    background:#111!important;
}
.product-tags , .gate-prod-man-box-wrapper, .m-select, .m-select ul{
    background: #656363;
}
.reply-group .att-file, .product-offers .offers-list .offer:hover , .m-select:hover{
    background: #424242;
}
.external-container .rating-container .rating-list .rating .discussion .comment, .fullscreen-search-results.noResults, .fullscreen-search-results.noResults , .modal-options .checkboxes-wrapper{
    background: #333;
}
body, .seller-info__user, .seller-info__feedback-message, .seller-info__transactions, .product-offers .offers-list .offer .price, .product-page-v2-price--large .product-page-v2-price__price, .offer-label, .tag__bold, .price, .m-select, .RatingComment__seller, .RatingComment__buyer-reply, span.new-lines .ng-binding .ng-scope >p, .conversations .conversation p {
    color: #f5f5f5;
}
.fullscreen-search-container , .product-sections__wrapper{
    background: #111;
}
.content_checkout4 .b-white, .content_checkout5 .b-white, .mp-user-popup {
    background: #222;
}

.modal-content, .modal-footer, .modal-window , .tabs-links .btn.click{
    background-color: var(--bg3);
}
.modal-options .checkboxes-wrapper .checkbox label , .Card__price-cost{
    color: #f2f2f2;
}
.modal-header{
    background-color: var(--bg2);
}
.fullscreen-search-form , .preorder-advance-table-box, .preorder-advance-status{
    background: #111;
}
.tabs-button.active span {
    background: #7777;
    color: #fffdfd;
}
.topbar-widget .popover .popover-content .popover-options .option:hover {
    color: #006cff;
}
.topbar-widgets {
    background: rgba(0, 0, 0, .54);
}
.topbar-widget-mymarketplace .popover .popover-content .popover-options {
    background: #111;
}
.gallery__thumbnails .thumbs__arrow-right {
    background: -webkit-gradient(linear, left top, right top, from(hsla(0, 0%, 100%, 0)), color-stop(30%, #111));
    background: linear-gradient(90deg, hsla(0, 0%, 100%, 0), #111 30%);
}
.ticket .col.col3 .attachment svg {
    fill: rgba(255, 255, 255, 0.72);
}
.gallery__thumbnails .thumbs__arrow-left {
    background: -webkit-gradient(linear, right top, left top, from(hsla(0, 0%, 100%, 0)), color-stop(30%, #111));
    background: linear-gradient(-90deg, hsla(0, 0%, 100%, 0), #111 30%);
}
.tabs-content {
    background: #7777;
}
.alert , .Card__discount .old-price{
    color: #d1d1d1;
}
.g2aui-loader {
    background: rgba(0, 0, 0, 0.8);
}
button:hover {
    background: #8888!important;
}
.mp-acc-f-head {
    background-color: #222121;
}
.product-tags>li a {
    color: #f5f5f5;
}
.user-pic .no-photo, .user-pic img {
    border: 2px solid #a8a8a8;
}
.conversations .conversation p, .form-control {
    background: #444;
}
.ticket .row.row4 , #category-container.topic-view .was-helpful{
    background: #444;
}
body, html, .page--homepage, .page--category, .page--homepage, .page--search, .mp-profit .jq--mp-profit, .main-loader, table.notifications tr.odd, .container {
    background: var(--main) !important;
}
.section--loot, .footer-wrap, .footer-main, .footer .footer-wrap, .mp_pagers, .notifications-bar, .mp-acc-front-special, .sidebar, #auction-type-tabs {
    background: var(--bg1);
}
.guides__wrapper .guide__item, .mp-acc-front, .verification .table, .table>.table-body>.tr, .tabs .nav-tabs>li.active>a, .tabs .nav-tabs>li.active>a:focus, .tabs .nav-tabs>li.active>a:hover, .tabs .nav-tabs>li>a:hover, .table thead, .table-grid .dataTables_processing {
    background: var(--bg1);
    border-color: var(--bg1);
}
.sticky-header-homepage, .sticky-header__bar, .mp-myacc .mp-acc-conetent {
    background: var(--bg1);
    border-top: 1px solid var(--bg6);
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.1), 0 2px 3px rgba(0, 0, 0, 0.1);
}
.extra-description__content :last-child {
    color: #e0e0e0;
}
.table tbody tr {
    cursor: pointer;
    -webkit-transition: background-color;
    transition: background-color;
}
.btn.btn-secondary, .Card__base, .attributes-list, .product-section__attributes, .topbar-widget-mymarketplace .popover .popover-content, .mp-table-head, .mpt-inv tbody tr:nth-child(2n+1), .seller__wrapper, #side-menu>li.active, .table tbody tr:hover td {
    background: var(--bg2);
    border-color: var(--bg2);
}
.btn.btn-secondary:hover {
    background: var(--bg3);
    border-color: var(--bg3);
    color: #03A9F4;
}
.form-container .loader {
    background: #444444b8;
}
.dropdown-menu {
    background-color: #1c1c1c;
}
.mp-balance-table .tr-first {
    background-color: #444;
    border: 0 solid #666;
}
#supportHubToolTip {
    background-color: #444;
}
.supportHubTriangle {
    border-color: #444 transparent transparent;
}
.ticket .collapse-animation .row.row2 {
    border-bottom: 0 solid #fff;
}
.reply-group .reply-group-actions {
    background: #444;
}
textarea.form-control.form-medium, textarea.form-medium.select-button {
    background-color: black;
    color:white;
}
.popover-content, .topbar-widget-footer, .mp-myacc {
    background: var(--bg3);
    border-color: var(--bg3);
}
.Button--secondary, #auction-type-tabs .tab--active {
    background: #444;
}
.m-row .vat-moss-out.upgrade {
    background: #36362c;
}
.go-to-top-arrow {
    background: var(--bg4);
    opacity: 1;
}
.SellingSettings__container {
    border:none;
}
.key-status-1 {
    background-color: var(--main);
}
.key-status-2 {
    background-color: #243823;
}
.wh-edit-navig {
    background-color: var(--bg2);
}
.marketplace-popup-header {
    background: url(https://www.g2a.com/skin/frontend/gate/default/images/logo_on-dark_v2-80px.png) 30px 20px no-repeat var(--bg6);
}
#mp-wholesale-viewkeyshistory th {
    background-color: var(--bg4);
}
.marketplace-popup {
    color: #999;
    background-color: var(--bg2);
}
.mp-seller-customize-section.store-url .url-block {
    border: 1px solid #adadad;
    background: #444;
}
.topic-list li:hover {
    background-color: #424242;
}
.mp-myacc .m-row h2, .mp-acc-conetent .m-row h3, .mp-th-count, .mb-th-sort span.sortby, .description-title, h4 span, h3, #best_marketplace_price span, .Card__title a, .topbar-widget-mymarketplace .panel-mymarketplace-account .account-id .label, td, .supportHubTitle, .mp-sum-pending-balance, .page-header h1, .form-group.form-group-medium>label, #ticket-search label, .tickets .tr.bold, .form-container .input-container label, .Requirements__maxSize, span.ng-scope, .SellingSettings__heading, .SellingSettings__header, .customize-header-wrapper, .title-center h2, .topic-list li div.question, .section-headline.medium, .sidebar-nav-btn:hover, .form-control, #mp-wholesale-viewkeyshistory td:nth-child(odd) , .modal-title, h2, #contact-container .contact-item a span.title, .progressbar-module__label, .progressbar-module__level-label, .progressbar-module__progress__title, .progressbar-module__marks, .quater-diagram-label, .quarter-textlabel p, .overall__level, #auction-type-tabs .tab, .pages li a, .mp_pagers.g2a-framework-pager .pager .limiter, .check-items-view .check-items-h4, .content_checkout4 .checkout2_h4, .content_checkout5 .checkout2_h4{
    color:#fafafa;
}
.tabs .nav-tabs>li.active>a, .form-group .form-control-input .form-input-description, p, .tickets .tr, .SellingSettings__about-option, .sidebar-nav-second_level a, .sidebar-nav-btn, .mp-table-head , .right-menu .right-menu-container .tags-menu, .category-tags-container .tags-menu .menu-item>div{
    color:#bfbfbf;
}
.fullscreen-search-input {
    color: #fff!important;
}
.account-phone .secure-options .option {
    background: #444444;
}
.product-tags {
    background: #222;
    border: 1px solid #444;
    border-radius: 5px;
}
.table>.table-body>.tr>.th {
    background: #444;
    color: #e0e0e0;
}
.sidebar-nav-btn .icon {
    fill: #f5f5f5;
}
.balance-list .show-more {
    background-color: #444;
    color: #f2f2f2;
}
.tabs .nav-tabs>li.active>a:hover {
    color:#fff!important;
}
.mpt-inv tr td.mp-amount-minus {
    color:orange;
}
.mpt-1 tr td.mpt-1-td-desc small, .mpt1-left, .mp_pagers, div[id="all-notifications"], #all_currencies-sg label, .main-title, .conversations .conversation span.date small {
    color:#707070;
}
.go-to-top-arrow svg {
    fill: #fff;
}
.footer-main, .footer-header .footer-padding, .product__title, .product-section, .attributes-list li, .filters__item, .btn-group .btn, .btn-group .btn:last-child, .panel-layout__row hr {
    border-color: var(--bg6)
}
.boku, .topbar-widget-mymarketplace .popover .popover-content .popover-options .option .option-icon, .seller-info__cart-icon, .tags-list__img {
    filter: invert(1);
    -webkit-filter: invert(1);
}
.fullscreen-search-container {
    top: 15px;
    overflow: hidden;
    border-radius: 3px;
}
.fullscreen-search-form {
    padding: 0;
}
.fullscreen-search-form-fields {
    border: none;
    padding: 0;
    margin: 0;
}
.fullscreen-search-input {
    font-size: 14px;
    line-height: 0;
    box-sizing: border-box;
    padding-left: 14px;
    color: #000;
}
.fullscreen-search-submit {
    background: #f2470f;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 0;
    width: 46px;
}
.fullscreen-search-submit .icon {
    fill: #fff;
}
.fullscreen-search-preloader {
    position: relative;
    left: -10px;
}
.fullscreen-search[data-results="true"] .fullscreen-search-form .results {
    padding: 5px 14px 10px 14px;
    border-top: 2px solid #f2470f;
    margin: 0;
}
.fullscreen-search-results .Card__base {
    background: #222;
    border: 1px solid #444;
}
.fullscreen-search-results .Card__base .Card__title, .fullscreen-search-results .Card__base .Card__price {
    color: #999;
}
.listing-header__sort .label {
    color: #fff;
    width: 60px
}
.listing-header hr {
    border-color: var(--bg6);
}
body.fullscreen-search-on {
    overflow: auto;
}
.product-offers .offer {
    background: var(--bg1);
    border-bottom: 1px solid var(--bg6)
}
.expander__fader {
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(0, 0, 0, 0.1)), to(rgba(20, 20, 20, 1)));
    background-image: linear-gradient(180deg, rgba(0, 0, 0, 0.1) 0, rgba(20, 20, 20, 1));
}
.topbar-widget-platforms .popover-content a {
    border-color: var(--bg6);
    color: #fff;
}
.topbar-widget-platforms .popover-content a:hover {
    background: var(--bg5);
}
.cat-navbar .popover-subcategories::before {
    background-color: var(--bg6);
}
.loot-banner__open, .loot-banner__open:hover, .footer-payments .more, .footer-links-column label, .categories-filter__title, .listing-header__query-results, .pagination .pagination-page, .product-tags > li, .cashback-widget__percentage, .cashback-widget__description, .shield-info__title, .shield-info__item, .shield-wrapper span.price, .user-widget .user-rating .rating-data > span:not(.separator), .user-widget .user-rating .rating-data, .product__price .price-label, .product-offers__title, .business-banner__description, .product-offers-header .icon, .product-offers-header .title, .navigation__link, .navigation__link:hover, .product-section__content__content, .product-section__title, .attributes-list__title, .attributes-list li > .attributes-list__value, .attributes-list li > strong, .guides__wrapper .guide__content .title, .guides__wrapper .guide__text, .product-section__content p:last-child, .product-section__content__description, .product__rating-popover .ratings-group p span, .popover-options .option, .popover-title, .listing-header__products-found, .listing-header__query-results, .filters__title, .price-filter__title, .price-filter__currency, .radio-filter__title, .radio > span, .price-filter__dash, .categories-filter__item--active span, .cat-navbar .popover-column .option--sub, .panel-layout .panel-layout__title .title h1, .filter-container label, .Card__skin-exterior-type {
    color: #fff;
}
.section__header h2, .section__header h2 a, .section__header h2 a:hover, .sticky-header-homepage__header, .sticky-header-homepage__header a, .sticky-header-homepage__header a:hover, .Card__description, .Card__title, .Card__price, .Card--loot .Card__title a, .Card--loot .Card__description, .Card__loot-points, .listing-header__title-wrapper h1, .product__title {
    color: #fff;
    font-weight: 400;
}
.sticky-header-homepage__header a:first-child .icon, .section__header h2 .icon, .topbar-widget-footer .icon, .filters .btn-link .icon {
    fill: #fff;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #000;
    width: 14px;
    height: 14px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background: #214867;
}
::-webkit-scrollbar-thumb:not(:active):hover {
    background: #1b4542;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
    background: #37888b;
}

.modal {
    position: relative;
    top: 0;
}
.noscroll {
    overflow: hidden;
    position: unset;
    
}
html {
        overflow: auto;
}
}
